INSERT INTO mount VALUES
(611, 230, 59324, 0, 'Tundra Icehoof', 'A translucent layer of down hair covers this breed''s leathery hide, acting as the perfect insulation for cold environments.', '|cFFFFD200Drop:|r Vengeance, Deathtalon, Terrorfist, Doomroller|n|cFFFFD200Zone:|r Tanaan Jungle|n', 0, 171619, 0, 19865),
