INSERT INTO locales_creature_text (entry, textgroup, id, text_loc2, text_loc6, text_loc7) VALUES
(62511, 0, 0, "Ah, je vois que vous avez trouvé qulques-unes de mes expériences. Approchez, et vous serez témoin de toute la gloire de mon travail ici.", "Ah, veo que has encontrado algunas de mis experiencias. Enfoque, y se puede ver toda la gloria de mi trabajo aquí.", "Ah, veo que has encontrado algunas de mis experiencias. Enfoque, y se puede ver toda la gloria de mi trabajo aquí."),
(62511, 1, 0, "Vous devez être puissants pour être parvenus jusqu'ici. Oui, oui... vous serez de dignes cobayes.", "Debis de ser poderosa para haber llegado hasta aqui. Si... Sois dignos sujetos para mis experimentos.", "Debis de ser poderosa para haber llegado hasta aqui. Si... Sois dignos sujetos para mis experimentos."),
(62511, 2, 0, "Attention où vous mettez les pieds !", "¡Mira donde pisas!", "¡Mira donde pisas!"),
(62511, 3, 0, "Ayez la chance de recevoir ce présent.", "Tienes suerte de recibir este don.", "Tienes suerte de recibir este don."),
(62511, 4, 0, "De la mort naît la vie, et de la vie, la mort...", "De la muerte surge la vida, y de la vida, la muerte...", "De la muerte nace la vida, y de la vida, la muerte..."),
(62511, 5, 0, "Vous devez penser être puissants. Quelle ignorance. Contemplez ma plus belle création !", "Os creéis muy poderosos. Qué poco sabéis. ¡Contemplad mi mayor creación!", "Os creéis muy poderosos. Qué poco sabéis. ¡Contemplad mi mayor creación!"),
(62511, 6, 0, "Qu'avez-vous fait ? Non, ça ne peut pas être la fin de mon œuvre. Je ne le permettrai pas !", "¿Qué habéis hecho? No, mi trabajo no teminarà asi. ¡No lo permititré!", "¿Qué habéis hecho? No, mi trabajo no teminarà asi. ¡No lo permititré!"),
(62511, 7, 0, "Est-ce tout ?", "¿Eso es todo?", "¿Eso es todo?"),
(62511, 7, 1, "Excellent !", "¡Excelente!", "¡Excelente!"),
(62511, 8, 0, "Pardonnez-moi, impératrice...", "Perdóname, emperatriz...", "Perdóname, emperatriz...");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc6, text_loc7) VALUES
(62711, 0, 0, "P... père...", "Pa... dre...", "Pa... dre..");