REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(45085, "Je peux vous apprendre l'art de la forge, et vous fournir les outils nécessaires.$B$BQu'est-ce qui vous plairait ?", "Je peux vous apprendre l'art de la forge, et vous fournir les outils nécessaires.$B$BQu'est-ce qui vous plairait ?", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(8447, "Je ne pense pas que vous possédiez la finesse mentale requise pour comprendre la nature de la magie des portails.", "Je ne pense pas que vous possédiez la finesse mentale requise pour comprendre la nature de la magie des portails.", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(87642, "", "Bienvenue, commandant. Dites-moi si je peux faire quoi que ce soit pour rendre votre séjour plus agréable.", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(88041, "Vous semblez $gsurpris:surprise; de nous voir ici, $C. C'est compréhensible. Je sais parfaitement comment les arakkoa ont traité votre peuple.Les adhérents de Rukhmar sont des créatures sauvages, et leur soif de pouvoir les mènera à leur propre perte. Nous ne suivons pas la même voie.Nous sommes à la recherche dartéfacts des apogides que nous pourrions utiliser pour instaurer une nouvelle ère pacifique pour les arakkoa. En échange de ces reliques, nous vous proposons des articles artisanaux précieux, qui, nous lespérons, seront de valeur égale à vos yeux.Acceptez-vous de nous aider, $C ?", "", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(88052, "Chef ! Vous êtes là aussi, vous ? Eh bé… Je commençais à croire que javais tiré le mauvais numéro. Allons gagner cette guerre pour la Horde !", "", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(88064, "", "Si vous jouez avec moi, vous jouez avec le feu… littéralement.", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(88630, "Murg, cest moi ! Avant, jétais un péon comme les autres orcs que vous voyez, mais en arrivant ici, ils ont vu que j'étais le meilleur casseur de pierres à la ronde !Maintenant, on m'appelle « Fissepierre » et je chapeaute tous ces ptits gars. Cest le meilleur métier du monde, cest moi qui vous le dis !", "", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(88646, "", "Ces gars du Reliquaire ont lair on ne peut plus sérieux.Cest pas bon pour les affaires, ça.", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(88880, "Bienvenue, commandant. Jai là les meilleures armes et armures de toute Ashran.$b$bDites-moi si je peux vous être utile à quoi que ce soit.", "Bienvenue, commandant. Jai là les meilleures armes et armures de toute Ashran.$b$bDites-moi si je peux vous être utile à quoi que ce soit.", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(89959, "Ça en surprend certains, mais les cliquetis répétitifs des machines gobelines ont comme un effet relaxant, sur moi. Lodeur, en revanche, je men passerais volontiers.", "", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(90146, "Les herbes et champignons de Draenor permettent de donner le jour à toutes sortes de fléaux.Je pense que notre prochaine création sera lune des plus réussies du genre !", "", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(90153, "Grâce à mes recettes, vous pourrez transformer les composants de Draenor en concoctions suffisamment puissantes pour donner vie à un être ou le tuer.Enfin… en admettant que vous ayez le niveau pour pouvoir les utiliser.", "", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(90165, "Nombre de créatures de Draenor sont dotées dune peau aussi épaisse que celle des kodos les plus durs au mal. Pour peu que vous ayez le niveau, je peux vous montrer comment en faire de belles armures.", "", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(90172, "On dirait que votre garde-robe a besoin dun coup de neuf.Heureusement pour vous, je peux vous apprendre à réaliser les plus beaux ouvrages quun tailleur puisse concevoir sur Draenor.Voulez-vous jeter un œil à mes patrons ?", "", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(90175, "", "Les bons mots, ils ont autant de puissance que les meilleurs grigris vaudou.Ceux qui connaissent ces mots, le pouvoir, ils savent ce que cest !", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(90176, "Ces gars de la Horde de Fer croient sy connaître en mécanique. Attendez un peu quils voient ce dont les gobelins sont capables, quand on mettra la main sur leurs inventions !", "", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(90177, "Depuis que lAlliance a décidé de semer la zizanie à Ashran, la demande darmes et darmures de qualité est plus forte que jamais !Vous la sentez, la bonne odeur de largent ?", "", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(90794, "Zarg aimer chatons. Zarg entraîner petits chatons à devenir petits guerriers tout mignons.Quoi ? Pourquoi vous regarder moi comme ça ?Vous avoir quelque chose contre chatons ?", "", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(90810, "Après notre assaut coûteux sur la Porte des ténèbres, nos troupes restantes sont venues se regrouper ici, à Ashran. Malheureusement, les affrontements avec lAlliance nont pas tardé à suivre.Êtes-vous ici pour chercher refuge, ou bien attiser le feu des combats ?", "", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(90812, "", "Ces trolls sont pas si mauvais ! Daccord, ils sont maigres et parlent bizarrement, mais comme lanciers, il ny a pas plus marrant !Je suis impatiente de men servir pour embrocher quelques nains !", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(90961, "Salutations, $r.$B$BAu vu de votre allure, je vois que vous navez rien d$gun aventurier:une aventurière; ordinaire.$B$BDraenor est un monde hostile, et des ennemis dangereux se tapissent aussi bien dans les cryptes que dans les palais. Ils ne vivent que pour semer la destruction sur ces terres, grâce à la magie et à des armes dévastatrices. Si vous sécurisiez ces endroits ainsi que les trésors quils renferment, vous et vos compagnons pourriez obtenir de très belles récompenses.$B$BCependant, hâtez-vous : dautres aventuriers vous talonnent, et seule une course contre la montre prouvera que vous êtes $gle meilleur:la meilleure; !$B$BÊtes-vous $gprêt:prête; à relever ce nouveau défi que vous lance Draenor ?", "Salutations, $r.$B$BAu vu de votre allure, je vois que vous navez rien d$gun aventurier:une aventurière; ordinaire.$B$BDraenor est un monde hostile, et des ennemis dangereux se tapissent aussi bien dans les cryptes que dans les palais. Ils ne vivent que pour semer la destruction sur ces terres, grâce à la magie et à des armes dévastatrices. Si vous sécurisiez ces endroits ainsi que les trésors quils renferment, vous et vos compagnons pourriez obtenir de très belles récompenses.$B$BCependant, hâtez-vous : dautres aventuriers vous talonnent, et seule une course contre la montre prouvera que vous êtes $gle meilleur:la meilleure; !$B$BÊtes-vous $gprêt:prête; à relever ce nouveau défi que vous lance Draenor ?", 0, 100, 19085);
