REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(8447, "Je ne pense pas que vous possédiez la finesse mentale requise pour comprendre la nature de la magie des portails.", "Je ne pense pas que vous possédiez la finesse mentale requise pour comprendre la nature de la magie des portails.", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(85290, "Jai besoin de beaucoup dartéfacts pour équiper nos Chevaucheurs de loups et les envoyer au combat. $B$BCombien men apportez-vous ?", "", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(85455, "", "Lénergie dont nous avons besoin se trouve dans les fragments dartéfacts, $n. En quantité suffisante, ils permettront aux mages dinvoquer un portail pour atteindre lavant-poste de Fer-de-Lance plus rapidement.$B$BCombien men apportez-vous ?", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(85464, "Si nous parvenons à nous procurer assez des fragments dartéfacts quon peut trouver à Ashran, nous pourrons activer les portes de démoniste pour nous déplacer plus rapidement dun point à un autre.$B$BCombien men apportez-vous ?", "", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(87402, "Tous les grands seigneurs de guerre ne se ressemblent pas, $c. Certains sont arrivés là à force de temps et defforts, dautres par leurs aptitudes au combat. Devinez à quelle catégorie jappartiens.", "", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(87642, "", "Bienvenue, commandant. Dites-moi si je peux faire quoi que ce soit pour rendre votre séjour plus agréable.", 0, 100, 19116);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(88041, "Vous semblez $gsurpris:surprise; de nous voir ici, $C. Cest compréhensible. Je sais parfaitement comment les arakkoa ont traité votre peuple.Les adhérents de Rukhmar sont des créatures sauvages, et leur soif de pouvoir les mènera à leur propre perte. Nous ne suivons pas la même voie.Nous sommes à la recherche dartéfacts des apogides que nous pourrions utiliser pour instaurer une nouvelle ère pacifique pour les arakkoa. En échange de ces reliques, nous vous proposons des articles artisanaux précieux, qui, nous lespérons, seront de valeur égale à vos yeux.Acceptez-vous de nous aider, $C ?", "", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(88048, "Je vous salue, $C. Nous sommes lOrdre des Éveillés et nous représentons les intérêts du peuple arakkoa.Je suis certain que vous avez été témoin des horreurs que les adhérents de Rukhmar ont fait subir aux arakkoa et aux autres peuples. Nous souhaitons réparer les dégâts et mener notre société dans une nouvelle ère de paix. Mais pour cela, nous avons besoin du savoir des apogides.Si vous souhaitez nous aider, nous vous échangerons des objets de valeur pour toute relique apogide que vous nous apporterez. Quen dites-vous, $C ?", "", 0, 100, 19116);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(88052, "Chef ! Vous êtes là aussi, vous ? Eh bé… Je commençais à croire que javais tiré le mauvais numéro. Allons gagner cette guerre pour la Horde !", "", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(88064, "", "Si vous jouez avec moi, vous jouez avec le feu… littéralement.", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(88158, "Les draeneï des environs ont été des alliés indispensables dans notre combat à Ashran.Tant que nous maintenons un approvisionnement régulier en provenance du continent, notre victoire est assurée !", "", 0, 100, 19116);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(88159, "", "Votre cause à Ashran est juste, et les draeneï vous aideront dans la mesure de leurs moyens.", 0, 100, 19116);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(88160, "Les gens se demandent souvent comment un worgen peut devenir palefrenier, mais la vérité cest que cétait mon métier bien avant que je sois infecté. Laissez vos familiers au vieil Orville et je moccuperai bien deux.Et avant que vous ne demandiez, je vous promets que je ne les mangerai pas.", "", 0, 100, 19116);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(88547, "", "Comment ça se passe sur la ligne de front, $n ?", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(88548, "", "PARTEZ !", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(88550, "Je vais et viens comme le vent.", "", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(88630, "Murg, cest moi ! Avant, jétais un péon comme les autres orcs que vous voyez, mais en arrivant ici, ils ont vu que j'étais le meilleur casseur de pierres à la ronde !Maintenant, on m'appelle « Fissepierre » et je chapeaute tous ces ptits gars. Cest le meilleur métier du monde, cest moi qui vous le dis !", "", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(88646, "", "Ces gars du Reliquaire ont lair on ne peut plus sérieux.Cest pas bon pour les affaires, ça.", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(88880, "Bienvenue, commandant. Jai là les meilleures armes et armures de toute Ashran.$b$bDites-moi si je peux vous être utile à quoi que ce soit.", "Bienvenue, commandant. Jai là les meilleures armes et armures de toute Ashran.$b$bDites-moi si je peux vous être utile à quoi que ce soit.", 0, 100, 19116);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(88925, "Vous voulez impressionner vos amis ? Ou peut-être attirer lattention dune charmante personne aperçue à lauberge ? Eh bien, vous avez de la chance, $glami:lamie;. Contemplez la splendeur des incantations fantastiques de Bil Palpitonique !Pour un tout petit prix, vous pouvez vous aussi enchanter le monde et le rendre chatoyant !Bob, mon assistant, peut aussi vous procurer les recettes des enchantements de Draenor les plus récents et les plus puissants !", "", 0, 100, 19116);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(88930, "Bienvenue aux enchantements Palpitonique, les prix les plus bas de tout Bouclier-des-Tempêtes.Avez-vous feuilleté notre catalogue de recettes ?", "", 0, 100, 19116);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(89238, "Lorsque jaccompagnais les Rangari, jai appris toutes sortes de façons de dépecer les différentes créatures de Draenor.Cependant, maintenant que je suis à Bouclier-des-Tempêtes, je trouve bien plus agréable de transmettre mon savoir aux autres que de peiner dans les champs avec un couteau à dépecer.Vous nêtes pas de mon avis ?", "", 0, 100, 19116);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(89239, "", "Cette Swann nest peut-être pas naine de naissance, mais la chaleur de la forge coule dans ses veines. Je ne pourrais rêver dune meilleure apprentie.", 0, 100, 19116);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(89245, "Votre venue à Ashran est une bonne chose, $C. Nous aurons sûrement besoin de vous dans les jours à venir.Les artéfacts de lancien royaume ogre que lon trouve sur cette île pourraient savérer décisifs dans notre combat contre la Horde de Fer. Malheureusement, la Horde a décidé de semparer de ce savoir, et ce malgré notre trêve.Nous ne devons pas la laisser nous empêcher de combattre lennemi sur Draenor. Affrontez ses soldats et récupérez les artéfacts sur leurs cadavres sil le faut !Pour lAlliance !", "", 0, 100, 19116);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(89247, "Lapparition de lordre des Éveillés contrarie de nombreux parias. Ils ont beau prétendre que leurs intentions sont pures, les leurs nous ont fait du tort à bien des reprises par le passé.Nous espérons quils tiendront parole, mais seul lavenir nous le dira.", "", 0, 100, 19116);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(89248, "Je ne suis pas guerrier, mais je crois que même les plus belliqueux dentre nous devraient saccorder un moment de calme et de tranquillité de temps à autre.Pour moi, rien ne vaut une canne à pêche et le doux bruit de locéan.Quen pensez-vous, $C ?", "", 0, 100, 19116);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(89362, "Je vends une collection de plans qui donneront vie aux plus incroyables créations de votre imagination !Vous voulez voir ?", "", 0, 100, 19116);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(89363, "Êtes-vous de taille à plier les métaux de Draenor à votre volonté ?Si cest le cas, mes plans pourraient vous être utiles.", "", 0, 100, 19116);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(89364, "", "$gPrêt:Prête; à transformer ces vieux morceaux de cuir rapiécés en quelque chose de présentable ?", 0, 100, 19116);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(89370, "", "Les mascottes rares vous intéressent ? Mes bestioles sont les meilleures !", 0, 100, 19116);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(89855, "Les fragments dartéfacts contiennent suffisamment dénergie magique pour remettre sur pied cette fureur terrestre. $B$BCombien men apportez-vous ?", "", 0, 100, 19085);
