REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(90177, "Depuis que lAlliance a décidé de semer la zizanie à Ashran, la demande darmes et darmures de qualité est plus forte que jamais !Vous la sentez, la bonne odeur de largent ?", "", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(90186, "", "Les composants de Draenor sont aussi formidablement puissants que mystérieux.Pour peu que vous y mettiez le prix, je vous apprendrai à les exploiter.", 0, 100, 19116);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(90340, "", "Si lartéfact convoité par Harrison est aussi puissant quil le pense, jespère que personne ne mettra jamais la main dessus.Quelles que soient les intentions qui la motivent, lobtention dun tel pouvoir peut avoir des conséquences désastreuses.", 0, 100, 19116);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(90794, "Zarg aimer chatons. Zarg entraîner petits chatons à devenir petits guerriers tout mignons.Quoi ? Pourquoi vous regarder moi comme ça ?Vous avoir quelque chose contre chatons ?", "", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(90810, "Après notre assaut coûteux sur la Porte des ténèbres, nos troupes restantes sont venues se regrouper ici, à Ashran. Malheureusement, les affrontements avec lAlliance nont pas tardé à suivre.Êtes-vous ici pour chercher refuge, ou bien attiser le feu des combats ?", "", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(90812, "", "Ces trolls sont pas si mauvais ! Daccord, ils sont maigres et parlent bizarrement, mais comme lanciers, il ny a pas plus marrant !Je suis impatiente de men servir pour embrocher quelques nains !", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(90961, "Salutations, $r.$B$BAu vu de votre allure, je vois que vous navez rien d$gun aventurier:une aventurière; ordinaire.$B$BDraenor est un monde hostile, et des ennemis dangereux se tapissent aussi bien dans les cryptes que dans les palais. Ils ne vivent que pour semer la destruction sur ces terres, grâce à la magie et à des armes dévastatrices. Si vous sécurisiez ces endroits ainsi que les trésors quils renferment, vous et vos compagnons pourriez obtenir de très belles récompenses.$B$BCependant, hâtez-vous : dautres aventuriers vous talonnent, et seule une course contre la montre prouvera que vous êtes $gle meilleur:la meilleure; !$B$BÊtes-vous $gprêt:prête; à relever ce nouveau défi que vous lance Draenor ?", "Salutations, $r.$B$BAu vu de votre allure, je vois que vous navez rien d$gun aventurier:une aventurière; ordinaire.$B$BDraenor est un monde hostile, et des ennemis dangereux se tapissent aussi bien dans les cryptes que dans les palais. Ils ne vivent que pour semer la destruction sur ces terres, grâce à la magie et à des armes dévastatrices. Si vous sécurisiez ces endroits ainsi que les trésors quils renferment, vous et vos compagnons pourriez obtenir de très belles récompenses.$B$BCependant, hâtez-vous : dautres aventuriers vous talonnent, et seule une course contre la montre prouvera que vous êtes $gle meilleur:la meilleure; !$B$BÊtes-vous $gprêt:prête; à relever ce nouveau défi que vous lance Draenor ?", 0, 100, 19116);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(91286, "Moi éclair !", "", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(91287, "Toi pas éclair ! Toi tonnerre ! Force de nature et dénergie.", "", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(91288, "Oh ! Oui. Moi tonnerre, naturellement.", "", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(91289, "Oui, ça vient à toi naturellement.", "", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(91290, "Mais toi éclair, si nous ensemble… étincelles !", "", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(91432, "Les elfes de la nuit tentent de ressusciter un ancien à laide de la puissance des artéfacts dAshran pour utiliser cette créature contre nous.$B$BAlors, si vous rencontrez des membres de lAlliance, débrouillez-vous comme vous voulez, mais prenez-leur les fragments quils transportent.", "", 0, 100, 19085);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(91453, "Où voulez-vous aller ?", "Où voulez-vous aller ?", 0, 100, 19116);
REPLACE INTO npc_text (ID, text0_0, text0_1, lang0, prob0, WDBVerified) VALUES(91535, "Que cherchiez-vous ?", "Que cherchiez-vous ?", 0, 100, 19085);