-- Battle pet correspondance, replace all battle pet spawns by critter spawns
DROP TABLE IF EXISTS `temp_battlepet_spawn_relation` ;
CREATE TABLE `temp_battlepet_spawn_relation` (
  `Name` VARCHAR(255) NOT NULL,
  `CritterEntry` INT(10) NOT NULL,
  `BattlePetEntry` INT(10) NOT NULL,
  PRIMARY KEY (`CritterEntry`,`BattlePetEntry`)
) ENGINE=INNODB DEFAULT CHARSET=latin1;

	SELECT 
		DISTINCT(a.name) AS `Name`,
		(SELECT d.entry FROM creature_template d WHERE d.name = a.name ORDER BY d.type LIMIT 0, 1) AS CritterEntry,
		(SELECT d.entry FROM creature_template d WHERE d.name = a.name ORDER BY d.type LIMIT 1, 2) AS BattlePetEntry
	FROM creature_template a
	WHERE (SELECT COUNT(b.name) FROM creature_template b WHERE b.name=a.name) = 2
	AND (SELECT d.type FROM creature_template d WHERE d.entry = (SELECT d.entry FROM creature_template d WHERE d.name = a.name ORDER BY d.type LIMIT 0, 1)) = 8
	AND (SELECT d.type FROM creature_template d WHERE d.entry = (SELECT d.entry FROM creature_template d WHERE d.name = a.name ORDER BY d.type LIMIT 1, 1)) = 14;
	
UPDATE creature a SET a.id=(SELECT b.CritterEntry FROM temp_battlepet_spawn_relation b WHERE b.BattlePetEntry=a.id) WHERE a.id IN (SELECT c.BattlePetEntry FROM temp_battlepet_spawn_relation c);

-- Fill draenors pools
	SELECT 
		c.zoneId,
		(SELECT b.ID FROM dbc_battle_pet_species b WHERE b.NpcID=(SELECT d.BattlePetEntry FROM temp_battlepet_spawn_relation d WHERE d.CritterEntry = c.id)),
		c.id,
		CAST(LEAST(20, GREATEST(1, (SELECT COUNT(d.guid) FROM creature d WHERE d.id=c.id AND d.zoneId = c.zoneId AND d.map=c.map) / 4)) AS SIGNED), 
		60, 
		24, 
		25, 
		3,
		3,
		3,
		3,
		3,
		3,
		3,
		3,
		3,
		3
	FROM creature c 
	WHERE map = 1116 
	AND c.id IN (SELECT d.CritterEntry FROM temp_battlepet_spawn_relation d)
	GROUP BY c.zoneId, c.id;