INSERT INTO `dbc_battle_pet_species` VALUES (2, 61892, 132332, 0, 4, -1, 49, "", "");
INSERT INTO `dbc_battle_pet_species` VALUES (39, 2671, 656559, 4055, 9, 3, 2, "|cFFFFD200Métier : |rIngénierie", "Certains disent que les écureuils mécaniques aiment manger des pignons et grimper aux arbres à cames.");
INSERT INTO `dbc_battle_pet_species` VALUES (40, 7385, 656575, 10673, 7, 2, 2, "|cFFFFD200Vendeur : |rDonni Anthania|n|cFFFFD200Région : |rForêt d’Elwynn|n|cFFFFD200Coût : |r40|TINTERFACE\MONEYFRAME\UI-SILVERICON.BLP:0|t", "Donni Anthania voudrait qu\'un bombay soit enterré avec elle à sa mort. Mais l\'aventurier sage saura faire bien meilleur usage de ses talents.");
INSERT INTO `dbc_battle_pet_species` VALUES (41, 7384, 656577, 10674, 7, 2, 2, "|cFFFFD200Vendeur : |rDonni Anthania|n|cFFFFD200Région : |rForêt d’Elwynn|n|cFFFFD200Coût : |r40|TINTERFACE\MONEYFRAME\UI-SILVERICON.BLP:0|t", "Donni Anthania invite régulièrement ces chats à prendre le thé. Mais elle ne leur sert jamais leur boisson favorite : les larmes de leurs ennemis.");
INSERT INTO `dbc_battle_pet_species` VALUES (42, 7383, 656574, 10675, 7, 0, 2, "|cFFFFD200Butin : |rButin mondial|n|cFFFFD200Région : |rContreforts de Hautebrande", "Vieux proverbe d\'Alterac : « Un chat a neuf vies, mais n\'a besoin que d\'une. »");
INSERT INTO `dbc_battle_pet_species` VALUES (43, 7382, 656578, 10676, 7, 2, 2, "|cFFFFD200Vendeur : |rDonni Anthania, Steven Lisbane|n|cFFFFD200Région : |rForêt d’Elwynn|n|cFFFFD200Coût : |r40|TINTERFACE\MONEYFRAME\UI-SILVERICON.BLP:0|t", "La dernière personne qui a essayé d\'apprendre à ce chat à faire où on lui dit de faire a appris à ses dépens qu\'une petite tache sur un tapis vaut mieux qu\'un grand ravage partout ailleurs.");
INSERT INTO `dbc_battle_pet_species` VALUES (44, 7380, 656579, 10677, 7, 2, 2, "|cFFFFD200Vendeur : |rCamelot Rashaad|n|cFFFFD200Région : |rRaz-de-Néant|n|cFFFFD200Coût : |r60|TINTERFACE\MONEYFRAME\UI-SILVERICON.BLP:0|t", "Est connu pour ses yeux bleus. Est également un mets très recherché par les murlocs géants.");
INSERT INTO `dbc_battle_pet_species` VALUES (45, 7381, 656580, 10678, 7, 2, 2, "|cFFFFD200Vendeur : |rDonni Anthania|n|cFFFFD200Région : |rForêt d’Elwynn|n|cFFFFD200Coût : |r40|TINTERFACE\MONEYFRAME\UI-SILVERICON.BLP:0|t", "Dormir est le deuxième sport préféré de ce chat. Le premier est bâiller.");
INSERT INTO `dbc_battle_pet_species` VALUES (46, 7386, 656581, 10679, 7, 2, 2, "|cFFFFD200Vendeur : |rJeune Timmy|n|cFFFFD200Région : |rHurlevent|n|cFFFFD200Coût : |r60|TINTERFACE\MONEYFRAME\UI-SILVERICON.BLP:0|t", "La reine possédait trois chatons. Le premier lui rapporta un rat dodu. Le second, un savoureux lapin. Et le troisième, le blanc, son préféré, lui rapporta la tête de sa rivale.");
INSERT INTO `dbc_battle_pet_species` VALUES (47, 7390, 132921, 10680, 2, 2, 2, "|cFFFFD200Vendeur : |rHarry Sans-crochet, Narkk|n|cFFFFD200Région : |rCap Strangleronce|n|cFFFFD200Coût : |r40|TINTERFACE\MONEYFRAME\UI-SILVERICON.BLP:0|t", "D\'aucuns disent qu\'il est possible d\'apprendre à cet oiseau à parler. Mais il est assez intelligent pour savoir se taire.");
INSERT INTO `dbc_battle_pet_species` VALUES (49, 7391, 132921, 10682, 2, 0, 2, "|cFFFFD200Butin : |rButin mondial|n|cFFFFD200Région : |rStrangleronce septentrionale, Cap Strangleronce", "Les trolls de la jungle apprennent à ces oiseaux à imiter les appels à l\'aide pour attirer les voyageurs ignorants dans leurs pièges.");
INSERT INTO `dbc_battle_pet_species` VALUES (50, 7387, 132925, 10683, 2, 0, 2, "|cFFFFD200Butin : |rPirate défias|n|cFFFFD200Région : |rLes Mortemines", "Cet oiseau coloré est l\'animal de compagnie préféré des pirates défias, qui se servent de lui pour ne pas oublier leurs mots de passe, listes de courses et recettes de cuisine.");
INSERT INTO `dbc_battle_pet_species` VALUES (51, 7389, 132924, 10684, 2, 2, 2, "|cFFFFD200Vendeur : |rNarkk, Harry Sans-crochet|n|cFFFFD200Région : |rCap Strangleronce|n|cFFFFD200Coût : |r40|TINTERFACE\MONEYFRAME\UI-SILVERICON.BLP:0|t|n|n|cFFFFD200Vendeur : |rCamelot Rashaad|n|cFFFFD200Région : |rRaz-de-Néant|n|cFFFFD200Coût : |r40|TINTERFACE\MONEYFRAME\UI-SILVERICON.BLP:0|t", "Cet oiseau coloré est l\'animal de compagnie préféré des gobelins de Baie-du-Butin, qui apprécient son talent pour le comptage des pièces, l\'équilibrage des budgets et le mensonge dans les négociations.");
INSERT INTO `dbc_battle_pet_species` VALUES (52, 7394, 135996, 10685, 2, 2, 2, "|cFFFFD200Vendeur : |rJohnson « le Brave »|n|cFFFFD200Région : |rMille pointes|n|cFFFFD200Coût : |r1|TINTERFACE\MONEYFRAME\UI-GOLDICON.BLP:0|t", "Pour lui, c\'est vous qui avez un goût de poulet.");
INSERT INTO `dbc_battle_pet_species` VALUES (55, 7395, 294474, 10688, 4, 2, 2, "|cFFFFD200Vendeur : |rCamelot Rashaad|n|cFFFFD200Région : |rRaz-de-Néant|n|cFFFFD200Coût : |r50|TINTERFACE\MONEYFRAME\UI-SILVERICON.BLP:0|t|n|n|cFFFFD200Vendeur : |rJeremiah Payson|n|cFFFFD200Région : |rClairières de Tirisfal|n|cFFFFD200Coût : |r50|TINTERFACE\MONEYFRAME\UI-SILVERICON.BLP:0|t", "Posséder un cafard est la plupart du temps le fruit d\'un achat compulsif. Ensuite, ses propriétaires ont toujours un mal fou à se séparer de lui, même après plusieurs tentatives.");
INSERT INTO `dbc_battle_pet_species` VALUES (56, 7543, 134154, 10695, 1, 0, 2, "|cFFFFD200Butin : |rJeunes dragonnets|n|cFFFFD200Région : |rLes Paluns, Marécage d’Âprefange, Terres Ingrates, Steppes Ardentes", "Il espère grandir suffisamment pour détruire des villages, comme maman.");
INSERT INTO `dbc_battle_pet_species` VALUES (57, 7547, 134155, 10696, 1, 0, 2, "|cFFFFD200Butin : |rButin mondial|n|cFFFFD200Région : |rBerceau-de-l’Hiver", "Comme tous les membres du vol draconique bleu, ces petites créatures fantasques démontrent une certaine affinité avec la magie des Arcanes.");
INSERT INTO `dbc_battle_pet_species` VALUES (58, 7544, 134158, 10697, 1, 0, 2, "|cFFFFD200Butin : |rButin mondial|n|cFFFFD200Région : |rLes Paluns", "Né dans les Paluns, ce petit dragon commence tout juste à apprendre à chasser, cracher du feu, et semer adorablement la dévastation sur son passage.");
INSERT INTO `dbc_battle_pet_species` VALUES (59, 7545, 134157, 10698, 1, 0, 2, "|cFFFFD200Butin : |rDragonnet nocif|n|cFFFFD200Région : |rFéralas", "Jadis couvés et élevés par le dragon vert Itharius, les dragons de cette lignée unique ont élu domicile dans les jungles de Féralas.");
INSERT INTO `dbc_battle_pet_species` VALUES (64, 7550, 237579, 10703, 8, 2, 2, "|cFFFFD200Vendeur : |rFlik|n|cFFFFD200Région : |rÎle de Sombrelune|n|cFFFFD200Coût : |r1|TINTERFACE\MONEYFRAME\UI-GOLDICON.BLP:0|t", "Les étals des marchands de la foire de Sombrelune regorgent de merveilles insolites et exotiques. Et aussi de grenouilles.");
INSERT INTO `dbc_battle_pet_species` VALUES (65, 7549, 237579, 10704, 8, 2, 2, "|cFFFFD200Vendeur : |rFlik|n|cFFFFD200Région : |rÎle de Sombrelune|n|cFFFFD200Coût : |r1|TINTERFACE\MONEYFRAME\UI-GOLDICON.BLP:0|t", "Célèbres pour leurs cuisses puissantes et leur vue perçante.");
INSERT INTO `dbc_battle_pet_species` VALUES (67, 7555, 132150, 10706, 2, 2, 2, "|cFFFFD200Vendeur : |rShylenai|n|cFFFFD200Région : |rTeldrassil|n|cFFFFD200Coût : |r50|TINTERFACE\MONEYFRAME\UI-SILVERICON.BLP:0|t", "Ce rapace au plumage noir est souvent considéré comme un signe funeste par les druides de Teldrassil.");
INSERT INTO `dbc_battle_pet_species` VALUES (68, 7553, 132150, 10707, 2, 2, 2, "|cFFFFD200Vendeur : |rShylenai|n|cFFFFD200Région : |rTeldrassil|n|cFFFFD200Coût : |r50|TINTERFACE\MONEYFRAME\UI-SILVERICON.BLP:0|t", "Traditionnellement, les enfants elfes de la nuit reçoivent une plume de chouette pour leur premier anniversaire, comme porte-bonheur.");
INSERT INTO `dbc_battle_pet_species` VALUES (69, 7554, 132150, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes : |rBerceau-de-l’Hiver|n|cFFFFD200Saison : |rHiver", "Le plumage d\'un blanc brillant de ces majestueuses créatures est très recherché par les furbolgs de Berceau-de-l\'Hiver, qui l\'utilisent pour leurs rituels.");
INSERT INTO `dbc_battle_pet_species` VALUES (70, 14421, 656176, 10709, 4, 2, 2, "|cFFFFD200Vendeur : |rHalpa, Naleen|n|cFFFFD200Région : |rMulgore|n|cFFFFD200Coût : |r50|TINTERFACE\MONEYFRAME\UI-SILVERICON.BLP:0|t", "C\'est un système complexe de galeries qui protège les chiens de prairie des sabots de leurs voisins les plus imposants.");
INSERT INTO `dbc_battle_pet_species` VALUES (72, 7560, 132762, 10711, 4, 2, 2, "|cFFFFD200Vendeur : |rYarlyn Distillambre|n|cFFFFD200Région : |rDun Morogh|n|cFFFFD200Coût : |r20|TINTERFACE\MONEYFRAME\UI-SILVERICON.BLP:0|t", "Ce matin, un lapin patte-blanche a tué un chasseur. Puis l\'a écorché. Avec les dents.");
INSERT INTO `dbc_battle_pet_species` VALUES (74, 7561, 136040, 10713, 7, 2, 2, "|cFFFFD200Vendeur : |rBreanni|n|cFFFFD200Région : |rDalaran|n|cFFFFD200Coût : |r50|TINTERFACE\MONEYFRAME\UI-GOLDICON.BLP:0|t", "Ce reptile est un compagnon recherché par les mages du Kirin Tor. C\'est à se demander s\'il n\'est vraiment qu\'un simple serpent.");
INSERT INTO `dbc_battle_pet_species` VALUES (75, 7565, 136040, 10714, 7, 2, 2, "|cFFFFD200Vendeur : |rXan’tish|n|cFFFFD200Région : |rOrgrimmar|n|cFFFFD200Coût : |r50|TINTERFACE\MONEYFRAME\UI-SILVERICON.BLP:0|t", "Le courageux Xan\'tish piste, capture et dresse ces nobles serpents pour qu\'ils combattent aux côtés de leurs compagnons.");
INSERT INTO `dbc_battle_pet_species` VALUES (77, 7562, 136040, 10716, 7, 2, 2, "|cFFFFD200Vendeur : |rXan’tish|n|cFFFFD200Région : |rOrgrimmar|n|cFFFFD200Coût : |r50|TINTERFACE\MONEYFRAME\UI-SILVERICON.BLP:0|t", "Le serpent brun habite les territoires de la Horde. Il ne paraît pas très impressionnant, jusqu\'à ce qu\'un ennemi passe à portée.");
INSERT INTO `dbc_battle_pet_species` VALUES (78, 7567, 136040, 10717, 7, 2, 2, "|cFFFFD200Vendeur : |rCamelot Rashaad|n|cFFFFD200Région : |rRaz-de-Néant|n|cFFFFD200Coût : |r50|TINTERFACE\MONEYFRAME\UI-SILVERICON.BLP:0|t|n|n|cFFFFD200Vendeur : |rXan’tish|n|cFFFFD200Région : |rOrgrimmar|n|cFFFFD200Coût : |r50|TINTERFACE\MONEYFRAME\UI-SILVERICON.BLP:0|t", "Il n\'y a pas que sa cruauté naturelle qui fait du serpent cramoisi un des compagnons préférés des trolls sombrelances. Il y a aussi sa loyauté.");
INSERT INTO `dbc_battle_pet_species` VALUES (83, 8376, 656513, 12243, 9, 1, 18, "|cFFFFD200Quête :|r Un OOX rien que pour vous|n|cFFFFD200Région :|r Baie-du-Butin|n", "N\'ayez aucune inquiétude : les protocoles de combat et le système de visée de ce modèle ont été désactivés. Euh, je crois. - Orguignard Odipnoticus");
INSERT INTO `dbc_battle_pet_species` VALUES (84, 30379, 135996, 13548, 2, 1, 18, "|cFFFFD200Quête : |rCOT-COT-CODEC !|n|cFFFFD200Région : |rMarche de l’Ouest", "Ne traitez pas cet oiseau de poule mouillée. C\'est un coup à se retrouver avec de l\'œuf plein le visage.");
INSERT INTO `dbc_battle_pet_species` VALUES (85, 9656, 133712, 15048, 9, 3, 2, "|cFFFFD200Métier : |rIngénierie", "Potentiellement explosive, incontestablement adorable. Tenir éloignée des flammes.");
INSERT INTO `dbc_battle_pet_species` VALUES (86, 9657, 294476, 15049, 9, 3, 2, "|cFFFFD200Métier : |rIngénierie", "Ce minuscule robot de maintien de l\'ordre est souvent équipé du dernier cri en matière d\'annulateur d\'Arcane et de faucheur de foule. Ne convient pas aux écologistes.");
INSERT INTO `dbc_battle_pet_species` VALUES (87, 9662, 657499, 15067, 1, 0, 2, "|cFFFFD200Butin : |rButin mondial|n|cFFFFD200Région : |rFéralas", "Ces adorables créatures aiment faire un gros câlin à leur maître après une longue journée de combats brutaux et sanglants.");
INSERT INTO `dbc_battle_pet_species` VALUES (89, 10259, 132599, 15999, 7, 0, 2050, "|cFFFFD200Butin : |rIntendant Zigris|n|cFFFFD200Région : |rBas du pic Rochenoire", "Les worgs sont les compagnons préférés des orcs et font preuve d\'une fidélité farouche sur le champ de bataille.");
INSERT INTO `dbc_battle_pet_species` VALUES (90, 10598, 132598, 16450, 7, 0, 2, "|cFFFFD200Butin : |rMatriarche Couveuse|n|cFFFFD200Région : |rBas du pic Rochenoire", "Les jeunes couveuses naissent sur les torrides hauteurs du pic Rochenoire, mais peuvent s\'adapter à la plupart des environnements.");
INSERT INTO `dbc_battle_pet_species` VALUES (92, 11325, 132494, 17707, 7, 7, 82, "|cFFFFD200Promotion :|r Édition collector de World of Warcraft", "Ce minuscule bébé panda est très recherché par les aventuriers d\'Azeroth, malgré une certaine tendance à la narcolepsie.");
INSERT INTO `dbc_battle_pet_species` VALUES (93, 11326, 132787, 17708, 5, 7, 82, "|cFFFFD200Promotion :|r Édition collector de World of Warcraft", "ET LES CIEUX TREMBLERONT, ET LES HOMMES PLEURERONT, ET LA FIN DES TEMPS SERA… Euh, mais vous êtes vraiment grand, vous !");
INSERT INTO `dbc_battle_pet_species` VALUES (94, 11327, 136217, 17709, 5, 7, 82, "|cFFFFD200Promotion :|r Édition collector de World of Warcraft", "Membre de l\'Essaim qui semble insignifiant au premier abord, le zergling se révèle féroce et n\'est pas à prendre à la légère. Très dangereux en grand nombre.");
INSERT INTO `dbc_battle_pet_species` VALUES (95, 12419, 237579, 19772, 9, 3, 2, "|cFFFFD200Métier : |rIngénierie", "Nos talentueux ingénieurs ont mis au point une réplique fidèle de la grenouille forestière, pour seulement dix fois son prix d\'achat.");
INSERT INTO `dbc_battle_pet_species` VALUES (106, 14878, 237579, 23811, 8, 6, 18, "|cFFFFD200Évènement mondial :|r Foire de Sombrelune", "La légendaire grenouille à bière était classée parmi les espèces disparues, jusqu\'à ce que les curieux breuvages de la foire de Sombrelune la poussent à sortir de sa cachette.");
INSERT INTO `dbc_battle_pet_species` VALUES (107, 15186, 656556, 24696, 0, 7, 82, "|cFFFFD200Promotion :|r BlizzCon 2005", "Chaque apparition de ce murloc adulé mais timide est un grand succès populaire.");
INSERT INTO `dbc_battle_pet_species` VALUES (111, 15358, 656558, 24988, 0, 7, 82, "|cFFFFD200Promotion :|r Édition collector de The Burning Crusade (Europe uniquement)|n", "Le caractère charmeur et le numéro enlevé d\'Albie sont un remède infaillible à l\'amertume même la plus tenace.");
INSERT INTO `dbc_battle_pet_species` VALUES (114, 15429, 132107, 25162, 5, 0, 2, "|cFFFFD200Butin :|r Butin mondial|n|cFFFFD200Créatures : |r Limons, gelées et vers", "Avertissement : Ne jamais manipuler le suinteux dégoûtant sans être équipé de gants et lunettes de protection. NE PAS LAVER. Très salissant.");
INSERT INTO `dbc_battle_pet_species` VALUES (115, 86445, 294472, 172695, 8, 3, 2, "|cFFFFD200Métier :|r Pêche|n|cFFFFD200Vendeur :|r Nat Pagle|n|cFFFFD200Région :|r fief, cabane de pêche|n|cFFFFD200Coût :|r 50|Tinterface\icons\inv_misc_coin_19.blp:0|t", "Après la naissance, les requins nouveaux-nés fuient rapidement leurs mères affamées. Leur indépendance précoce les rend très dangereux, même lorsqu’ils sont jeunes.");
INSERT INTO `dbc_battle_pet_species` VALUES (116, 15699, 132189, 26010, 9, 3, 2, "|cFFFFD200Métier : |rIngénierie", "Cet astucieux jouet est équipé d’un commutateur secret permettant de désactiver le mode « Tranquillité ». Heureusement, même les ingénieurs qui l’ont construit ne savent pas comment le trouver.");
INSERT INTO `dbc_battle_pet_species` VALUES (117, 15710, 655957, 26045, 6, 6, 2, "|cFFFFD200Évènement mondial :|r Voile d’hiver|n", "Il adore danser, gigoter et jouer avec vous ! Il a tendance à fondre après la fête du Voile d\'hiver, certes, mais ne vous inquiétez pas : il reviendra !");
INSERT INTO `dbc_battle_pet_species` VALUES (118, 15706, 133706, 26529, 4, 6, 2, "|cFFFFD200Évènement mondial :|r Voile d’hiver", "N\'a pas trop apprécié tout ce temps passé enfermé dans un paquet cadeau.");
INSERT INTO `dbc_battle_pet_species` VALUES (119, 15698, 133204, 26533, 0, 6, 2, "|cFFFFD200Évènement mondial :|r Voile d’hiver", "Ces compagnons saisonniers travaillent très dur pour préparer la fête du Voile d\'hiver. Aussi, ils apprécieraient qu\'on les laisse tranquilles le reste de l\'année.");
INSERT INTO `dbc_battle_pet_species` VALUES (120, 15705, 133202, 26541, 0, 6, 2, "|cFFFFD200Évènement mondial :|r Voile d’hiver", "Pendant la glorieuse fête du Voile d\'hiver, ces fidèles compagnons sèment la joie partout dans le monde. Mais le reste de l\'année, ils sont occupés.");
INSERT INTO `dbc_battle_pet_species` VALUES (121, 16069, 656557, 27241, 0, 7, 82, "|cFFFFD200Promotion :|r Promotion des sites de fans européens|n", "Gloubie est une artiste née, mais elle reste discrète. Ses apparitions en public sont rares.");
INSERT INTO `dbc_battle_pet_species` VALUES (122, 16085, 132382, 27570, 0, 6, 2, "|cFFFFD200Évènement mondial :|r De l’amour dans l’air|n|cFFFFD200Vendeur : |rMarchande ravissante|n|cFFFFD200Coût : |r40|TINTERFACE\ICONS\INV_ValentinesCard01:0|t|n", "Le pouvoir de l\'amour peut être ravageur. Surtout dans les mains d\'un gobelin volant et acariâtre.");
INSERT INTO `dbc_battle_pet_species` VALUES (124, 16456, 132498, 28505, 7, 7, 82, "|cFFFFD200Promotion :|r iCoke", "Cette petite créature tient de nombreux traits de ses parents. Mais pour ce qui est du chant, elle est unique.");
INSERT INTO `dbc_battle_pet_species` VALUES (125, 16547, 656341, 28738, 8, 6, 2, "|cFFFFD200Évènement mondial :|r Semaine des enfants", "Rien ne sert de courir : il faut partir à point.");
INSERT INTO `dbc_battle_pet_species` VALUES (126, 16548, 132514, 28739, 4, 6, 2, "|cFFFFD200Évènement mondial :|r Semaine des enfants", "Nombreux sont ceux qui pensent que le nom de « Trémousse » fait référence au postérieur de ce cochon. C\'est en voyant le mouvement de leurs entrailles pendantes qu\'ils comprennent leur erreur.");
INSERT INTO `dbc_battle_pet_species` VALUES (127, 16549, 294480, 28740, 4, 6, 2, "|cFFFFD200Évènement mondial :|r Semaine des enfants", "Moustaches est un fin tacticien, capable de feindre la mort pour échapper aux prédateurs et aux huissiers de justice.");
INSERT INTO `dbc_battle_pet_species` VALUES (128, 16701, 134813, 28871, 6, 6, 2, "|cFFFFD200Évènement mondial :|r Fête du Feu du solstice d’été|n|cFFFFD200Vendeur : |rMarchande du solstice|n|cFFFFD200Coût : |r350|TINTERFACE\ICONS\INV_SummerFest_FireFlower:0|t", "C\'est un fragment d\'été, qui a l\'odeur des soirées chaleureuses et des orages estivaux. En regardant de près, on y distingue un visage.");
INSERT INTO `dbc_battle_pet_species` VALUES (130, 17255, 132833, 30156, 2, 8, 2, "|cFFFFD200Jeu de cartes à collectionner :|r Héros d’Azeroth", "Lors d\'une invasion de harpies en Féralas, plusieurs aires d\'hippogriffes furent attaquées. Des centaines de bébés se retrouvèrent orphelins et furent recueillis par les druides de l\'île de Sardor.");
INSERT INTO `dbc_battle_pet_species` VALUES (131, 18381, 134539, 32298, 1, 7, 82, "|cFFFFD200Promotion :|r Édition collector de The Burning Crusade", "Petit des drakes du Néant d\'Outreterre, ce jeune dragon débute dans son rôle de chasseur, gardien et adorable terreur venue des cieux.");
INSERT INTO `dbc_battle_pet_species` VALUES (132, 18839, 133900, 33050, 8, 3, 2, "|cFFFFD200Pêche :|r Pêche (430)|n|cFFFFD200Région :|r Forêt de Terokkar (bancs de pêche)", "Les écrevisses magiques sont le fruit d\'un peu de pêche et de beaucoup de magie. Si votre ligne est merveilleuse, votre pêche pourrait être miraculeuse.");
INSERT INTO `dbc_battle_pet_species` VALUES (136, 20408, 136006, 35156, 5, 2, 2, "|cFFFFD200Vendeur : |rCamelot Rashaad|n|cFFFFD200Région : |rRaz-de-Néant|n|cFFFFD200Coût : |r40|TINTERFACE\MONEYFRAME\UI-GOLDICON.BLP:0|t", "Rashaad l\'éthéré a commencé l\'élevage de ces énigmatiques créatures après avoir eu vent de leur capacité à se nourrir d\'énergie arcanique.");
INSERT INTO `dbc_battle_pet_species` VALUES (137, 20472, 132762, 35239, 4, 2, 2, "|cFFFFD200Vendeur : |rCamelot Rashaad|n|cFFFFD200Région : |rRaz-de-Néant|n|cFFFFD200Coût : |r10|TINTERFACE\MONEYFRAME\UI-GOLDICON.BLP:0|t", "Ce lapin est la chose la plus adorable que ses adversaires ont jamais vu. Et la dernière, aussi.");
INSERT INTO `dbc_battle_pet_species` VALUES (138, 21010, 236193, 35907, 2, 2, 2, "|cFFFFD200Vendeur : |rSixx|n|cFFFFD200Région : |rL’Exodar|n|cFFFFD200Coût : |r50|TINTERFACE\MONEYFRAME\UI-SILVERICON.BLP:0|t", "Cet insecte délicat habite l\'île de Brume-Azur, et on raconte qu\'il possède des propriétés guérisseuses.");
INSERT INTO `dbc_battle_pet_species` VALUES (139, 21009, 236193, 35909, 2, 2, 2, "|cFFFFD200Vendeur : |rCamelot Rashaad|n|cFFFFD200Région : |rRaz-de-Néant|n|cFFFFD200Coût : |r10|TINTERFACE\MONEYFRAME\UI-GOLDICON.BLP:0|t", "Produit de l\'exposition d\'insectes à des énergies magiques sauvages, cette créature aux couleurs surprenantes est apparue après l\'introduction de phalènes dans les écodômes de Raz-de-Néant.");
INSERT INTO `dbc_battle_pet_species` VALUES (140, 21008, 236193, 35910, 2, 2, 2, "|cFFFFD200Vendeur : |rSixx|n|cFFFFD200Région : |rL’Exodar|n|cFFFFD200Coût : |r50|TINTERFACE\MONEYFRAME\UI-SILVERICON.BLP:0|t", "Aime par-dessus tout voler au cœur des orages, qui sont fréquents sur l\'île de Brume-Azur.");
INSERT INTO `dbc_battle_pet_species` VALUES (141, 21018, 236193, 35911, 2, 2, 2, "|cFFFFD200Vendeur : |rSixx|n|cFFFFD200Région : |rL’Exodar|n|cFFFFD200Coût : |r50|TINTERFACE\MONEYFRAME\UI-SILVERICON.BLP:0|t", "Aime par-dessus tout voler au cœur des nuits embrumées, qui sont fréquentes sur l\'île de Brume-Azur.");
INSERT INTO `dbc_battle_pet_species` VALUES (142, 21055, 132188, 36027, 1, 2, 2, "|cFFFFD200Vendeur : |rJilanne|n|cFFFFD200Région : |rBois des Chants éternels|n|cFFFFD200Coût : |r50|TINTERFACE\MONEYFRAME\UI-SILVERICON.BLP:0|t", "Naguère coqueluche des éleveurs de faucons-dragons de Lune-d’Argent. Facilement repérable pour ses prédateurs la nuit, ce qui stimule ses réflexes dès son plus jeune âge.");
INSERT INTO `dbc_battle_pet_species` VALUES (143, 21064, 132188, 36028, 1, 2, 2, "|cFFFFD200Vendeur : |rJilanne|n|cFFFFD200Région : |rBois des Chants éternels|n|cFFFFD200Coût : |r50|TINTERFACE\MONEYFRAME\UI-SILVERICON.BLP:0|t", "Ennemi juré des faucons-dragons argentés. S\'entend relativement bien avec les faucons-dragons dorés.");
INSERT INTO `dbc_battle_pet_species` VALUES (144, 21063, 132188, 36029, 1, 2, 2, "|cFFFFD200Vendeur : |rJilanne|n|cFFFFD200Région : |rBois des Chants éternels|n|cFFFFD200Coût : |r50|TINTERFACE\MONEYFRAME\UI-SILVERICON.BLP:0|t", "Un faucon-dragon d\'une espèce féroce, qu\'on aperçoit souvent piquer du ciel sur sa proie.");
INSERT INTO `dbc_battle_pet_species` VALUES (145, 21056, 132188, 36031, 1, 2, 2, "|cFFFFD200Vendeur : |rCamelot Rashaad|n|cFFFFD200Région : |rRaz-de-Néant|n|cFFFFD200Coût : |r10|TINTERFACE\MONEYFRAME\UI-GOLDICON.BLP:0|t", "Élevé par les rares marchands encore actifs à Raz-de-Néant.");
INSERT INTO `dbc_battle_pet_species` VALUES (146, 21076, 236196, 36034, 2, 0, 2, "|cFFFFD200Butin : |rPiqueur Tourbembrase|n|cFFFFD200Région : |rMarécage de Zangar", "Souvent utilisé comme source de lumière par les sporelins pour naviguer le long des rivières du marécage de Zangar, ce petit insecte lumineux les aide à éviter les dangereux piqueurs Tourbembrase.");
INSERT INTO `dbc_battle_pet_species` VALUES (149, 22445, 132598, 39181, 2, 1, 18, "|cFFFFD200Quête :|r Aile-du-ciel|n|cFFFFD200Région :|r Forêt de Terokkar|n", "L\'énergie magique émise lors de la transformation d\'Aile-du-ciel en sa véritable forme arakkoa a libéré cette pauvre créature, un petit oiseau emprisonné par Luanga pour alimenter son ignoble malédiction.");
INSERT INTO `dbc_battle_pet_species` VALUES (153, 22943, 132800, 39709, 4, 6, 2, "|cFFFFD200Évènement mondial :|r Fête des Brasseurs", "Les participants de la fête des Brasseurs qui racontent l\'histoire de cette mystérieuse créature sont légion. Ceux qui s\'en rappellent encore le lendemain matin sont plus rares.");
INSERT INTO `dbc_battle_pet_species` VALUES (155, 23198, 133858, 40405, 4, 7, 82, "|cFFFFD200Promotion :|r World Wide Invitational 2007|n", "Verni grogne quand il est de mauvaise humeur, ou quand il mange. Dans les deux cas, mieux vaut le laisser tranquille.");
INSERT INTO `dbc_battle_pet_species` VALUES (156, 23234, 133980, 40549, 7, 8, 2, "|cFFFFD200Jeu de cartes à collectionner :|r Porte des ténèbres", "Landro aurait préféré ne pas se séparer de Bananas. Mais il s\'est dit qu\'il valait mieux que le petit soit loin, très loin de Baie-du-Butin au moment où son père, le roi Mukla, se rendrait compte qu\'il avait disparu.");
INSERT INTO `dbc_battle_pet_species` VALUES (157, 23231, 294482, 40613, 5, 6, 2, "|cFFFFD200Évènement mondial :|r Semaine des enfants", "Un rien maléfique mais considérablement narcoleptique. À tenir éloigné des bestioles !");
INSERT INTO `dbc_battle_pet_species` VALUES (158, 23258, 294475, 40614, 4, 6, 2, "|cFFFFD200Évènement mondial :|r Semaine des enfants", "Les faucons-pérégrins servent généralement de montures aux Sin\'dorei. Mais celui-ci fut déclaré inapte au service après le diagnostic de plusieurs névroses anxieuses.");
INSERT INTO `dbc_battle_pet_species` VALUES (159, 23266, 656598, 40634, 4, 6, 2, "|cFFFFD200Évènement mondial :|r Semaine des enfants", "Les elekk domestiques sont dressés à la monte par les draeneï. Les sauvages sont chassés pour leurs défenses d\'ivoire.");
INSERT INTO `dbc_battle_pet_species` VALUES (160, 23274, 132599, 40990, 4, 5, 82, "|cFFFFD200Haut fait : |rLes aimer comme ils vous aiment|n|cFFFFD200Catégorie : |rCombats de mascottes", "Schlingo se sert de son fumet pour repousser les prédateurs et attirer les chattes. La technique marche beaucoup mieux dans un cas que dans l\'autre.");
INSERT INTO `dbc_battle_pet_species` VALUES (162, 23909, 134015, 42609, 6, 6, 2, "|cFFFFD200Évènement mondial :|r Sanssaint", "Cette cucurbitacée coquine aime surgir du sol et vous coller une trouille d\'enfer. Voire six trouilles d\'enfer.");
INSERT INTO `dbc_battle_pet_species` VALUES (163, 24388, 656555, 43697, 8, 3, 2, "|cFFFFD200Métier : |rPêche|n|cFFFFD200Région : |rShattrath", "La peau et la viande du crocilisque sont très recherchées par les trappeurs. Mais l\'animal est féroce et dur à surprendre.");
INSERT INTO `dbc_battle_pet_species` VALUES (164, 24389, 656553, 43698, 8, 3, 2, "|cFFFFD200Métier :|r Pêche|n|cFFFFD200Région :|r Shattrath", "Bourbe-haleine est un crocilisque comme les autres, mais doté d\'un excellent sens de l\'humour.");
INSERT INTO `dbc_battle_pet_species` VALUES (165, 24480, 237579, 43918, 8, 0, 2, "|cFFFFD200Butin :|r Grenouille forestière|n|cFFFFD200Région :|r Zul’Aman", "Les féticheurs amani transforment beaucoup de gens en grenouilles. Certains ne s\'en remettent jamais.");
INSERT INTO `dbc_battle_pet_species` VALUES (166, 24753, 656600, 44369, 4, 6, 2, "|cFFFFD200Évènement mondial :|r Fête des Brasseurs|n|cFFFFD200Vendeur : |rBelbi Coupecircuit|n|cFFFFD200Région : |rDun Morogh|n|cFFFFD200Coût : |r100|TINTERFACE\ICONS\INV_Misc_Coin_01:0|t|n|n|cFFFFD200Vendeur : |rBliz Réparouage|n|cFFFFD200Région : |rDurotar|n|cFFFFD200Coût : |r100|TINTERFACE\ICONS\INV_Misc_Coin_01:0|t", "L\'elekk rose est une créature timide. Il ne se montre qu\'à ceux qui ont su gagner sa confiance, ou à ceux qui sont trop ivres pour lui faire du mal.");
INSERT INTO `dbc_battle_pet_species` VALUES (167, 25062, 132197, 45082, 2, 2, 2, "|cFFFFD200Vendeur : |rMycah|n|cFFFFD200Région : |rMarécage de Zangar|n|cFFFFD200Faction : |rSporeggar - Exalté|n|cFFFFD200Coût : |r30|TINTERFACE\ICONS\INV_Mushroom_02:0|t", "Les sporoptères sauvages sont des créatures féroces. Mais, domestiqués jeunes, ils peuvent devenir des compagnons fidèles.");
INSERT INTO `dbc_battle_pet_species` VALUES (168, 25109, 133883, 45125, 9, 8, 2, "|cFFFFD200Jeu de cartes à collectionner :|r Marche de la légion", "Lors de l\'invasion de l\'Outreterre par la Légion ardente, Landro Tirauloin a proposé les plans de cette créature pour participer à l\'effort de guerre. La Horde et l\'Alliance ont toutes deux décliné l\'offre.");
INSERT INTO `dbc_battle_pet_species` VALUES (169, 25110, 133838, 45127, 2, 8, 2050, "|cFFFFD200Jeu de cartes à collectionner :|r Marche de la légion", "Beaucoup moins bruyant qu\'un vrai dragon.");
INSERT INTO `dbc_battle_pet_species` VALUES (170, 25146, 133784, 45174, 4, 7, 82, "|cFFFFD200Promotion :|r Parrainez un ami|n", "Ce cochon doré fait honneur à ses ancêtres en apportant bonne fortune à ses amis.");
INSERT INTO `dbc_battle_pet_species` VALUES (171, 25147, 133786, 45175, 4, 7, 82, "|cFFFFD200Promotion :|r Parrainez un ami", "D\'après les druides, il existe un lien particulier entre les cochons argentés et les dorés, car les deux espèces sont apparues en Azeroth à la même époque.");
INSERT INTO `dbc_battle_pet_species` VALUES (172, 25706, 294481, 45890, 6, 2, 2, "|cFFFFD200Vendeur :|r Zen’Vorka|n|cFFFFD200Région :|r Front du Magma|n|cFFFFD200Coût : |r30|TINTERFACE\ICONS\INV_MISC_MARKOFTHEWORLDTREE.BLP:0|t", "Les anciens chamans ont lancé un avertissement : des feux follets de Ragnaros, fidèles serviteurs du seigneur du Feu, ont envahi le front du Magma. Nous vous conseillons la plus grande vigilance.");
INSERT INTO `dbc_battle_pet_species` VALUES (173, 26050, 656554, 46425, 8, 3, 2, "|cFFFFD200Métier : |rPêche|n|cFFFFD200Région : |rShattrath", "Les crocilisques sont des prédateurs amphibiens hexapodes des eaux côtières. Certains archéologues ont pour théorie qu\'ils descendent des dimeurtrodons du cratère d\'Un\'Goro.");
INSERT INTO `dbc_battle_pet_species` VALUES (174, 26056, 656552, 46426, 8, 3, 2, "|cFFFFD200Métier : |rPêche|n|cFFFFD200Région : |rShattrath", "Après des années passées à étudier les crocilisques, le célèbre pêcheur Nat Pagle a établi que leurs bébés étaient plutôt inoffensifs.");
INSERT INTO `dbc_battle_pet_species` VALUES (175, 26119, 134373, 46599, 6, 0, 2, "|cFFFFD200Butin : |rKael’thas Haut-Soleil|n|cFFFFD200Région : |rTerrasse des Magistères", "Enfant du dieu phénix Al\'ar, ce petit vole dès sa sortie de l\'œuf.");
INSERT INTO `dbc_battle_pet_species` VALUES (179, 27217, 133278, 48406, 1, 7, 18, "|cFFFFD200Promotion :|r Évènement en champ de bataille", "Ces créatures stupéfiantes sont offertes en récompense aux héros victorieux au combat. Elles symbolisent le courage et la force.");
INSERT INTO `dbc_battle_pet_species` VALUES (180, 27346, 133443, 48408, 1, 7, 18, "|cFFFFD200Promotion :|r Évènement JcJ en Chine", "Seuls les héros ayant prouvé leur valeur par de grands exploits reçoivent l\'un de ces reptiles mystiques.");
INSERT INTO `dbc_battle_pet_species` VALUES (183, 27914, 136039, 49964, 5, 8, 2, "|cFFFFD200Jeu de cartes à collectionner :|r La traque d’Illidan", "Les marchands-d\'âmes éthériens emmènent en voyage ceux qui peuvent libérer les essences dont ils ont besoin de leurs hôtes turbulents.");
INSERT INTO `dbc_battle_pet_species` VALUES (186, 28470, 132191, 51716, 2, 2, 2, "|cFFFFD200Vendeur : |rGrella|n|cFFFFD200Région : |rForêt de Terokkar|n|cFFFFD200Faction : |rGarde-ciel sha’tari - Exalté|n|cFFFFD200Coût : |r40|TINTERFACE\MONEYFRAME\UI-GOLDICON.BLP:0|t", "Venus de la forêt de Terokkar, les petits de raie du Néant naissent pendant la saison des pluies. Des centaines d\'individus qui constituent une couvée, seuls quelques-uns survivent jusqu\'à l\'âge adulte.");
INSERT INTO `dbc_battle_pet_species` VALUES (187, 28513, 132182, 51851, 3, 0, 18, "|cFFFFD200Butin : |rPrince Tenris Bourbassang|n|cFFFFD200Région : |rKarazhan", "Les chauves-souris vampires sont dressées pour servir d\'éclaireurs et de montures par les Réprouvés de Fossoyeuse et les trolls amani de Zul\'Aman.");
INSERT INTO `dbc_battle_pet_species` VALUES (188, 28883, 237441, 52615, 3, 7, 82, "|cFFFFD200Promotion :|r Édition collector de Wrath of the Lich King", "Créé accidentellement lorsque les pouvoirs macabres du roi-liche relevèrent Frigecroc dans la non-mort.");
INSERT INTO `dbc_battle_pet_species` VALUES (189, 29089, 135277, 53082, 0, 7, 82, "|cFFFFD200Promotion :|r World Wide Invitational 2008", "Je suis la Justice incarnée !");
INSERT INTO `dbc_battle_pet_species` VALUES (190, 29147, 133730, 53316, 3, 2, 2, "|cFFFFD200Vendeur : |rDarahir|n|cFFFFD200Région : |rForêt du Chant de cristal|n|cFFFFD200Coût : |r40|TINTERFACE\MONEYFRAME\UI-GOLDICON.BLP:0|t", "Lorsqu\'il sent la mort imminente d\'une créature, le crâne lance un ricanement strident.");
INSERT INTO `dbc_battle_pet_species` VALUES (191, 24968, 133000, 54187, 9, 6, 2, "|cFFFFD200Évènement mondial :|r Voile d’hiver", "Ce guerrier mécanique est un cadeau en vogue lors de la fête du Voile d’hiver. Piles non incluses. Munitions mortelles incluses.");
INSERT INTO `dbc_battle_pet_species` VALUES (192, 29726, 327502, 55068, 8, 7, 82, "|cFFFFD200Promotion :|r Fusion des comptes Battle.net et WoW", "M. Glagla nous ramène à une époque plus éclairée, lorsque les gentilshommes se livraient leurs duels en queue-de-pie.");
INSERT INTO `dbc_battle_pet_species` VALUES (193, 31575, 134324, 59250, 4, 3, 2, "|cFFFFD200Métier :|r Pêche|n|cFFFFD200Région :|r Dalaran", "Rencontrer ce rat dans une allée sombre ne serait pas une bonne idée. Ni ailleurs, en fait.");
INSERT INTO `dbc_battle_pet_species` VALUES (194, 32589, 132192, 61348, 2, 0, 2, "|cFFFFD200Butin : |rŒuf mystérieux", "Contrairement à la plupart des « œufs mystérieux » de Geen le gorloc, celui-ci contenait une vraie créature vivante.");
INSERT INTO `dbc_battle_pet_species` VALUES (195, 32590, 132192, 61349, 2, 0, 2, "|cFFFFD200Butin : |rŒuf mystérieux", "Le très rare pique-bœuf albinos fait l\'objet d\'un culte chez les Oracles. Il est réputé apporter de grands orages, de bonnes récoltes d\'asticots, et de longues hibernations de varleus.");
INSERT INTO `dbc_battle_pet_species` VALUES (196, 32592, 236233, 61350, 1, 0, 2, "|cFFFFD200Butin : |rŒuf mystérieux", "Ces dragonnets voraces grandissent à une vitesse incroyable et peuvent ingurgiter pas loin de deux fois leur poids en viande crue chaque jour.");
INSERT INTO `dbc_battle_pet_species` VALUES (197, 32591, 236177, 61351, 7, 0, 2, "|cFFFFD200Butin : |rŒuf mystérieux", "Les cobras d\'Azeroth sont célèbres pour leur venin à action rapide et leurs frappes fulgurantes.");
INSERT INTO `dbc_battle_pet_species` VALUES (198, 32595, 655866, 61357, 8, 2, 2, "|cFFFFD200Vendeur : |rSairuk|n|cFFFFD200Région : |rDésolation des dragons|n|cFFFFD200Faction : |rLes Kalu’aks - Exalté|n|cFFFFD200Coût : |r12|TINTERFACE\MONEYFRAME\UI-GOLDICON.BLP:0|t|n|n|cFFFFD200Vendeur : |rTanaika|n|cFFFFD200Région : |rFjord Hurlant|n|cFFFFD200Faction : |rLes Kalu’aks - Exalté|n|cFFFFD200Coût : |r12|TINTERFACE\MONEYFRAME\UI-GOLDICON.BLP:0|t", "Connu pour ses grands yeux ébahis et ses glissades stylées.");
INSERT INTO `dbc_battle_pet_species` VALUES (199, 32643, 132102, 61472, 6, 5, 82, "|cFFFFD200Haut fait :|r Lectures studieuses|n|cFFFFD200Catégorie : |rGénéral", "Pour empêcher les apprentis d\'utiliser ce familier pour faire leurs corvées à leur place, les archimages dispersèrent la formule permettant de l\'invoquer dans les pages de nombreux recueils magiques cachés dans Dalaran.");
INSERT INTO `dbc_battle_pet_species` VALUES (200, 32791, 134010, 61725, 4, 6, 2, "|cFFFFD200Évènement mondial :|r Jardin des nobles|n|cFFFFD200Vendeur : |rMarchand du Jardin des nobles|n|cFFFFD200Coût : |r100|TINTERFACE\ICONS\Achievement_Noblegarden_Chocolate_Egg:0|t", "Les lapins du printemps symbolisent la fertilité, et ils adorent montrer pourquoi.");
INSERT INTO `dbc_battle_pet_species` VALUES (201, 32818, 250626, 61773, 2, 6, 82, "|cFFFFD200Évènement mondial :|r Bienfaits du pèlerin", "Humble, clairvoyante, et délicieuse avec une petite sauce aux airelles.");
INSERT INTO `dbc_battle_pet_species` VALUES (202, 32841, 294471, 61855, 7, 5, 82, "|cFFFFD200Haut fait : |r4e anniversaire de WoW|n|cFFFFD200Catégorie : |rTours de force", "Cet ours rare est élevé pour sa taille réduite et son apparence éternellement jeune.");
INSERT INTO `dbc_battle_pet_species` VALUES (203, 32939, 656350, 61991, 4, 5, 82, "|cFFFFD200Haut fait : |rLa chasse au p’tit gibier|n|cFFFFD200Catégorie : |rCombats de mascottes", "Elle adore être entourée d\'amis. D\'au moins soixante-quinze amis, pour être précis.");
INSERT INTO `dbc_battle_pet_species` VALUES (204, 33188, 134183, 62491, 6, 2, 2, "|cFFFFD200Vendeur : |rFreux Poing-faucon|n|cFFFFD200Région : |rLa Couronne de glace|n|cFFFFD200Coût : |r40|TINTERFACE\ICONS\Ability_Paladin_ArtofWar.blp:0|t", "Tous les jours, il danse dès le réveil. Tant de joie de vivre dans un si petit arbre !");
INSERT INTO `dbc_battle_pet_species` VALUES (205, 33194, 236243, 62508, 7, 2, 2, "|cFFFFD200Vendeur : |rDerrick Piquebarbe|n|cFFFFD200Région : |rLa Couronne de glace|n|cFFFFD200Coût : |r40|TINTERFACE\ICONS\Ability_Paladin_ArtofWar.blp:0|t", "Les ours de Dun Morogh sont très recherchés pour leur robustesse, leur fidélité et leur ardeur au combat. Et la compagnie des nains ne fait rien pour atténuer leur férocité.");
INSERT INTO `dbc_battle_pet_species` VALUES (206, 33197, 132182, 62510, 2, 2, 2, "|cFFFFD200Vendeur : |rEliza Killian|n|cFFFFD200Région : |rLa Couronne de glace|n|cFFFFD200Coût : |r40|TINTERFACE\ICONS\Ability_Paladin_ArtofWar.blp:0|t", "Capable de percevoir les battements de cœur d\'un adversaire par écholocalisation.");
INSERT INTO `dbc_battle_pet_species` VALUES (207, 33198, 132195, 62513, 7, 2, 2, "|cFFFFD200Vendeur : |rFreka Hache-sanglante|n|cFFFFD200Région : |rLa Couronne de glace|n|cFFFFD200Coût : |r40|TINTERFACE\ICONS\Ability_Paladin_ArtofWar.blp:0|t", "L\'impétueux scorpion de Durotar est un féroce prédateur du désert. Mais il ne pique que rarement son propre maître.");
INSERT INTO `dbc_battle_pet_species` VALUES (209, 33200, 136071, 62516, 4, 2, 2, "|cFFFFD200Vendeur : |rCaporal Arthur Flew|n|cFFFFD200Région : |rLa Couronne de glace|n|cFFFFD200Coût : |r40|TINTERFACE\ICONS\Ability_Paladin_ArtofWar.blp:0|t", "Contrairement à ceux obtenus par métamorphose, les véritables moutons de souche peuvent se révéler effrayants.");
INSERT INTO `dbc_battle_pet_species` VALUES (210, 33219, 132198, 62542, 4, 2, 2, "|cFFFFD200Vendeur : |rDoru Corne-Tonnerre|n|cFFFFD200Région : |rLa Couronne de glace|n|cFFFFD200Coût : |r40|TINTERFACE\ICONS\Ability_Paladin_ArtofWar.blp:0|t", "La vitesse de course d\'un haut-trotteur sur terrain dégagé est inégalée. Ses prédateurs doivent donc avoir recours à la furtivité et aux tactiques de groupe.");
INSERT INTO `dbc_battle_pet_species` VALUES (211, 33226, 132186, 62561, 8, 3, 2, "|cFFFFD200Métier : |rPêche|n|cFFFFD200Région : |rNorfendre, Hurlevent, Orgrimmar", "Le clampant des rivages s\'est adapté au grand froid du nord en hibernant sous les plages insulaires.");
INSERT INTO `dbc_battle_pet_species` VALUES (212, 33205, 133941, 62562, 6, 2, 2, "|cFFFFD200Vendeur : |rIrisee|n|cFFFFD200Région : |rLa Couronne de glace|n|cFFFFD200Coût : |r40|TINTERFACE\ICONS\Ability_Paladin_ArtofWar.blp:0|t |n", "Les effets à long terme de la pollution entraînée par l\'Exodar font encore l\'objet d’observations.");
INSERT INTO `dbc_battle_pet_species` VALUES (213, 33227, 655994, 62564, 5, 2, 2, "|cFFFFD200Vendeur : |rTrellis Solmatin|n|cFFFFD200Région : |rLa Couronne de glace|n|cFFFFD200Coût : |r40|TINTERFACE\ICONS\Ability_Paladin_ArtofWar.blp:0|t", "Créé par un apprenti elfe de sang, ce balai se révèlerait une invention très utile si quelqu\'un trouvait un jour comment le faire s\'arrêter.");
INSERT INTO `dbc_battle_pet_species` VALUES (214, 33238, 236689, 62609, 0, 1, 659, "|cFFFFD200Quête :|r La naissance d’un Champion|n|cFFFFD200Région :|r La Couronne de glace", "De nombreux adolescents de Hurlevent se sont engagés comme écuyers dans la Croisade d\'argent, espérant un jour pouvoir servir la Lumière sacrée au combat.");
INSERT INTO `dbc_battle_pet_species` VALUES (215, 33274, 132247, 62674, 9, 2, 2, "|cFFFFD200Vendeur : |rRillie Fuseboulon|n|cFFFFD200Région : |rLa Couronne de glace", "Utilisé comme monture d\'apprentissage pour les jeunes gnomes, le mécacuicui n\'a pas encore la vitesse de ses grands frères, mais déjà leur charme.");
INSERT INTO `dbc_battle_pet_species` VALUES (216, 33239, 236689, 62746, 0, 1, 403, "|cFFFFD200Quête :|r La naissance d’un Champion|n|cFFFFD200Région :|r La Couronne de glace", "Ces jeunes orcs s\'efforcent d\'incarner les valeurs de la Croisade d\'argent : vaillance, force et intégrité.");
INSERT INTO `dbc_battle_pet_species` VALUES (217, 33578, 135128, 63318, 0, 7, 82, "|cFFFFD200Promotion :|r Tournoi d’arène", "Bourbimus est le plus imposant de tous les combattants d\'arène murlocs. Et pendant longtemps, il était le seul.");
INSERT INTO `dbc_battle_pet_species` VALUES (218, 33810, 133798, 63712, 3, 2, 2050, "|cFFFFD200Vendeur : |rSamamba|n|cFFFFD200Région : |rLa Couronne de glace|n|cFFFFD200Coût : |r40|TINTERFACE\ICONS\Ability_Paladin_ArtofWar.blp:0|t|n", "Ce fétiche vous apporte trois compagnons en un. Impossible de savoir lequel sortira avant de l\'avoir invoqué !");
INSERT INTO `dbc_battle_pet_species` VALUES (220, 34278, 132129, 65046, 6, 1, 18, "|cFFFFD200Quête : |rSouvenirs d\'Auberdine|n|cFFFFD200Région : |rSombrivage", "Une évocation solennelle de ce qui fut perdu lorsque le Brise-monde changea pour toujours la face d\'Azeroth.");
INSERT INTO `dbc_battle_pet_species` VALUES (224, 34364, 656576, 65358, 7, 2, 2, "|cFFFFD200Vendeur : |rBreanni|n|cFFFFD200Région : |rForêt du Chant de cristal|n|cFFFFD200Coût : |r50|TINTERFACE\MONEYFRAME\UI-GOLDICON.BLP:0|t", "Nombreux sont ceux qui se sont moqués de ce chat juste avant un combat. Les derniers mots qu\'ils prononcent sont en général : « Ouh le gentil chachat à sa mémère. »");
INSERT INTO `dbc_battle_pet_species` VALUES (225, 33530, 252780, 65381, 0, 6, 18, "|cFFFFD200Évènement mondial :|r Semaine des enfants", "Le sautillant gorloc est un lointain cousin du murloc.");
INSERT INTO `dbc_battle_pet_species` VALUES (226, 33529, 236698, 65382, 0, 6, 18, "|cFFFFD200Évènement mondial :|r Semaine des enfants", "Malgré leur petite taille, les varleus sont parmi les créatures les plus agressives du Norfendre, ce qui rend souvent leur curiosité naturelle un peu intimidante.");
INSERT INTO `dbc_battle_pet_species` VALUES (227, 34587, 134239, 65682, 9, 7, 82, "|cFFFFD200Promotion :|r Promotion Mountain Dew", "Un robot de combat dont la batterie est alimentée par de délicieux électrolytes bleus et rouges.");
INSERT INTO `dbc_battle_pet_species` VALUES (228, 34694, 656340, 66030, 0, 7, 82, "|cFFFFD200Promotion :|r BlizzCon 2009", "Très rares sont les murlocs qui réussissent à franchir les frontières de l\'espace et du temps. Plus rares encore sont ceux qui en reviennent avec le type d\'armure et d\'armement futuristes que porte Grognie.");
INSERT INTO `dbc_battle_pet_species` VALUES (229, 34724, 132849, 66096, 5, 2, 2, "|cFFFFD200Vendeur : |rHiren Chanterite|n|cFFFFD200Région : |rLa Couronne de glace|n|cFFFFD200Faction : |rLe Concordat argenté - Exalté|n|cFFFFD200Coût : |r40|TINTERFACE\ICONS\Ability_Paladin_ArtofWar.blp:0|t|n", "Cette rarissime espèce de wyrm de mana est très recherchée pour sa couleur unique et son tempérament docile.");
INSERT INTO `dbc_battle_pet_species` VALUES (231, 34930, 134134, 66520, 5, 7, 82, "|cFFFFD200Promotion :|r Parrainez un ami", "Considérés comme des porte-bonheur, les tigres sculptés dans le jade sont très recherchés, mais dompter un tigre de jade est une récompense encore plus grande.");
INSERT INTO `dbc_battle_pet_species` VALUES (232, 35396, 132193, 67413, 7, 0, 2, "|cFFFFD200Butin : |rŒuf de Flèche|n|cFFFFD200Région : |rMarécage d’Âprefange", "Quitte rarement le nid avant d\'être arrivé à maturité. Adore aller faire des farces au nid de tranchegueules le plus proche.");
INSERT INTO `dbc_battle_pet_species` VALUES (233, 35395, 132193, 67414, 7, 0, 2, "|cFFFFD200Butin : |rGardien déviant, Ravageur déviant|n|cFFFFD200Région : |rCavernes des Lamentations", "La demande de cette race aux tons pourpres a entraîné un fort déclin de la population de raptors dans la région des cavernes des Lamentations.");
INSERT INTO `dbc_battle_pet_species` VALUES (234, 35400, 132253, 67415, 7, 0, 2, "|cFFFFD200Butin : |rRaptor de Gundrak|n|cFFFFD200Région : |rZul’Drak", "Ce raptor rusé vient des déserts gelés du Norfendre. L\'hostilité de l\'environnement dans lequel il a grandi lui permet de rester fort là où beaucoup d\'autres seraient déjà morts.");
INSERT INTO `dbc_battle_pet_species` VALUES (235, 35387, 132253, 67416, 7, 0, 2, "|cFFFFD200Butin : |rŒuf de Takk|n|cFFFFD200Région : |rLes Tarides", "Ce petit de Takk le Bondisseur possède une agilité incroyable. Il est à l\'aise même dans les environnements les plus encombrés.");
INSERT INTO `dbc_battle_pet_species` VALUES (236, 35399, 132253, 67417, 7, 2, 2, "|cFFFFD200Vendeur : |rBreanni|n|cFFFFD200Région : |rForêt du Chant de cristal|n|cFFFFD200Coût : |r50|TINTERFACE\MONEYFRAME\UI-GOLDICON.BLP:0|t", "Ce petit est recherché pour sa robe unique. Mais malgré les soins affectueux de Breanni, le naturel féroce du raptor revient toujours au galop.");
INSERT INTO `dbc_battle_pet_species` VALUES (237, 35397, 132253, 67418, 7, 0, 2, "|cFFFFD200Butin : |rŒuf de matriarche ravasaure|n|cFFFFD200Région : |rCratère d’Un’Goro", "Plus grands que les autres bébés raptors, ces petits deviennent d\'imposants reptiles capables de ravages impressionnants.");
INSERT INTO `dbc_battle_pet_species` VALUES (238, 35398, 132193, 67419, 7, 0, 2, "|cFFFFD200Butin : |rŒuf de matriarche tranchegueule|n|cFFFFD200Région : |rLes Paluns", "Les mères tranchegueules protègent leurs œufs avec sauvagerie. L\'air adorable des petits ne fait que cacher leur férocité innée.");
INSERT INTO `dbc_battle_pet_species` VALUES (239, 35394, 132193, 67420, 7, 0, 2, "|cFFFFD200Butin : |rButin mondial|n|cFFFFD200Région : |rStrangleronce septentrionale, Cap Strangleronce", "Les raptors razzashi font partie des prédateurs les plus rusés de la jungle, ce qui leur attire la faveur des trolls gurubashi.");
INSERT INTO `dbc_battle_pet_species` VALUES (240, 35468, 134399, 67527, 5, 7, 82, "|cFFFFD200Promotion :|r Évènement mondial en Corée", "La nuit, la panthère onyx est invisible, semblable à une ombre qui rôde et disparaît à l’aube.");
INSERT INTO `dbc_battle_pet_species` VALUES (241, 36482, 318523, 68767, 2, 8, 2, "|cFFFFD200Jeu de cartes à collectionner :|r Guerre du Fléau", "Lorsqu\'on aperçoit ces cerfs-volants au-dessus des rives du Norfendre, c\'est un signe : les roharts ne sont pas loin.");
INSERT INTO `dbc_battle_pet_species` VALUES (242, 36511, 298591, 68810, 5, 8, 2, "|cFFFFD200Jeu de cartes à collectionner :|r Guerre du Fléau", "Curieux et affectueux, ces chatons semblent réussir à se cacher dans les endroits les plus surprenants.");
INSERT INTO `dbc_battle_pet_species` VALUES (243, 36607, 134154, 69002, 1, 5, 82, "|cFFFFD200Haut fait : |r5e anniversaire de WoW|n|cFFFFD200Catégorie : |rTours de force", "Ce jeune dragon, rejeton d\'Onyxia, est fermement décidé à ne pas reproduire les erreurs cataclysmiques de son grand-père.");
INSERT INTO `dbc_battle_pet_species` VALUES (244, 36871, 236191, 69452, 6, 7, 82, "|cFFFFD200Promotion :|r Authenticator attaché au compte|n", "Venus des terres de Feu et du Cœur du magma, les chiens du magma sont de la lignée du mâle alpha, Magmadar.");
INSERT INTO `dbc_battle_pet_species` VALUES (245, 36908, 327735, 69535, 2, 9, 82, "|cFFFFD200Boutique en jeu|r", "Cadeau des nains Marteaux-hardis aux héros de l\'Alliance, ces petits descendent directement des griffons chevauchés par Falstad et son clan à Grim Batol.");
INSERT INTO `dbc_battle_pet_species` VALUES (246, 36909, 328270, 69536, 7, 9, 82, "|cFFFFD200Boutique en jeu|r", "Thorg le Cul-de-jatte aurait dû mieux écouter le vieux proverbe orc qui dit : « Ne tirez pas la wyverne par la queue. »");
INSERT INTO `dbc_battle_pet_species` VALUES (247, 36910, 237194, 69539, 5, 7, 82, "|cFFFFD200Promotion :|r Parrainez un ami", "Un grand nombre de personnes pense (peut-être à tort) que la couleur unique de cette famille de tigres, aperçus en de très rares occasions, provient de magies résiduelles ou d’un autre évènement de ce genre.");
INSERT INTO `dbc_battle_pet_species` VALUES (248, 36911, 328269, 69541, 0, 9, 82, "|cFFFFD200Boutique en jeu|r", "Le moine pandaren est un compagnon intrépide, qui s\'entraîne avec acharnement pour perfectionner son art… sauf si vous lui offrez un verre. Il trouverait cela malpoli de refuser.");
INSERT INTO `dbc_battle_pet_species` VALUES (249, 36979, 254094, 69677, 3, 9, 82, "|cFFFFD200Boutique en jeu|r", "De son vivant, Kel\'Thuzad a trahi le Kirin Tor et créé le Culte des damnés. Dans la mort, il sert le Fléau comme seigneur archiliche de Naxxramas.");
INSERT INTO `dbc_battle_pet_species` VALUES (250, 37865, 133718, 70613, 4, 5, 82, "|cFFFFD200Haut fait : |rRecherche démentielle de compagnons|n|cFFFFD200Catégorie : |rDonjons et raids", "Groopy est tout petit, mais très dangereux si on le pousse à bout.");
INSERT INTO `dbc_battle_pet_species` VALUES (251, 38374, 134437, 71840, 5, 6, 2, "|cFFFFD200Évènement mondial :|r De l’amour dans l’air", "Cette, euh, « adorable » petite créature adore jouer avec les bestioles. Il faut juste ne pas les laisser approcher trop près.");
INSERT INTO `dbc_battle_pet_species` VALUES (253, 40198, 135851, 74932, 6, 6, 2, "|cFFFFD200Évènement mondial :|r Fête du Feu du solstice d’été", "Même s\'il est clairement moins dangereux que son premier maître, le seigneur du Givre Ahune, le givrelin s\'est déjà fait prendre à bombarder les aventuriers innocents de boules de neige.");
INSERT INTO `dbc_battle_pet_species` VALUES (254, 40295, 133000, 75134, 9, 2, 2, "|cFFFFD200Vendeur : |rVendeurs dans le monde|n|cFFFFD200Coût : |r50|TINTERFACE\MONEYFRAME\UI-GOLDICON.BLP:0|t", "C\'est la meilleure vente du rayon jouets de Jepetto cette saison. Le nouveau châssis bleu de ce robot au tempérament de feu en fait la coqueluche des enfants d\'Azeroth.");
INSERT INTO `dbc_battle_pet_species` VALUES (255, 40624, 368365, 75613, 1, 5, 82, "|cFFFFD200Haut fait : |rLa petite boutique des mascottes|n|cFFFFD200Catégorie : |rCombats de mascottes", "Breanni de Dalaran, une des plus grandes spécialistes des compagnons exotiques du monde, n\'offre le dragon céleste mystique qu\'à ceux dont la folie collectionneuse peut égaler la sienne.");
INSERT INTO `dbc_battle_pet_species` VALUES (256, 40703, 254104, 75906, 9, 9, 82, "|cFFFFD200Boutique en jeu|r", "Cet instable petit assemblage est une reproduction du gigantesque prototype des titans d\'Ulduar. Il lui arrive de casser ses jouets.");
INSERT INTO `dbc_battle_pet_species` VALUES (257, 40721, 135128, 75936, 0, 7, 160, "|cFFFFD200Promotion :|r Tournoi d’arène 2010", "Bourbimus est le plus imposant de tous les combattants d\'arène murlocs. Le seul, en fait.");
INSERT INTO `dbc_battle_pet_species` VALUES (258, 42078, 387415, 78381, 9, 7, 82, "|cFFFFD200Promotion :|r Édition collector de Starcraft II: Wings of Liberty", "Puissante unité d\'artillerie de l\'armée terrane. Le Thor est toujours le premier à foncer au feu, et le dernier à en sortir.");
INSERT INTO `dbc_battle_pet_species` VALUES (259, 42177, 132188, 78683, 2, 1, 18, "|cFFFFD200Quête :|r Qui vole sur un œuf...|n|cFFFFD200Région :|r Mont Hyjal|n|n", "Contrairement à son homologue doré, le mini jouteur bleu aime prendre son temps quand il plante sa lance dans les yeux d\'un adversaire.");
INSERT INTO `dbc_battle_pet_species` VALUES (260, 42183, 132200, 78685, 2, 1, 18, "|cFFFFD200Quête :|r Qui vole sur un œuf...|n|cFFFFD200Région :|r Mont Hyjal|n|n", "Cette petite créature est bien connue pour sa capacité à vous suriner les yeux plus vite que l\'éclair.");
INSERT INTO `dbc_battle_pet_species` VALUES (261, 43800, 133861, 81937, 9, 3, 2, "|cFFFFD200Métier : |rIngénierie", "Le dernier cri de l\'ingénierie gobeline. Plans inspirés du gigantesque saccageur gangrené de la péninsule des Flammes infernales.");
INSERT INTO `dbc_battle_pet_species` VALUES (262, 43916, 466036, 82173, 9, 3, 2, "|cFFFFD200Métier : |rIngénierie", "Robuste porte-étendard de la technologie gnome, ce petit automate est un compagnon parfait pour les ingénieurs solitaires itinérants.");
INSERT INTO `dbc_battle_pet_species` VALUES (264, 45128, 134555, 84263, 3, 3, 2, "|cFFFFD200Métier : |rArchéologie", "À première vue, il ne s\'agit que de la patte d\'un petit primate. À mieux y regarder, on s\'aperçoit que son aspect desséché lui vient de siècles d\'exposition aux sables d\'Uldum.");
INSERT INTO `dbc_battle_pet_species` VALUES (265, 45247, 136024, 84492, 6, 5, 82, "|cFFFFD200Haut fait : |rPour l’amour d’une pierre|n|cFFFFD200Catégorie : |rQuêtes", "Petit élémentaire aventureux, Gravillon a échappé aux girevers affamés des profondeurs Désagrégées et bravé le vide sidéral du Néant distordu, pour terminer enfermé dans une étroite boîte aux lettres.");
INSERT INTO `dbc_battle_pet_species` VALUES (266, 45340, 458255, 84752, 3, 3, 2, "|cFFFFD200Métier : |rArchéologie", "Vous le fixez dans les yeux. Et il vous fixe aussi intensément. Il paraît si intelligent. Parfois, on jurerait qu’il songe au meilleur moyen d\'ouvrir les portes.");
INSERT INTO `dbc_battle_pet_species` VALUES (267, 46898, 134249, 93836, 5, 3, 2, "|cFFFFD200Métier :|r Enchantement (525)|n|cFFFFD200Formule :|r Lanterne enchantée", "Réduit les chances de vous faire dévorer par des monstres voraces lorsque vous pénétrez dans un endroit obscur.");
INSERT INTO `dbc_battle_pet_species` VALUES (268, 46896, 441880, 87344, 1, 7, 82, "|cFFFFD200Promotion :|r Édition collector de Cataclysm", "Des nations entières tremblent sous son ombre. Il est le Destructeur, celui qui fut l’Aspect de la terre, et qui brisa Azeroth. Désormais disponible en format poche.");
INSERT INTO `dbc_battle_pet_species` VALUES (270, 47944, 464139, 89039, 6, 2, 82, "|cFFFFD200Vendeur :|r Vendeur de guilde|n|cFFFFD200Région :|r Hurlevent, Orgrimmar|n|cFFFFD200Coût : |r300|TINTERFACE\MONEYFRAME\UI-GOLDICON.BLP:0|t|n", "Les faucons de feu naissent des cendres d\'un phénix. Les phénix sombres, eux, naissent de leurs ombres.");
INSERT INTO `dbc_battle_pet_species` VALUES (271, 48107, 464160, 89472, 2, 2, 2, "|cFFFFD200Vendeur : |rIntendant Brazie|n|cFFFFD200Région : |rPéninsule de Tol Barad|n|cFFFFD200Faction : |rLes Gardiens de Baradin - Honoré|n|cFFFFD200Coût : |r50|TINTERFACE\ICONS\Achievement_Zone_TolBarad.blp:0|t", "Cet oiseau est un vétéran chevronné des longues batailles navales autour de Tol Barad. Un vétéran un peu empâté, il faut bien le dire, à force de se servir sur les épaves.");
INSERT INTO `dbc_battle_pet_species` VALUES (272, 48242, 464159, 89670, 4, 2, 82, "|cFFFFD200Vendeur :|r Vendeur de guilde|n|cFFFFD200Région :|r Hurlevent, Orgrimmar|n|cFFFFD200Coût : |r300|TINTERFACE\MONEYFRAME\UI-GOLDICON.BLP:0|t|n", "Se roule en boule pour se protéger quand il se sent menacé. Ou quand sa mère l\'appelle pour le dîner.");
INSERT INTO `dbc_battle_pet_species` VALUES (277, 48609, 134152, 90523, 9, 3, 2, "|cFFFFD200Métier : |rArchéologie", "Le gnome mécanique serait à l\'origine une création des titans. Certaines de ses pièces sont si petites qu\'on ne peut les voir à l\'œil nu.");
INSERT INTO `dbc_battle_pet_species` VALUES (278, 48641, 462532, 90637, 7, 0, 2, "|cFFFFD200Butin : |rRenard de Baradin|n|cFFFFD200Région : |rPéninsule de Tol Barad", "Les incessants combats de Tol Barad rendent ces renardeaux difficiles à se procurer. L\'afflux de collectionneurs a donc décimé la population de renards de la région.");
INSERT INTO `dbc_battle_pet_species` VALUES (279, 48982, 134397, 91343, 6, 0, 2, "|cFFFFD200Butin : |rJadecroc|n|cFFFFD200Région : |rLe Tréfonds", "L\'araignée de schiste est un élémentaire, pas un véritable arachnide. Mais sa résilience et sa férocité en font un animal craint jusqu\'au cœur du Tréfonds.");
INSERT INTO `dbc_battle_pet_species` VALUES (280, 49586, 413584, 92395, 0, 2, 663, "|cFFFFD200Vendeur : |rVendeur de guilde|n|cFFFFD200Région :|r Hurlevent, Orgrimmar|n|cFFFFD200Coût : |r300|TINTERFACE\MONEYFRAME\UI-GOLDICON.BLP:0|t|n|cFFFFD200Temps de recharge : |r8 h", "Les pages de guildes se doivent d\'être intrépides, car c\'est à eux qu\'il revient d\'annoncer les victoires de leurs guildes sur la Horde.");
INSERT INTO `dbc_battle_pet_species` VALUES (281, 49588, 413584, 92396, 0, 2, 407, "|cFFFFD200Vendeur : |rVendeur de guilde|n|cFFFFD200Région :|r Hurlevent, Orgrimmar|n|cFFFFD200Coût : |r300|TINTERFACE\MONEYFRAME\UI-GOLDICON.BLP:0|t|n|cFFFFD200Temps de recharge : |r8 h", "Quand une guilde se couvre d\'honneur en terrassant les champions de l\'Alliance, certains orcs viennent s\'associer à sa gloire en se mettant à son service.");
INSERT INTO `dbc_battle_pet_species` VALUES (282, 49587, 413585, 92397, 0, 2, 151, "|cFFFFD200Vendeur : |rVendeur de guilde|n|cFFFFD200Région :|r Hurlevent, Orgrimmar|n|cFFFFD200Coût : |r500|TINTERFACE\MONEYFRAME\UI-GOLDICON.BLP:0|t|n|cFFFFD200Temps de recharge : |r4 h", "Quand une guilde a accumulé des monceaux de richesses, de courageux soldats sont appelés pour porter haut ses couleurs.");
INSERT INTO `dbc_battle_pet_species` VALUES (283, 49590, 413585, 92398, 0, 2, 151, "|cFFFFD200Vendeur : |rVendeur de guilde|n|cFFFFD200Région :|r Hurlevent, Orgrimmar|n|cFFFFD200Coût : |r500|TINTERFACE\MONEYFRAME\UI-GOLDICON.BLP:0|t|n|cFFFFD200Temps de recharge : |r4 h", "Les guildes riches s\'attirent la faveur de soldats prêts à porter leurs étendards au combat.");
INSERT INTO `dbc_battle_pet_species` VALUES (285, 50468, 254104, 93624, 9, 8, 2, "|cFFFFD200Jeu de cartes à collectionner : |rLe Brise-monde|n", "Toujours « prêt à jouer », le mini XT de Landro est presque impossible à distinguer du vrai. Sujets à de fréquents caprices et attaques de narcolepsie.");
INSERT INTO `dbc_battle_pet_species` VALUES (286, 50586, 648918, 93739, 7, 0, 2, "|cFFFFD200Butin mondial :|r Maleterres de l’Est (requiert Le porte-bonheur de Fiona)", "Les larves des Maleterres sont un effet secondaire de la corruption nécromantique et de l\'accumulation de cadavres. M. Boustifs est d\'une lignée particulièrement agile.");
INSERT INTO `dbc_battle_pet_species` VALUES (287, 51632, 237146, 93813, 2, 1, 18, "|cFFFFD200Quête :|r SEPT ÇA SUFFIT !, Pas des lucioles, des flamouches !|n|cFFFFD200Région :|r Steppes Ardentes|n", "Ces insectes étranges volent autour des bassins de lave dans la journée et émettent une lumière incandescente la nuit.");
INSERT INTO `dbc_battle_pet_species` VALUES (289, 51635, 134434, 93817, 4, 6, 2, "|cFFFFD200Évènement mondial :|r Semaine des enfants", "Si vous le cherchez, il est dans sa coquille. Comme tout escargot qui se respecte.");
INSERT INTO `dbc_battle_pet_species` VALUES (291, 51090, 133939, 93823, 6, 1, 18, "|cFFFFD200Quête :|r Gazon maudit|n|cFFFFD200Région :|r Contreforts de Hautebrande|n", "Cette fleur suave embellira votre journée grâce à son chaleureux sourire et son chant joyeux ! Aussi très efficace contre les morts-vivants.");
INSERT INTO `dbc_battle_pet_species` VALUES (292, 50545, 134971, 93837, 5, 3, 2, "|cFFFFD200Métier : |rEnchantement", "Higitus figi… ? Hockiti pocki… ? Abracadab… ? Prestidigi… Oh, mais enfin Archimède !");
INSERT INTO `dbc_battle_pet_species` VALUES (293, 50722, 399040, 93838, 6, 0, 2, "|cFFFFD200Butin :|r Filon d’élémentium, riche filon d’élémentium|n|cFFFFD200Région :|r Le Tréfonds, Hautes-terres du Crépuscule, Uldum", "Souvent confondue avec une gemme rare par les mineurs, la géode prévient de sa présence en émettant des vibrations mélodiques.");
INSERT INTO `dbc_battle_pet_species` VALUES (294, 51122, 441880, 94070, 0, 7, 82, "|cFFFFD200Promotion :|r BlizzCon 2010", "La venue d\'Aile de mort a profondément transformé Azeroth. Même les murlocs des fonds océaniques n\'ont pas échappé à l\'influence de sa corruption.");
INSERT INTO `dbc_battle_pet_species` VALUES (296, 51601, 467893, 95786, 0, 9, 594, "|cFFFFD200Boutique en jeu :|r Alliance", "Les séléniens sont bénis par Elune et répandent la grâce de la nature partout où ils vont. Ces vivaces volailles sont toujours partantes pour une petite danse.");
INSERT INTO `dbc_battle_pet_species` VALUES (297, 51600, 254652, 95787, 6, 9, 82, "|cFFFFD200Boutique en jeu|r", "Il n\'est jamais trop tôt pour invoquer cette minuscule incarnation du funeste seigneur du Feu.");
INSERT INTO `dbc_battle_pet_species` VALUES (298, 51649, 467894, 95909, 0, 9, 338, "|cFFFFD200Boutique en jeu :|r Horde", "Le rugissement caractéristique du sélénien est l\'un des sons les plus terrifiants du règne animal.");
INSERT INTO `dbc_battle_pet_species` VALUES (301, 52226, 132225, 96571, 7, 1, 18, "|cFFFFD200Quête :|r Faire le bien|n|cFFFFD200Région :|r Strangleronce septentrionale|n|n", "Il vous a regardé avec ses grands yeux noirs, l\'air de vous demander : « C’est toi, ma maman ? »");
INSERT INTO `dbc_battle_pet_species` VALUES (302, 52343, 254094, 96817, 3, 8, 2, "|cFFFFD200Jeu de cartes à collectionner :|r Le Brise-monde", "Comment le gobelin Landro Tirauloin a pu se procurer sa lichette reste un mystère… mais il n\'aurait jamais dû la mettre en jeu si facilement lors d\'une partie de cartes.");
INSERT INTO `dbc_battle_pet_species` VALUES (303, 52344, 132242, 96819, 7, 8, 2, "|cFFFFD200Jeu de cartes à collectionner :|r Le Crépuscule des Dragons", "Elune a donné aux sabres-de-nuit leur robe noire et argentée afin qu\'ils puissent se fondre dans les ombres du clair de lune.");
INSERT INTO `dbc_battle_pet_species` VALUES (306, 52831, 132252, 97638, 7, 2, 2, "|cFFFFD200Vendeur : |rMichelle De Rum|n|cFFFFD200Région : |rBerceau-de-l’Hiver|n|cFFFFD200Coût : |r50|TINTERFACE\MONEYFRAME\UI-GOLDICON.BLP:0|t", "C\'est quand ils batifolent dans la neige fraîche que les bébés de Berceau-de-l\'Hiver sont les plus heureux.");
INSERT INTO `dbc_battle_pet_species` VALUES (307, 52894, 132193, 97779, 7, 1, 18, "|cFFFFD200Quête : |rUn vieil ami|n|cFFFFD200Région : |rZul’Gurub", "Dans les jungles de Strangleronce, les bébés raptors qui n\'ont pas de chance sont capturés par les Gurubashi et emmenés dans la cité trolle de Zul’Gurub.");
INSERT INTO `dbc_battle_pet_species` VALUES (308, 53048, 254502, 98079, 5, 6, 2, "|cFFFFD200Évènement mondial :|r Semaine des enfants", "Les marcheurs des marais sauvages sont agressifs et dangereux. Mais si vous réussissez à en apprivoiser un, il se montrera fidèle et affectueux.");
INSERT INTO `dbc_battle_pet_species` VALUES (309, 53225, 516665, 98571, 2, 3, 2, "|cFFFFD200Métier : |rArchéologie", "Les œufs de pterreurdactyles sont couvés sous les fossiles de leurs ancêtres, ce qui permet aux bébés de s\'imprégner de la férocité et de la ruse de leurs ascendants.");
INSERT INTO `dbc_battle_pet_species` VALUES (310, 53232, 516666, 98587, 3, 3, 2, "|cFFFFD200Métier : |rArchéologie", "Les figurines vaudou peuvent tirer leur puissance des flacons de mojo, de la sueur de troll et de la chair des ennemis de leur tribu.");
INSERT INTO `dbc_battle_pet_species` VALUES (311, 53283, 519402, 98736, 2, 9, 2, "|cFFFFD200Boutique en jeu|r", "Ce minuscule lion ailé est encore un peu maladroit, mais il s\'entraîne dur pour devenir la meilleure monture possible.");
INSERT INTO `dbc_battle_pet_species` VALUES (316, 53623, 132833, 99578, 2, 9, 4178, "|cFFFFD200Boutique en jeu|r", "Les bébés hippogriffes cénariens, pas encore aussi redoutables qu’ils le deviendront adultes, ont une apparence presque inoffensive qui a pris au dépourvu plus d’un dresseur novice du Cercle cénarien.");
INSERT INTO `dbc_battle_pet_species` VALUES (317, 53658, 446127, 99663, 7, 2, 2, "|cFFFFD200Vendeur : |rVarlan Hautebranche|n|cFFFFD200Région : |rFront du Magma|n|cFFFFD200Coût : |r1500|TINTERFACE\MONEYFRAME\UI-GOLDICON.BLP:0|t", "Les oursons d\'Hyjal ont beaucoup souffert lors du Cataclysme. Mais pour voir le bon côté des choses, c\'est aussi lui qui a développé leur attirance pour les hauteurs et les acrobaties périlleuses.");
INSERT INTO `dbc_battle_pet_species` VALUES (318, 53661, 133941, 99668, 6, 2, 2, "|cFFFFD200Vendeur : |rAyla Ombretempête|n|cFFFFD200Région : |rFront du Magma|n|cFFFFD200Coût : |r1500|TINTERFACE\MONEYFRAME\UI-GOLDICON.BLP:0|t", "Né sur le sol cendreux des terres de Feu, le flagellant fut transplanté vers Azeroth avec l\'aide des Gardiens d\'Hyjal.");
INSERT INTO `dbc_battle_pet_species` VALUES (319, 53884, 236547, 100330, 7, 6, 2, "|cFFFFD200Évènement mondial :|r Sanssaint|n|cFFFFD200Vendeur : |rPoupin|n|cFFFFD200Région : |rClairières de Tirisfal|n|cFFFFD200Coût : |r150|TINTERFACE\ICONS\achievement_halloween_candy_01:0|t|n|n|cFFFFD200Vendeur : |rDorothy|n|cFFFFD200Région : |rHurlevent|n|cFFFFD200Coût : |r150|TINTERFACE\ICONS\achievement_halloween_candy_01:0|t", "Avec cette sorcière à moustaches, il faut jouer à pile ou fâche, et s\'envoler à tombeau ouvert !");
INSERT INTO `dbc_battle_pet_species` VALUES (320, 54027, 524050, 100576, 1, 2, 82, "|cFFFFD200Vendeur : |rVendeur de guilde|n|cFFFFD200Région :|r Hurlevent, Orgrimmar|n|cFFFFD200Coût : |r1500|TINTERFACE\MONEYFRAME\UI-GOLDICON.BLP:0|t|n", "Membre légendaire du vol draconique bleu.");
INSERT INTO `dbc_battle_pet_species` VALUES (321, 54128, 132762, 100684, 3, 6, 18, "|cFFFFD200Évènement mondial :|r Sanssaint", "Les précédents propriétaires de cette caisse ont signalé la mystérieuse disparition de plusieurs autres compagnons de leur collection.");
INSERT INTO `dbc_battle_pet_species` VALUES (323, 54227, 133944, 100970, 4, 5, 82, "|cFFFFD200Haut fait : |rMa petite animalerie|n|cFFFFD200Catégorie : |rCombats de mascottes", "La plupart des écureuils amassent des glands pour se préparer à l\'hiver. Noisette, lui, les amasse pour être sûr d\'avoir assez de munitions.");
INSERT INTO `dbc_battle_pet_species` VALUES (325, 54374, 132926, 101424, 2, 5, 82, "|cFFFFD200Haut fait : |rC’est une super ménagerie|n|cFFFFD200Catégorie : |rCombats de mascottes", "Ce kaliri, une des créatures les plus rares, se différencie des autres par son plumage brillant et ce que les chasseurs sont prêts à faire pour le capturer.");
INSERT INTO `dbc_battle_pet_species` VALUES (328, 54383, 133920, 101493, 8, 8, 2, "|cFFFFD200Jeu de cartes à collectionner :|r Trône des marées", "Vous pourriez être tenté de penser que ce poisson ferait un piètre adversaire hors de l\'eau… Attendez donc de voir l\'intensité qu\'abrite sa bulle protectrice.");
INSERT INTO `dbc_battle_pet_species` VALUES (329, 54438, 528288, 101606, 0, 7, 82, "|cFFFFD200Promotion :|r BlizzCon 2011", "Si vous devez faire face à Diablourbie ou à l\'un des autres Murlocs primordiaux, le port de la pierre d\'âme est fortement conseillé.");
INSERT INTO `dbc_battle_pet_species` VALUES (330, 54491, 132159, 101733, 7, 2, 2, "|cFFFFD200Vendeur : |rLhara|n|cFFFFD200Région : |rÎle de Sombrelune|n|cFFFFD200Coût : |r90|TINTERFACE\ICONS\inv_misc_ticket_darkmoon_01:0|t |n", "Les singes d\'Azeroth sont célèbres pour leurs tours pendables, et cette ruse innée est encore plus développée chez ces adorables compagnons de la foire de Sombrelune.");
INSERT INTO `dbc_battle_pet_species` VALUES (331, 54539, 647628, 101986, 2, 1, 658, "|cFFFFD200Quête :|r Emportés par le vent|n|cFFFFD200Région :|r Hurlevent", "Créés pour amuser les enfants à la foire de Sombrelune, ces ballons magiques ne sont pas du genre à se dégonfler.");
INSERT INTO `dbc_battle_pet_species` VALUES (332, 54541, 647626, 101989, 2, 1, 402, "|cFFFFD200Quête :|r Emportés par le vent|n|cFFFFD200Région :|r Orgrimmar|n", "Ces ballons sont une merveille de magie. Ils ne se dégonflent jamais et apparaissent en général par paquets de quatre-vingt-dix-neuf.");
INSERT INTO `dbc_battle_pet_species` VALUES (333, 54730, 136218, 102317, 0, 8, 2, "|cFFFFD200Jeu de cartes à collectionner :|r Couronne des Cieux", "Les grells sont l\'incarnation de la sauvagerie de la nature. Les grells domestiques sont extrêmement rares.");
INSERT INTO `dbc_battle_pet_species` VALUES (335, 54487, 656341, 103074, 8, 2, 2, "|cFFFFD200Vendeur : |rLhara|n|cFFFFD200Région : |rÎle de Sombrelune|n|cFFFFD200Coût : |r90|TINTERFACE\ICONS\inv_misc_ticket_darkmoon_01:0|t", "Ce reptile caparaçonné protège vaillamment la maison qu\'il transporte sur son dos par son humeur infailliblement enjouée.");
INSERT INTO `dbc_battle_pet_species` VALUES (336, 55187, 647625, 103076, 2, 2, 144, "|cFFFFD200Vendeur : |rLhara|n|cFFFFD200Région : |rÎle de Sombrelune|n|cFFFFD200Coût : |r90|TINTERFACE\ICONS\inv_misc_ticket_darkmoon_01:0|t |n", "Il ne fait que flotter…");
INSERT INTO `dbc_battle_pet_species` VALUES (337, 55215, 134579, 103125, 6, 6, 2, "|cFFFFD200Évènement mondial :|r Voile d’hiver", "Échappé de l\'atelier du Grand-père Hiver, ce minuscule élémentaire s\'est retrouvé enfermé dans un paquet cadeau dérobé par l\'abominable Grinche.");
INSERT INTO `dbc_battle_pet_species` VALUES (338, 55356, 133015, 103544, 9, 2, 2, "|cFFFFD200Vendeur : |rLhara|n|cFFFFD200Région : |rÎle de Sombrelune|n|cFFFFD200Coût : |r90|TINTERFACE\ICONS\inv_misc_ticket_darkmoon_01:0|t", "Ce robuste jouet, capable d\'encaisser les traitements même les plus imaginatifs, fait fureur chez les enfants d\'Azeroth.");
INSERT INTO `dbc_battle_pet_species` VALUES (339, 55367, 134509, 103549, 9, 2, 2, "|cFFFFD200Vendeur : |rLhara|n|cFFFFD200Région : |rÎle de Sombrelune|n|cFFFFD200Coût : |r90|TINTERFACE\ICONS\inv_misc_ticket_darkmoon_01:0|t", "Débarrassée des mines et fusées de l\'ancien modèle, cette version adaptée aux plus jeunes rencontre un grand succès auprès des enfants d\'Azeroth.");
INSERT INTO `dbc_battle_pet_species` VALUES (340, 55386, 433440, 103588, 8, 3, 2, "|cFFFFD200Métier : |rPêche|n|cFFFFD200Région : |rÎle de Sombrelune", "Malgré sa toute petite taille, il est impossible de ne pas voir un jeune hippocampe traverser l’eau.");
INSERT INTO `dbc_battle_pet_species` VALUES (341, 55571, 134971, 104047, 5, 6, 2, "|cFFFFD200Évènement mondial :|r Fête lunaire|n|cFFFFD200Vendeur : |rValadar Chantétoile|n|cFFFFD200Région : |rReflet-de-Lune|n|cFFFFD200Coût : |r50|TINTERFACE\ICONS\INV_Misc_ElvenCoins:0|t", "Ces lanternes magiques se trouvent pendant la Fête lunaire, qui célèbre la défaite de la Légion ardente face aux races libres d\'Azeroth.");
INSERT INTO `dbc_battle_pet_species` VALUES (342, 55574, 134971, 104049, 5, 6, 2, "|cFFFFD200Évènement mondial :|r Fête lunaire|n|cFFFFD200Vendeur : |rValadar Chantétoile|n|cFFFFD200Région : |rReflet-de-Lune|n|cFFFFD200Coût : |r50|TINTERFACE\ICONS\INV_Misc_ElvenCoins:0|t", "Chacune de ces lanternes brille en l\'honneur de l\'âme d\'un esprit ancestral. Durant la Fête lunaire, les fantômes des anciens transmettent leur sagesse aux jeunes générations.");
INSERT INTO `dbc_battle_pet_species` VALUES (343, 56031, 132242, 105122, 7, 2, 2, "|cFFFFD200Vendeur : |rLhara|n|cFFFFD200Région : |rÎle de Sombrelune|n|cFFFFD200Coût : |r90|TINTERFACE\ICONS\inv_misc_ticket_darkmoon_01:0|t |n", "Ce chaton très câlin aime jouer, mais il semble parfois chasser des proies invisibles.");
INSERT INTO `dbc_battle_pet_species` VALUES (344, 56082, 133642, 105228, 2, 2, 160, "|cFFFFD200Vendeur : |rCarl Bonpied|n|cFFFFD200Région : |rÎle de Sombrelune|n|cFFFFD200Coût : |r10|TINTERFACE\MONEYFRAME\UI-SILVERICON.BLP:0|t", "");
INSERT INTO `dbc_battle_pet_species` VALUES (345, 56083, 133639, 105229, 2, 2, 160, "|cFFFFD200Vendeur : |rCarl Bonpied|n|cFFFFD200Région : |rÎle de Sombrelune|n|cFFFFD200Coût : |r10|TINTERFACE\MONEYFRAME\UI-SILVERICON.BLP:0|t", "");
INSERT INTO `dbc_battle_pet_species` VALUES (346, 56266, 135128, 105633, 3, 7, 82, "|cFFFFD200Promotion :|r Édition collector de Diablo III", "Les fétiches chamans tirent leur puissance des esprits de leurs ancêtres disparus. Ils sont malveillants, mais liés à la lance tribale de leur chaman : contrôlez la lance, vous contrôlerez le fétiche.");
INSERT INTO `dbc_battle_pet_species` VALUES (347, 58163, 574806, 110029, 1, 9, 82, "|cFFFFD200Boutique en jeu|r", "On raconte que l\'âme des Aspects est sujette aux humeurs les plus lunatiques, de stoïque et pondéré à erratique et imprévisible, en passant par affectueux et contemplatif.");
INSERT INTO `dbc_battle_pet_species` VALUES (348, 59020, 136155, 112994, 3, 8, 2, "|cFFFFD200Jeu de cartes à collectionner : |rMarcheurs du temps : la guerre des Anciens|n", "Toujours à vous regarder, vous juger.");
INSERT INTO `dbc_battle_pet_species` VALUES (354, 59216, 577318, 0, 5, -1, 32, "", "");
INSERT INTO `dbc_battle_pet_species` VALUES (374, 60649, 136071, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes :|r Forêt d’Elwynn", "Petit du mouton noir, l\'agneau noir ne grandit que dans les forêts d\'Elwynn.");
INSERT INTO `dbc_battle_pet_species` VALUES (375, 62626, 252780, 0, 3, -1, 56, "", "");
INSERT INTO `dbc_battle_pet_species` VALUES (378, 61080, 254857, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rAzshara, Les Tranchantes, Forêt du Chant de cristal, Sombrivage, Dun Morogh, Bois de la Pénombre, Forêt d’Elwynn, Bois des Chants éternels, Féralas, Contreforts de Hautebrande, Fjord Hurlant, Reflet-de-Lune, Mont Hyjal, Mulgore, Nagrand, Les Carmines, Lune-d’Argent, Forêt des Pins-Argentés, Les Serres-Rocheuses, Hurlevent, Clairières de Tirisfal, Maleterres de l’Ouest, Marche de l\'Ouest, Les Paluns", "Si vous possédez deux lapins, vous en posséderez vite trois.");
INSERT INTO `dbc_battle_pet_species` VALUES (379, 61081, 134322, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rVal d\'Ammen, Orneval, Azshara, Les Tranchantes, Forêt du Chant de cristal, Sombrivage, Dun Morogh, Bois de la Pénombre, Marécage d’Âprefange, Forêt d’Elwynn, Féralas, Gilnéas, Contreforts de Hautebrande, Fjord Hurlant, Loch Modan, Terrasse des Magistères, Reflet-de-Lune, Mont Hyjal, Nagrand, Ruines de Gilnéas, Bassin de Sholazar, Forêt des Pins-Argentés, Flèches d’Arak, Hurlevent, Teldrassil, Forêt de Terokkar, Péninsule de Tol Barad, Hautes-terres du Crépuscule, Puits d’éternité, Maleterres de l’Ouest, Marche de l\'Ouest, Les Paluns, Zul’Aman", "La plupart des gens ne sont pas capables de faire la différence entre un écureuil et un tamia. La plupart des gens n\'en ont pas grand-chose à faire.");
INSERT INTO `dbc_battle_pet_species` VALUES (380, 62992, 645526, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rLa forêt de Jade", "Difficile de dire ce qui est le plus dangereux : ses dents, ou sa queue ?");
INSERT INTO `dbc_battle_pet_species` VALUES (381, 61086, 644001, 118414, 4, 0, 2, "|cFFFFD200Butin :|r Sac de fournitures pour mascottes|n", "Malgré son nom, vous ne devez jamais, au grand jamais, le caresser.");
INSERT INTO `dbc_battle_pet_species` VALUES (382, 61087, 646059, 118417, 4, -1, 32, "", "Judicieusement appelés « bandits à lunettes » par les pandarens, les ratons laveurs ont des pattes très habiles et un appétit insatiable pour les objets brillants, en particulier les bijoux.");
INSERT INTO `dbc_battle_pet_species` VALUES (383, 61088, 643423, 0, 8, 4, 26, "|cFFFFD200Combat de mascottes : |rVal de l’Éternel printemps", "On dirait une illusion d’optique, mais le trotteur éternel peut marcher sur l’eau, ses petits pieds n’en traversant jamais la surface.");
INSERT INTO `dbc_battle_pet_species` VALUES (384, 61089, 645906, 118419, 8, -1, 32, "", "Grâce à leur épaisse fourrure, les loutres sont adaptées à n’importe quel climat, mais elles préfèrent les eaux glaciales où les ormeaux sont foisons.");
INSERT INTO `dbc_battle_pet_species` VALUES (385, 61143, 647701, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rBois de la Pénombre, Marécage d’Âprefange, Les Grisonnes, Mulgore, Raz-de-Néant, Marche de l’Ouest, Les Paluns", "Il semblerait que les souris soient complètement incapables de résister à l\'attrait du fromage, même si elles risquent l\'emprisonnement, la douleur ou la mort.");
INSERT INTO `dbc_battle_pet_species` VALUES (386, 61141, 656176, 0, 4, 4, 24, "|cFFFFD200Combat de mascottes : |rHautes-terres Arathies, Mulgore, Nagrand, Tarides du Nord, Tarides du Sud, Pitons-du-Tonnerre, Marche de l’Ouest", "Différents de leurs cousins arboricoles, ces écureuils terrestres peuplent les plaines d\'Azeroth et d\'ailleurs.");
INSERT INTO `dbc_battle_pet_species` VALUES (387, 61142, 132211, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rMarécage d’Âprefange, Bois des Chants éternels, Féralas, Les terres Fantômes, Fjord Hurlant, Loch Modan, Nagrand, Strangleronce septentrionale, Bassin de Sholazar, Forêt des Pins-Argentés, Forêt de Terokkar, Marche de l’Ouest, Marécage de Zangar, Zul’Drak", "Suite à de récents accidents, les cartels gobelins ont strictement interdit l\'introduction des serpents dans le zeppelin.");
INSERT INTO `dbc_battle_pet_species` VALUES (388, 61158, 132186, 0, 8, 4, 26, "|cFFFFD200Combat de mascottes : |rAzshara, Toundra Boréenne, Fjord Hurlant, Étendues sauvages de Krasarang, Nagrand (Draenor), Hautes-terres du Crépuscule, Marche de l’Ouest", "Ces arthropodes fourmillent sur de nombreux rivages d\'Azeroth, et n\'ont qu\'un seul véritable but dans la vie : pincer.");
INSERT INTO `dbc_battle_pet_species` VALUES (389, 61160, 134065, 0, 9, 4, 26, "|cFFFFD200Combat de mascottes : |rMarche de l’Ouest", "Ce minuscule moissonneur collecte les brins d\'herbe et petites graines que les grandes machines auraient pu rater.");
INSERT INTO `dbc_battle_pet_species` VALUES (390, 61750, 132368, 0, 4, -1, 160, "", "Lorsqu\'ils plongent dans ses grands yeux marron, certains chasseurs ont des scrupules à appuyer sur la gâchette. C\'est pour ça qu\'ils préfèrent tirer de loin.");
INSERT INTO `dbc_battle_pet_species` VALUES (391, 61167, 254857, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rLes Carmines", "Ce lapin est capable de gravir les pentes les plus raides à une vitesse qui vous surprendrait.");
INSERT INTO `dbc_battle_pet_species` VALUES (392, 61168, 134324, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rLes Carmines", "Les rats des Carmines sont bien nourris grâce aux razzias qu\'ils opèrent dans les garde-manger des villages.");
INSERT INTO `dbc_battle_pet_species` VALUES (393, 61384, 294474, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rsteppes Ardentes, Maleterres de l’Est, Gorgrond, Couronne de glace, quais de Fer, Les Hinterlands, hautes-terres du Crépuscule, cratère d’Un’Goro, les Paluns", "Ils étaient là bien avant nous. Et ils seront toujours là bien longtemps après notre disparition.");
INSERT INTO `dbc_battle_pet_species` VALUES (394, 61170, 136071, 0, 4, -1, 58, "", "Un mouton se désaltérait dans le courant d\'une onde pure. Un loup survient à jeun qui cherchait aventure. Là-dessus, au fond des forêts, le mouton l\'emporte et puis lui PÈTE LA FIGURE. Parce qu\'il ne faut pas l\'embêter.");
INSERT INTO `dbc_battle_pet_species` VALUES (395, 61171, 612362, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes : |rLes Carmines", "Ne vous promenez jamais dans les Carmines sans un bon casque. Les buses considèrent les cheveux d\'humanoïdes comme un matériau parfait pour construire leurs nids.");
INSERT INTO `dbc_battle_pet_species` VALUES (396, 61253, 132196, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rBois de la Pénombre", "Venues du bois de la Pénombre, ces créatures à huit pattes sont un ingrédient récurrent dans les potions d\'alchimie.");
INSERT INTO `dbc_battle_pet_species` VALUES (397, 61255, 463569, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rVal d’Ammen, Azshara, Île de Brume-Sang, Bois de la Pénombre, Gilnéas, Fjord Hurlant, Nagrand (Draenor), Forêt de Terokkar|n", "Les putois sont réputés être des créatures solitaires, pour des raisons qui devraient être évidentes pour tout le monde.");
INSERT INTO `dbc_battle_pet_species` VALUES (398, 61257, 134324, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rTerres Ingrates, Bois de la Pénombre, Marécage d’Âprefange, Maleterres de l’Est, Mille pointes, Hautes-terres du Crépuscule, Maleterres de l’Ouest, Les Paluns|n", "Beaucoup de gens considèrent que les chats noirs portent malheur. Mais les rats noirs jouissent d\'une meilleure réputation.");
INSERT INTO `dbc_battle_pet_species` VALUES (399, 61258, 132211, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rBois de la Pénombre", "Il y a des années de cela, les humains ont introduit ces reptiles dans le bois de la Pénombre pour limiter la prolifération des rats.");
INSERT INTO `dbc_battle_pet_species` VALUES (400, 61259, 132196, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rBois de la Pénombre|n|cFFFFD200Heure: |rNuit", "Les jeunes veuves ont tout le temps d\'explorer le bois de la Pénombre une fois qu\'elles se sont débarrassées de leurs encombrantes attaches familiales.");
INSERT INTO `dbc_battle_pet_species` VALUES (401, 61312, 132186, 0, 8, 4, 26, "|cFFFFD200Combat de mascottes : |rSombrivage, Désolation des dragons, Dun Morogh, Gilnéas, Kezan, Strangleronce septentrionale, Marais des Chagrins, Cap Strangleronce, Les îles Perdues", "Chaque marée basse révèle des colonies de crabes des rivages, et le claquement de leur danse à la recherche de petites créatures des sables à dévorer.");
INSERT INTO `dbc_battle_pet_species` VALUES (402, 61370, 236193, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes : |rMarais des Chagrins", "Aime les endroits humides et boueux et raffole du nectar de chagrinelle.");
INSERT INTO `dbc_battle_pet_species` VALUES (403, 61313, 136036, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes : |rStrangleronce septentrionale, Marais des Chagrins, Cap Strangleronce, Cratère d’Un’Goro", "Les perroquets peuvent apprendre à répéter des phrases, mais ils ont un peu de mal à soutenir une véritable conversation.");
INSERT INTO `dbc_battle_pet_species` VALUES (404, 61317, 134324, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rDun Morogh, Strangleronce septentrionale, Cap Strangleronce, Cratère d’Un’Goro", "Beaucoup croient qu\'elles n’existent que dans les comptines pour enfants. Mais non, les taupes à longue queue sont bien réelles.");
INSERT INTO `dbc_battle_pet_species` VALUES (405, 61318, 132211, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rStrangleronce septentrionale, Cap Strangleronce, Cratère d’Un’Goro", "Les trolls gurubashi de Strangleronce capturent ces puissants animaux pour les domestiquer… ou parfois les manger.");
INSERT INTO `dbc_battle_pet_species` VALUES (406, 61319, 237144, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rTerres Ingrates, Maleterres de l’Est, Gangrebois, Strangleronce septentrionale, Silithus, Cap Strangleronce, Cratère d’Un’Goro", "Le hanneton est un insecte robuste capable de survivre dans à peu près n\'importe quel climat. Il pullule sur les champs de bataille une fois les combats terminés.");
INSERT INTO `dbc_battle_pet_species` VALUES (407, 61320, 132196, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rStrangleronce septentrionale, vallée d’Ombrelune, cap Strangleronce", "C\'est une espèce dangereuse, qui tisse des toiles sophistiquées partout dans l\'épaisseur de la jungle de Strangleronce.");
INSERT INTO `dbc_battle_pet_species` VALUES (408, 61321, 463491, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rStrangleronce septentrionale, Cap Strangleronce", "Si un lézard femelle ne fait pas attention à choisir un endroit bien camouflé pour pondre, ses œufs ont de fortes chances de finir dévorés par un raptor flagellant.");
INSERT INTO `dbc_battle_pet_species` VALUES (409, 61322, 136036, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes : |rStrangleronce septentrionale", "Les perroquets ne se lassent pas du poisson frais que mangent les trolls scalps-rouges. Des quignons de pain sec, si.");
INSERT INTO `dbc_battle_pet_species` VALUES (410, 61323, 134324, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rquais de Fer, cap Strangleronce, péninsule de Tol Barad", "Tout comme les marins à la moralité douteuse qui peuplent les docks, les rats des quais sont des créatures sauvages et violentes.");
INSERT INTO `dbc_battle_pet_species` VALUES (411, 61324, 651242, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rCap Strangleronce|n|cFFFFD200Temps : |rPluie", "Les bébés singes apprennent vite à se suspendre aux branches avec leurs pieds, ce qui leur permet d\'avoir les deux mains libres pour balancer divers objets au visage des intrus.");
INSERT INTO `dbc_battle_pet_species` VALUES (412, 61327, 132196, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rAhn’kahet : l’Ancien royaume, Azjol-Nérub, Azshara, Temple Noir, Terres Foudroyées, Donjon de Drak’Tharon, Dun Morogh, Marécage d’Âprefange, Maleterres de l’Est, Forêt d’Elwynn, Les terres Fantômes, Gilnéas, Gilnéas (ville), Salles des Reflets, Contreforts de Hautebrande, Fjord Hurlant, Citadelle de la Couronne de glace, Karazhan, Naxxramas, Orgrimmar, Fosse de Saron, Ruines de Gilnéas, Vallée d’Ombrelune (Draenor), Les Serres-Rocheuses, Marais des Chagrins, Teldrassil, L’Épuration de Stratholme, La Forge des Âmes, Les Hinterlands, Les pics Foudroyés, Le fort Pourpre, Clairières de Tirisfal, Donjon d’Utgarde, Cime d’Utgarde, Berceau-de-l’Hiver, Zul’Aman, Zul\'Drak", "Représentantes les plus communes de la famille des arachnides, ces bestioles tissent leurs toiles dans presque tous les coins d\'Azeroth.");
INSERT INTO `dbc_battle_pet_species` VALUES (414, 61326, 132195, 0, 7, 4, 24, "|cFFFFD200Combat de mascottes : |rLes Tranchantes, Terres Foudroyées, Steppes Ardentes, Maleterres de l’Est, Péninsule des Flammes Infernales, Orgrimmar, Vallée d’Ombrelune, Silithus, Mille pointes, Hautes-terres du Crépuscule", "Son venin est capable de foudroyer des créatures qui font jusqu\'à cent fois son poids.");
INSERT INTO `dbc_battle_pet_species` VALUES (415, 61328, 237144, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rTerres Foudroyées, Steppes Ardentes, Mont Hyjal, Gorge des Vents brûlants, Cratère d’Un’Goro", "Attiré par la chaleur et l’odeur de fumée, le hanneton de feu se nourrit de cendres.");
INSERT INTO `dbc_battle_pet_species` VALUES (416, 61329, 132195, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rTerres Foudroyées", "Malgré ses pinces carnassières et sa queue empoisonnée, c’est la morsure du scorpiot qui constitue la plus grande menace.");
INSERT INTO `dbc_battle_pet_species` VALUES (417, 61366, 134324, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rval d’Ammen, bassin Arathi, hautes-terres Arathies, Orneval, Azshara, île de Brume-Sang, forêt du Chant de cristal, Sombrivage, Désolace, donjon de Drak’Tharon, Dun Morogh, crête de Givrefeu, terres Fantômes, Gilnéas, Gilnéas (ville), contreforts de Hautebrande, fjord Hurlant, Karazhan, Loch Modan, Nagrand, Naxxramas, ruines de Gilnéas, monastère Écarlate, Scholomance, Shattrath, forêt des Pins-Argentés, flèches d’Arak, les Serres-Rocheuses, Hurlevent, temple Englouti, marais des Chagrins, forêt de Terokkar, cap Strangleronce, Épuration de Stratholme, Mortemines, Hinterlands, îles Perdues, salles Brisées, le fort Pourpre, île du Temps figé, clairières de Tirisfal", "L\'intrépide archéologue Harrison Jones est célèbre pour sa peur panique des rats.");
INSERT INTO `dbc_battle_pet_species` VALUES (418, 61367, 132211, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rDurotar, Strangleronce septentrionale, Orgrimmar, Marais des Chagrins, Hautes-terres du Crépuscule, Les Paluns", "Arrivés à Orgrimmar sur les navires marchands, ces reptiles ne vivent pas très vieux mais sont considérés comme une nuisance par les habitants de la ville.");
INSERT INTO `dbc_battle_pet_species` VALUES (419, 61071, 237579, 0, 8, 4, 26, "|cFFFFD200Combat de mascottes : |rHautes-terres Arathies, Darnassus, Désolace, Forêt d’Elwynn, Bois des Chants éternels, Les terres Fantômes, Loch Modan, Tarides du Nord, Tarides du Sud, Marais des Chagrins, Teldrassil, Marécage de Zangar", "Cette grenouille n\'est pas très grande.");
INSERT INTO `dbc_battle_pet_species` VALUES (420, 61369, 237579, 0, 8, 4, 26, "|cFFFFD200Combat de mascottes : |rOrneval, Durotar, Marécage d’Âprefange, Bois des Chants éternels, Gangrebois, Les terres Fantômes, Contreforts de Hautebrande, Fjord Hurlant, Nagrand, Orgrimmar, Forêt des Pins-Argentés, Marais des Chagrins, Teldrassil, Hautes-terres du Crépuscule, Les Paluns", "Dans la nuit de l\'été quand il fait beau, on entend résonner le chant du crapaud.");
INSERT INTO `dbc_battle_pet_species` VALUES (421, 61314, 236193, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes : |rStrangleronce septentrionale, Cap Strangleronce", "Aime les endroits humides et tropicaux et raffole du nectar de sang-royal.");
INSERT INTO `dbc_battle_pet_species` VALUES (422, 61372, 132211, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rMarais des Chagrins", "Lorsqu\'il chasse ses proies ou fuit ses prédateurs, ce serpent peut glisser à la surface de l\'eau sur de courtes distances.");
INSERT INTO `dbc_battle_pet_species` VALUES (423, 61383, 132186, 0, 6, 4, 26, "|cFFFFD200Combat de mascottes : |rSteppes Ardentes, Gorge des Vents brûlants", "Cet arthropode s’adapte facilement et son exosquelette épais lui permet de survivre dans les environnements hostiles.");
INSERT INTO `dbc_battle_pet_species` VALUES (424, 61169, 294474, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rOrneval, Azshara, Désolace, Dun Morogh, bois de la Pénombre, Gilnéas, Gilnéas (ville), fjord Hurlant, citadelle de la Couronne de glace, Kezan, Loch Modan, Strangleronce septentrionale, souilles de Tranchebauge, les Carmines, les Serres-Rocheuses, cap Strangleronce, Épuration de Stratholme, Mille pointes, clairières de Tirisfal|n", "Les cafards sont considérés comme des nuisibles par la plupart des gens, mais leur incroyable capacité de survie en fait une espèce à part.");
INSERT INTO `dbc_battle_pet_species` VALUES (425, 61385, 132211, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rSteppes Ardentes, Vallée d’Ombrelune", "Ce serpent peu accommodant s\'installe généralement autour des bassins de lave des steppes Ardentes.");
INSERT INTO `dbc_battle_pet_species` VALUES (427, 61420, 132196, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rGorge des Vents brûlants, Talador", "La solide carapace de ces araignées leur permet de survivre sous la chaleur écrasante de la gorge des Vents brûlants.");
INSERT INTO `dbc_battle_pet_species` VALUES (428, 61425, 132196, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rGorge des Vents brûlants", "Ces araignées qui dégagent une chaleur bouillante se camouflent en se plongeant dans les coulées de magma.");
INSERT INTO `dbc_battle_pet_species` VALUES (429, 61386, 237144, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rSteppes Ardentes", "Les hannetons des steppes Ardentes roulent le magma en boules minuscules et le consomment une fois qu\'il a refroidi.");
INSERT INTO `dbc_battle_pet_species` VALUES (430, 61438, 237144, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rterres Ingrates, Gorgrond, Tanaris", "Ces minuscules machines autonomes furent créées par les titans pour entretenir et nettoyer leurs imposants édifices.");
INSERT INTO `dbc_battle_pet_species` VALUES (431, 61439, 132211, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rTerres Ingrates, Tanaris, Hautes-terres du Crépuscule", "Pour entraîner leurs jeunes griffons, les nains Marteaux-hardis les envoient chasser les serpents à sonnettes des hautes-terres du Crépuscule.");
INSERT INTO `dbc_battle_pet_species` VALUES (432, 61440, 132195, 0, 7, 4, 24, "|cFFFFD200Combat de mascottes : |rTerres Ingrates, Tanaris, Forêt de Terokkar", "Moins venimeux mais plus agressif que les autres races de scorpions.");
INSERT INTO `dbc_battle_pet_species` VALUES (433, 61441, 463491, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rTerres Ingrates, Silithus", "La robe du lézard à pointes lui permet de se fondre parfaitement dans les sables du désert de Silithus.");
INSERT INTO `dbc_battle_pet_species` VALUES (434, 61456, 132248, 0, 7, 4, 160, "|cFFFFD200Combat de mascottes : |rHautes-terres Arathies, Contreforts de Hautebrande, Loch Modan, Ruines de Gilnéas, Les Paluns", "Quand un bélier baisse la tête, c\'est qu\'il se prépare à charger. Ou qu\'il a un coup de fatigue.");
INSERT INTO `dbc_battle_pet_species` VALUES (437, 61459, 132248, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rLoch Modan", "Ces béliers s\'entraînent au combat en chargeant sur les brindilles et arbrisseaux près du Loch Modan.");
INSERT INTO `dbc_battle_pet_species` VALUES (438, 61443, 136040, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rTerres Ingrates", "On aperçoit souvent ce serpent en train de parfaire son bronzage sur les rochers au soleil des terres Ingrates.");
INSERT INTO `dbc_battle_pet_species` VALUES (439, 61375, 132123, 0, 3, 4, 26, "|cFFFFD200Combat de mascottes : |rDéfilé de Deuillevent|n|cFFFFD200Heure : |rTôt le matin", "Ces créatures sont la plupart du temps silencieuses, si ce n\'est leurs occasionnels soupirs de désespoir surréel.");
INSERT INTO `dbc_battle_pet_species` VALUES (440, 61689, 132252, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rDun Morogh", "L\'épaisse neige de Dun Morogh permet aux jeunes félins des neiges d\'apprendre à se camoufler pour chasser.");
INSERT INTO `dbc_battle_pet_species` VALUES (441, 61690, 254857, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rDun Morogh, Berceau-de-l’Hiver", "Le lièvre alpin peut s\'enterrer jusque dans les glaces éternelles et y hiberner plusieurs semaines, voire plusieurs mois.");
INSERT INTO `dbc_battle_pet_species` VALUES (442, 61691, 294474, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rDun Morogh", "La plupart des créatures les plus impressionnantes ne survivraient pas à des conditions à moitié aussi difficiles que celles dans lesquelles les cafards s\'épandent joyeusement.");
INSERT INTO `dbc_battle_pet_species` VALUES (443, 61704, 254857, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rHautes-terres Arathies", "Malgré un environnement idyllique, les lapins des pâturages sautent à une vitesse surprenante pour fuir leurs poursuivants.");
INSERT INTO `dbc_battle_pet_species` VALUES (444, 61708, 577318, 0, 4, -1, 56, "", "");
INSERT INTO `dbc_battle_pet_species` VALUES (445, 61703, 236154, 0, 6, 4, 26, "|cFFFFD200Combat de mascottes : |rHautes-terres Arathies", "Les tornades adorent approcher furtivement des autres créatures et leur envoyer une bouffée de vent au visage sans prévenir.");
INSERT INTO `dbc_battle_pet_species` VALUES (446, 61718, 134437, 0, 5, 4, 26, "|cFFFFD200Combat de mascottes : |rLes Hinterlands", "Les suinteux de jade brillent dans le noir, ce qui leur vaut toute l\'affection des mineurs qui travaillent dans les galeries obscures.");
INSERT INTO `dbc_battle_pet_species` VALUES (447, 61165, 656350, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rForêt d’Elwynn, Teldrassil", "Attention aux traversées de daims. Même un petit faon peut vous ruiner la carrosserie d\'un triklomoteur gobelin flambant neuf.");
INSERT INTO `dbc_battle_pet_species` VALUES (448, 61751, 254857, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rHautes-terres Arathies, Durotar, Les Hinterlands", "Il suffit d\'une carotte, d\'une caisse, d\'un bâton et d\'un bout de ficelle. Patientez quelques heures, et vous aurez un lièvre.");
INSERT INTO `dbc_battle_pet_species` VALUES (449, 61752, 656176, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rles Tranchantes, Gorgrond, les Hinterlands", "Différentes de leurs cousins d\'Azeroth, ces créatures d\'Outreterre peuplent les hauteurs des Tranchantes.");
INSERT INTO `dbc_battle_pet_species` VALUES (450, 61753, 651538, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rOrneval, Les terres Fantômes, Contreforts de Hautebrande, Fjord Hurlant, Les Hinterlands, Clairières de Tirisfal", "L\'habitat d\'élection de cette créature est les régions boisées, où elle peut achever sa métamorphose en toute sécurité.");
INSERT INTO `dbc_battle_pet_species` VALUES (452, 61757, 134322, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rDarnassus, Désolace, contreforts de Hautebrande, Talador, Teldrassil", "D\'après les légendes des elfes de la nuit, le tamia à queue rouge fut ainsi coloré après avoir volé et mangé une pomme sacrée.");
INSERT INTO `dbc_battle_pet_species` VALUES (453, 61758, 648803, 0, 3, 4, 26, "|cFFFFD200Combat de mascottes : |rContreforts de Hautebrande", "Les oursons infestés ont gardé l\'habitude de guetter le bourdonnement caractéristique des abeilles, même s\'ils ne peuvent plus goûter leur miel.");
INSERT INTO `dbc_battle_pet_species` VALUES (454, 61889, 134324, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rClairières de Tirisfal", "Même les rats de Lordaeron ont succombé à la peste des apothicaires, à force de dévorer la chair des victimes.");
INSERT INTO `dbc_battle_pet_species` VALUES (455, 61890, 646782, 0, 3, 4, 26, "|cFFFFD200Combat de mascottes : |rForêt des Pins-Argentés", "Les écureuils morts-vivants ont développé une prédilection pour les étranges baies et champignons qui poussent aujourd\'hui dans la forêt des Pins-Argentés.");
INSERT INTO `dbc_battle_pet_species` VALUES (456, 61826, 648726, 0, 3, 4, 26, "|cFFFFD200Combat de mascottes : |rMaleterres de l’Ouest", "Étrangement, ce faucon continue à utiliser ses serres acérées pour écorcer les arbres à la recherche d\'insectes, même s\'il n\'a plus besoin de se nourrir.");
INSERT INTO `dbc_battle_pet_species` VALUES (457, 61830, 236197, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rMaleterres de l’Est", "Peut avaler plus de trois fois son poids en sang.");
INSERT INTO `dbc_battle_pet_species` VALUES (458, 61905, 236548, 0, 3, 4, 26, "|cFFFFD200Combat de mascottes : |rClairières de Tirisfal", "Ces esprits de petite taille goûtent peu la compagnie de leurs voisins morts-vivants, et préfèrent errer seuls dans les clairières de Tirisfal.");
INSERT INTO `dbc_battle_pet_species` VALUES (459, 62019, 132167, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rHautes-terres Arathies, Forêt d’Elwynn, Bois des Chants éternels, Raz-de-Néant, Lune-d’Argent", "Après des siècles de domestication, ces bêtes à fourrure se trouvent dans presque toutes les nations humaines des royaumes de l\'Est.");
INSERT INTO `dbc_battle_pet_species` VALUES (460, 62020, 132129, 0, 6, 4, 26, "|cFFFFD200Combat de mascottes : |rBois des Chants éternels", "En grandissant, les petits arbrisseaux élémentaires deviennent des tréants, farouches défenseurs des forêts et des bois.");
INSERT INTO `dbc_battle_pet_species` VALUES (461, 62022, 651538, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rLes terres Fantômes", "Ce nouvel arrivant en Azeroth est né tout récemment. Son but est d\'assurer sa survie en attendant sa prochaine phase d\'évolution.");
INSERT INTO `dbc_battle_pet_species` VALUES (462, 62035, 464160, 89472, 2, 2, 32, "|cFFFFD200Vendeur : |rIntendant Brazie|n|cFFFFD200Région : |rPéninsule de Tol Barad|n|cFFFFD200Faction : |rLes Gardiens de Baradin - Honoré", "Ce compagnon vient du joli pays des tests gentils où il gambade tout nu en grignotant des noix de développeurs.");
INSERT INTO `dbc_battle_pet_species` VALUES (463, 62034, 133275, 0, 3, 4, 26, "|cFFFFD200Combat de mascottes : |rLes terres Fantômes", "Les âmes perdues des crabes disparus ont soif de vengeance contre les murlocs qui les ont dévorés.");
INSERT INTO `dbc_battle_pet_species` VALUES (464, 62050, 236193, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes : |rÎle de Brume-Azur", "Pour trouver ces phalènes, il suffit de se promener vêtu d\'habits chers près du val d\'Ammen. Ils raffolent du goût de la haute couture.");
INSERT INTO `dbc_battle_pet_species` VALUES (465, 62051, 132194, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rÎle de Brume-Sang", "Ces voraces carnivores sont arrivés en Azeroth à bord de la forteresse interdimensionnelle des draeneï, l\'Exodar.");
INSERT INTO `dbc_battle_pet_species` VALUES (466, 62114, 463491, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rDurotar, Orgrimmar", "Les enfants d’Orgrimmar ont l’habitude de capturer ces lézards pour les apprivoiser. Les adultes d’Orgrimmar ont l’habitude de leur marcher dessus.");
INSERT INTO `dbc_battle_pet_species` VALUES (467, 62115, 133570, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rDurotar, Orgrimmar, Uldum", "Un fourrageur du désert attiré par les zones à haute teneur en… bouse.");
INSERT INTO `dbc_battle_pet_species` VALUES (468, 62116, 133570, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rDurotar", "Ces hannetons s\'installent souvent dans les carrières abandonnées par les forces militaires de la Horde en Durotar.");
INSERT INTO `dbc_battle_pet_species` VALUES (469, 62118, 133570, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rAzshara, Le Tréfonds, Mont Hyjal", "Une espèce énigmatique. On dit qu\'ils se nourrissent des énergies chaotiques des Dieux très anciens.");
INSERT INTO `dbc_battle_pet_species` VALUES (470, 62117, 132196, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rAzshara, Le Tréfonds, Hautes-terres du Crépuscule", "Corrompues par la magie ténébreuse du Marteau du crépuscule, ces sinistres créatures se nourrissent parfois de leurs propres progénitures.");
INSERT INTO `dbc_battle_pet_species` VALUES (471, 62119, 656513, 0, 9, 4, 26, "|cFFFFD200Combat de mascottes : |rAzshara, Orgrimmar, Berceau-de-l’Hiver", "Construit à l\'origine par un gobelin rancunier pour embêter ses voisins en les réveillant très tôt le matin.");
INSERT INTO `dbc_battle_pet_species` VALUES (472, 62120, 134322, 0, 9, 4, 26, "|cFFFFD200Combat de mascottes : |rAzshara, Les Serres-Rocheuses, Berceau-de-l’Hiver", "À sa sortie, il fut considéré comme une notable amélioration par rapport à la série 4000. Certains s\'inquiètent tout de même de la quantité de chaleur qu\'il dégage.");
INSERT INTO `dbc_battle_pet_species` VALUES (473, 62121, 133936, 0, 8, 4, 26, "|cFFFFD200Combat de mascottes : |rAzshara", "Les tortues inspirent la peur aux nagas, qui considèrent qu\'elles portent malheur et font un détour à chaque fois qu\'ils en aperçoivent une sur les rives d\'Azshara.");
INSERT INTO `dbc_battle_pet_species` VALUES (474, 62129, 132242, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rTarides du Nord", "Ces petits félins joueurs s\'entraînent à la chasse en se traquant les uns les autres partout dans les Tarides.");
INSERT INTO `dbc_battle_pet_species` VALUES (475, 62130, 132368, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rTarides du Sud", "Les barbares centaures ont abusé de la chasse aux girafes pendant des décennies, et l\'espèce ne fut sauvée de l\'extinction que grâce à l\'intervention des taurens.");
INSERT INTO `dbc_battle_pet_species` VALUES (476, 62163, 132368, 0, 4, 4, 160, "|cFFFFD200Combat de mascottes : |rDésolace, Mulgore, Tarides du Nord, Tarides du Sud", "La plupart des gens ne voient jamais que l\'arrière d\'une gazelle.");
INSERT INTO `dbc_battle_pet_species` VALUES (477, 62176, 132368, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rMulgore", "Les jeunes gazelles apprennent vite à prendre la fuite en entendant les cris de guerre des chasseurs taurens en Mulgore. Malheureusement, les bons chasseurs savent approcher silencieusement, eux.");
INSERT INTO `dbc_battle_pet_species` VALUES (478, 62177, 236193, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes : |rOrneval, Darnassus, Désolace, Reflet-de-Lune, Mont Hyjal, Teldrassil", "Aime les endroits frais et boisés et raffole du nectar de mage royal.");
INSERT INTO `dbc_battle_pet_species` VALUES (479, 62178, 254857, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rDarnassus, Désolace, Mont Hyjal, Teldrassil", "Vivre exposés à la magie elfique a développé la longévité de certains de manière surnaturelle. Et, plus rarement, les effets ont pu se révéler... vraiment étranges.");
INSERT INTO `dbc_battle_pet_species` VALUES (480, 62181, 134395, 0, 6, 4, 26, "|cFFFFD200Combat de mascottes : |rLe Tréfonds, Désolace|n", "Très recherchée par les tailleurs de gemmes, une carapace de jeune araignée topaze peut servir à créer des ornements éblouissants pour les anneaux et bracelets.");
INSERT INTO `dbc_battle_pet_species` VALUES (482, 62184, 132211, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rLes Tranchantes, Désolace, Mont Hyjal, Silithus", "Il y a longtemps, les druides du mont Hyjal ont adapté cette espèce afin qu\'elle puisse cohabiter avec les autres créatures de la région.");
INSERT INTO `dbc_battle_pet_species` VALUES (483, 62185, 237579, 0, 8, 4, 26, "|cFFFFD200Combat de mascottes : |rDésolace", "Beaucoup de scientifiques se demandent s\'il s\'agit ou non d\'un crapaud.");
INSERT INTO `dbc_battle_pet_species` VALUES (484, 62186, 132196, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rDésolace, Silithus, Tanaris, Uldum", "Ces créatures rusées creusent des galeries sous Uldum et jaillissent du sol pour emprisonner leurs proies.");
INSERT INTO `dbc_battle_pet_species` VALUES (485, 62187, 464159, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rDésolace|n|cFFFFD200Heure : |rNuit|n", "Quand ces créatures dormeuses ne sont pas en train de faire la sieste, elles utilisent leurs énormes griffes pour se dénicher de juteux insectes.");
INSERT INTO `dbc_battle_pet_species` VALUES (486, 62188, 132368, 0, 4, 4, 160, "|cFFFFD200Combat de mascottes : |rLes Serres-Rocheuses", "Ainsi appelée à cause de ses grandes oreilles. Sert parfois à acheminer du chardon sanglant de contrebande par-delà les frontières de Quel\'Thalas.");
INSERT INTO `dbc_battle_pet_species` VALUES (487, 62189, 134322, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rMont Hyjal, Les Serres-Rocheuses, Berceau-de-l’Hiver", "Nombreux sont les explorateurs qui, sous le charme de l\'apparence adorable du tamia alpin, n\'ont pas remarqué la pression de ses petites dents sur leurs doigts gelés.");
INSERT INTO `dbc_battle_pet_species` VALUES (488, 62190, 132211, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rLes Serres-Rocheuses", "Les assassins les plus intrépides capturent parfois ces créatures afin d\'utiliser leur puissant venin pour empoisonner leurs armes.");
INSERT INTO `dbc_battle_pet_species` VALUES (489, 62201, 134154, 0, 1, 4, 26, "|cFFFFD200Combat de mascottes : |rMarécage d’Âprefange", "Ces féroces dragonnets cracheurs de feu se sont échappés de la tanière d\'Onyxia et se sont éparpillés dans les environs. À ne manipuler qu\'avec la plus grande prudence.");
INSERT INTO `dbc_battle_pet_species` VALUES (491, 62257, 132167, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rTanaris", "Élevés à l\'origine par les trolls furie-des-sables pour servir de compagnons, ces farouches félins chassent dans le désert de Tanaris.");
INSERT INTO `dbc_battle_pet_species` VALUES (492, 62256, 237144, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rTanaris", "Un insecte du désert qui défend son nid en projetant une vile odeur rappelant celle des ogres.");
INSERT INTO `dbc_battle_pet_species` VALUES (493, 62246, 442735, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rSombrivage", "Cherche sa nourriture juste sous la surface de l’eau sur la côte de Sombrivage.");
INSERT INTO `dbc_battle_pet_species` VALUES (494, 62258, 236195, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rTanaris|n|cFFFFD200Temps : |rTempête de sable", "Pour passer le temps, ces petits s\'amusent à faire claquer leurs mandibules sur des rythmes toujours plus compliqués.");
INSERT INTO `dbc_battle_pet_species` VALUES (495, 62312, 237579, 0, 8, 4, 26, "|cFFFFD200Combat de mascottes : |rOrneval", "Pour chaque grenouille en liberté, quelque part, un prince et une princesse se morfondent.");
INSERT INTO `dbc_battle_pet_species` VALUES (496, 62313, 442735, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rOrneval", "Ne laissez pas leur absence de gueule vous induire en erreur : ces créatures visqueuses en veulent au monde entier et ne cherchent que la bagarre.");
INSERT INTO `dbc_battle_pet_species` VALUES (497, 62314, 294474, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rGangrebois, Vallée d’Ombrelune", "Même les cafards ne furent pas assez coriaces pour résister à l\'abjecte influence de la corruption démoniaque.");
INSERT INTO `dbc_battle_pet_species` VALUES (498, 62315, 236193, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes : |rGangrebois", "Aime les bois corrompus et raffole du nectar de lotus pourpre.");
INSERT INTO `dbc_battle_pet_species` VALUES (499, 62316, 134324, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rGangrebois", "Il y a des choses si sales que même les rats n\'en veulent pas.");
INSERT INTO `dbc_battle_pet_species` VALUES (500, 62317, 136219, 0, 5, 4, 26, "|cFFFFD200Combat de mascottes : |rGangrebois", "Ces créatures de feu et de rage ont mauvais caractère. Elles naissent parfois des cendres de plus grands infernaux.");
INSERT INTO `dbc_battle_pet_species` VALUES (502, 62370, 237579, 0, 8, 4, 26, "|cFFFFD200Combat de mascottes : |rCratère d’Un’Goro", "L\'exploratrice naine qui découvrit la première rainette tachetée comptait la nommer d\'après son coassement si particulier. Mais en entendant le bruit d\'un couple en plein accouplement, elle opta pour une caractéristique visuelle.");
INSERT INTO `dbc_battle_pet_species` VALUES (503, 62373, 236193, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes : |rReflet-de-Lune, Mont Hyjal, Cratère d’Un’Goro", "Aime vivre à l\'ombre de Nordrassil et raffole du nectar de vignétincelle.");
INSERT INTO `dbc_battle_pet_species` VALUES (504, 62375, 134318, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rCratère d’Un’Goro", "Les dimeurtrodons abandonnent leurs bébés à leur sort dans le cratère d\'Un\'Goro. Seuls les forts survivent.");
INSERT INTO `dbc_battle_pet_species` VALUES (505, 62255, 463491, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rMille pointes", "Alors que la plupart des reptiles aiment se prélasser au soleil, ces créatures écailleuses préfèrent paresser au clair des lunes jumelles d\'Azeroth.");
INSERT INTO `dbc_battle_pet_species` VALUES (506, 62191, 132196, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rLes Serres-Rocheuses", "Ces octopodes se terrent parmi les ombres des falaises des Serres-Rocheuses depuis des millénaires.");
INSERT INTO `dbc_battle_pet_species` VALUES (507, 62242, 132192, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes : |rTeldrassil", "Beaucoup de chouettes font leur nid dans les hauteurs des bâtiments de pierre de Darnassus, et chassent les rongeurs qui s\'aventurent dans la cité elfique.");
INSERT INTO `dbc_battle_pet_species` VALUES (508, 62250, 446127, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rSombrivage", "La fourrure des ours de Sombrivage est due à l’obscurité quasi perpétuelle dans laquelle ils vivent, et ils possèdent une vision nocturne surnaturelle.");
INSERT INTO `dbc_battle_pet_species` VALUES (509, 61686, 135049, 0, 6, 4, 26, "|cFFFFD200Combat de mascottes : |rLes Paluns", "Ces petits élémentaires s\'accrochent dans le dos des plus grandes créatures des marécages pour y dénicher de juteux insectes et de savoureuses tiques.");
INSERT INTO `dbc_battle_pet_species` VALUES (510, 62434, 466036, 0, 9, 4, 34, "|cFFFFD200Combat de mascottes : |rKezan, Berceau-de-l’Hiver", "Nombreux sont les jeunes ingénieurs gnomes qui, pour compenser la perte d\'un animal de compagnie, en ont créé une réplique mécanique.");
INSERT INTO `dbc_battle_pet_species` VALUES (511, 62523, 132211, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rSilithus, Uldum", "Venus des régions désertiques d\'Azeroth, ces reptiles coriaces peuvent survivre des semaines sans la moindre goutte d\'eau.");
INSERT INTO `dbc_battle_pet_species` VALUES (512, 62524, 133570, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rSilithus", "Rejetons des dangereux scarabées hurleurs d’Ahn’Qiraj, ces petits sont bien plus dociles (et bien moins bruyants) que leurs parents.");
INSERT INTO `dbc_battle_pet_species` VALUES (513, 62526, 236829, 0, 0, 4, 26, "|cFFFFD200Combat de mascottes : |rSilithus|n|cFFFFD200Saison : |rÉté", "Les crissements de ces minuscules insectoïdes peuvent vous sembler très mignons. Sachez cependant que c\'est un cri de mort dirigé contre vous.");
INSERT INTO `dbc_battle_pet_species` VALUES (514, 62555, 366938, 0, 0, 4, 26, "|cFFFFD200Combat de mascottes : |rPéninsule des Flammes Infernales", "Un gangr\'orc un peu prétentieux a un jour voulu domestiquer ces créatures pour en faire ses soldats personnels. On trouve encore des morceaux de lui éparpillés un peu partout en Outreterre.");
INSERT INTO `dbc_battle_pet_species` VALUES (515, 62564, 134532, 0, 0, 4, 26, "|cFFFFD200Combat de mascottes : |rMarécage de Zangar", "Ces petites créatures dodues se créent souvent des ennuis en partant à la recherche de leur nourriture préférée : le chapeluisant.");
INSERT INTO `dbc_battle_pet_species` VALUES (517, 62583, 132201, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rForêt de Terokkar", "À leur naissance, ces reptiles malfaisants dévorent les membres les plus faibles de leur couvée pour établir leur domination.");
INSERT INTO `dbc_battle_pet_species` VALUES (518, 62620, 134060, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rNagrand", "Chez les ogres, comparer les cheveux d\'une femelle à la crinière miteuse d\'un sabot-fourchu est une belle marque d\'affection.");
INSERT INTO `dbc_battle_pet_species` VALUES (519, 62621, 135794, 0, 6, 4, 26, "|cFFFFD200Combat de mascottes :|r vallée d’Ombrelune", "Créés par une magie démoniaque, ces petits êtres pétillants corrompent tout ce qu\'ils touchent de leur abjecte énergie.");
INSERT INTO `dbc_battle_pet_species` VALUES (521, 62627, 132191, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes : |rRaz-de-Néant", "Les raies du Néant adultes sont relativement paisibles, mais leurs petits sont des créatures ombrageuses et débordantes d\'énergie.");
INSERT INTO `dbc_battle_pet_species` VALUES (523, 62640, 134895, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rFjord Hurlant", "Le fjord Hurlant fourmille d’une vie sans cesse en mutation. Quand une créature meurt, son corps est rapidement mangé par les vers, qui à leur tour fournissent ces nutriments à un nombre d’autres créatures.");
INSERT INTO `dbc_battle_pet_species` VALUES (525, 62648, 250626, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes : |rFjord Hurlant", "Une des principales sources de nourriture pendant la fête du Voile d\'hiver.");
INSERT INTO `dbc_battle_pet_species` VALUES (528, 62628, 463491, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rLes Tranchantes", "Cette grincheuse race de basilic peuple les hauteurs des Tranchantes, et évite autant que possible le contact avec les autres animaux.");
INSERT INTO `dbc_battle_pet_species` VALUES (529, 62669, 132203, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rFjord Hurlant", "Comme le dit un vieux proverbe vrykul : « On ne dérange pas un worg qui dort, surtout si on tient à son visage. »");
INSERT INTO `dbc_battle_pet_species` VALUES (530, 62697, 134438, 0, 5, 4, 26, "|cFFFFD200Combat de mascottes : |rToundra Boréenne", "Ces amas de limon vivants sont dénués de même la plus rudimentaire intelligence. Mais ça ne veut pas dire qu\'ils ne veulent pas être vos amis.");
INSERT INTO `dbc_battle_pet_species` VALUES (532, 62816, 236194, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rBassin de Sholazar", "Les farouches varleus chassent ces brutes ombrageuses pour leurs cornes, dont ils font des talismans et autres bijoux.");
INSERT INTO `dbc_battle_pet_species` VALUES (534, 62819, 132172, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes : |rLes Grisonnes", "Les taunkas collectionnent les plumes de ces nobles oiseaux de proie pour les utiliser dans leurs primitives cérémonies de divination.");
INSERT INTO `dbc_battle_pet_species` VALUES (535, 62820, 135862, 0, 6, 4, 26, "|cFFFFD200Combat de mascottes : |rZul’Drak", "Les trolls de Zul\'Drak ont créé ces petites créatures par mégarde en cherchant à invoquer des élémentaires d\'eau pour combattre le Fléau.");
INSERT INTO `dbc_battle_pet_species` VALUES (536, 62835, 655866, 0, 8, 4, 26, "|cFFFFD200Combat de mascottes : |rToundra Boréenne, Désolation des dragons", "Les pingouins de la toundra ont pour habitude de se rassembler en grands groupes pour se protéger des menaces aquatiques. Malheureusement, cela fait d\'eux des proies faciles pour les chasseurs humanoïdes en quête de viande, peaux ou divers hauts faits.");
INSERT INTO `dbc_battle_pet_species` VALUES (537, 62852, 132200, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes : |rDésolation des dragons", "Ces petites créatures furtives naissent dans des nids construits autour des vieux ossements de dragons du Norfendre central.");
INSERT INTO `dbc_battle_pet_species` VALUES (538, 62854, 134154, 0, 3, 4, 26, "|cFFFFD200Combat de mascottes : |rLa Couronne de glace", "Kel\'Thuzad avait son propre dragonnet pestiféré, qu\'il éleva jusqu\'à l\'âge adulte en le nourrissant des plus beaux morceaux de cadavres prémâchés.");
INSERT INTO `dbc_battle_pet_species` VALUES (539, 62884, 134324, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rMont Hyjal", "Il n\'est pas rare que quelqu\'un confonde le campagnol des grottes avec une jolie petite souris. Mais il n\'a jamais l\'occasion de se tromper une deuxième fois.");
INSERT INTO `dbc_battle_pet_species` VALUES (540, 62885, 134324, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rMont Hyjal", "Là où il y a des charognes, il y aura toujours des rats.");
INSERT INTO `dbc_battle_pet_species` VALUES (541, 62886, 294474, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rCrête de Givrefeu, Mont Hyjal", "La couche extérieure des œufs de cafard protège les larves de la combustion. C\'est durant l\'incubation qu’ils développent leur résistance au feu et à la chaleur des climats volcaniques.");
INSERT INTO `dbc_battle_pet_species` VALUES (542, 62892, 237579, 0, 8, 4, 26, "|cFFFFD200Combat de mascottes : |rUldum", "Affublée de couleurs vives et plus exubérante que la plupart des autres grenouilles.");
INSERT INTO `dbc_battle_pet_species` VALUES (543, 62893, 294474, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rUldum", "Quand une nuée de locustes se déplace, on n\'entend plus que le son de leurs ailes.");
INSERT INTO `dbc_battle_pet_species` VALUES (544, 62895, 236193, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes : |rUldum", "Aime les ruisseaux, puits ou points d\'eau du désert et raffole du nectar de fouettine.");
INSERT INTO `dbc_battle_pet_species` VALUES (545, 62896, 132195, 0, 7, 4, 24, "|cFFFFD200Combat de mascottes : |rUldum", "Son activité préférée : se coucher pour la nuit dans les chaussures des voyageurs distraits.");
INSERT INTO `dbc_battle_pet_species` VALUES (546, 62899, 134936, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rUldum", "Ces nécrophages pullulent souvent dans les tombeaux antiques. Ils peuvent survivre sans nourriture pendant de longues périodes.");
INSERT INTO `dbc_battle_pet_species` VALUES (547, 62888, 136116, 0, 5, 4, 26, "|cFFFFD200Combat de mascottes : |rMont Hyjal", "Pendant la Troisième guerre, ce sont des milliers de ces êtres mystiques qui anéantirent le seigneur démon Archimonde.");
INSERT INTO `dbc_battle_pet_species` VALUES (548, 62900, 327735, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes : |rHautes-terres du Crépuscule", "Trop peureux pour essayer de voler avant qu\'on le jette d\'une grande hauteur.");
INSERT INTO `dbc_battle_pet_species` VALUES (549, 62904, 656176, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rHautes-terres du Crépuscule", "Différentes de leurs cousins des plaines, ces créatures peuplent les hautes-terres du Crépuscule déchirées par la guerre.");
INSERT INTO `dbc_battle_pet_species` VALUES (550, 62905, 647701, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rHautes-terres du Crépuscule", "Les souris des hautes-terres, peut-être à cause de la magie du Marteau du crépuscule, sont agressives et vont jusqu\'à se battre pour dominer celles de leur propre espèce.");
INSERT INTO `dbc_battle_pet_species` VALUES (552, 62914, 136199, 0, 5, 4, 26, "|cFFFFD200Combat de mascottes : |rHautes-terres du Crépuscule", "Ce petit démon use de ses dents acérées et de sa mâchoire démesurée pour dévorer tout ce qui croise son chemin, ce qui montre bien qu\'une mauvaise action est toujours récompensée.");
INSERT INTO `dbc_battle_pet_species` VALUES (553, 62921, 134324, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rLe Tréfonds", "Lors d\'une longue traversée, lorsque la nourriture vient à manquer, la vue d\'un rat est toujours une bonne nouvelle.");
INSERT INTO `dbc_battle_pet_species` VALUES (554, 62922, 134396, 0, 6, 4, 26, "|cFFFFD200Combat de mascottes : |rLe Tréfonds", "Grâce à diverses expériences, les mages de Dalaran ont découvert que les restes d\'une araignée de schiste cramoisie réduits en poudre augmentent fortement le potentiel de déflagration de leurs sorts de feu.");
INSERT INTO `dbc_battle_pet_species` VALUES (555, 62924, 294474, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rLe Tréfonds", "Les cafards du Tréfonds peuvent creuser même dans la pierre pour construire leurs nids.");
INSERT INTO `dbc_battle_pet_species` VALUES (556, 62925, 237144, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rLe Tréfonds", "Cousin du hanneton du Crépuscule, cet insecte a appris à se nourrir de cristaux qui vibrent d\'une énergie antique.");
INSERT INTO `dbc_battle_pet_species` VALUES (557, 62395, 236997, 0, 1, 4, 26, "|cFFFFD200Combat de mascottes : |rFéralas", "Tout comme les autres dragons féeriques, ces petites créatures volantes mystiques et joueuses naissent avec une résistance innée à la magie.");
INSERT INTO `dbc_battle_pet_species` VALUES (558, 62864, 797544, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rLes pics Foudroyés|n|cFFFFD200Temps : |rNeige", "En haut des pics Foudroyés, pendant les nuits froides, ces créatures se couvrent du pelage épais de leur queue pour conserver leur chaleur.");
INSERT INTO `dbc_battle_pet_species` VALUES (559, 62927, 134396, 0, 6, 4, 26, "|cFFFFD200Combat de mascottes : |rLe Tréfonds", "Therazane utilisait ces hardies petites créatures comme bijoux avant que la mode de l’azur ne s\'empare du Tréfonds.");
INSERT INTO `dbc_battle_pet_species` VALUES (560, 62953, 464160, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes : |rMur-de-Givre, étendues sauvages de Krasarang, Talador, Tanaris", "La mouette peuple les côtes et se nourrit principalement de restes. On trouve de grandes colonies dans les ports marins.");
INSERT INTO `dbc_battle_pet_species` VALUES (562, 62991, 132211, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes :|r La forêt de Jade|n", "Le venin de la vipère corail est si puissant qu\'on a déjà vu un kodo adulte succomber à une morsure unique.");
INSERT INTO `dbc_battle_pet_species` VALUES (564, 62994, 133936, 0, 8, 4, 26, "|cFFFFD200Combat de mascottes : |rLa forêt de Jade", "Il est difficile d\'imaginer que Shen Zin Su, qui porte sur sa carapace l\'île Vagabonde tout entière, ait été au début de sa vie une minuscule tortue émeraude.");
INSERT INTO `dbc_battle_pet_species` VALUES (565, 62997, 237579, 0, 8, 4, 26, "|cFFFFD200Combat de mascottes : |rLa forêt de Jade", "Dans la jungle, chaque feuille peut abriter une grenouille. Mais si vous vous arrêtez pour vérifier, elle aura de doute façon décampé depuis longtemps.");
INSERT INTO `dbc_battle_pet_species` VALUES (566, 62998, 643423, 0, 8, 4, 26, "|cFFFFD200Combat de mascottes : |rLa forêt de Jade", "Lorsqu\'ils préparent un voyage vers un pays lointain, les pandarens ont pour tradition de capturer un trotteur du miroir et de le relâcher sain et sauf dans un autre cours d\'eau que le sien.");
INSERT INTO `dbc_battle_pet_species` VALUES (567, 62999, 132211, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rLa forêt de Jade", "Pour les pandarens de la forêt de Jade, ces reptiles sont de lointains cousins du Serpent de jade sacré, Yu\'lon.");
INSERT INTO `dbc_battle_pet_species` VALUES (568, 63001, 442735, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rGorgrond, flèches d’Arak, Talador, forêt de Jade", "Se cache dans sa coquille pour échapper aux pinces des crabes des alentours.");
INSERT INTO `dbc_battle_pet_species` VALUES (569, 63002, 237579, 0, 8, 4, 26, "|cFFFFD200Combat de mascottes : |rLa forêt de Jade", "Les grenouilles des jardins font partie des plus étranges nuisances qui puissent accabler un horticulteur. Elles sont capables de dégâts incroyables sur les plus belles pièces végétales.");
INSERT INTO `dbc_battle_pet_species` VALUES (570, 63003, 646059, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rLa forêt de Jade", "Un grand débat fait rage, visant à déterminer si les tanukis sont des ratons laveurs ou des chiens. Ce qui a finalement peu d\'importance quand ils entreprennent de vous dévorer le fémur.");
INSERT INTO `dbc_battle_pet_species` VALUES (571, 63004, 132211, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rLa forêt de Jade", "Les hozen des montagnes de la forêt de Jade craignent ces reptiles venimeux, et les chassent parfois par pure rancune.");
INSERT INTO `dbc_battle_pet_species` VALUES (572, 63005, 132186, 0, 8, 4, 26, "|cFFFFD200Combat de mascottes : |rLa forêt de Jade", "Ces minuscules arthropodes sont natifs de la Pandarie. C\'est sur l\'île Bonace qu\'ils sont chez eux.");
INSERT INTO `dbc_battle_pet_species` VALUES (573, 63006, 464160, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes : |rLa forêt de Jade", "Le pétrel des sables vole loin au-dessus de l\'île Bonace, bien à l\'abri des prédateurs terrestres.");
INSERT INTO `dbc_battle_pet_species` VALUES (626, 61829, 132182, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes : |rMaleterres de l’Est, Mont Hyjal, Clairières de Tirisfal", "Cette créature de mauvais augure hante les cieux nocturnes des Maleterres de l\'Est et se repaît de charognes.");
INSERT INTO `dbc_battle_pet_species` VALUES (627, 61828, 646782, 0, 3, 4, 26, "|cFFFFD200Combat de mascottes : |rÎle de Brume-Sang, Maleterres de l’Est, Forêt des Pins-Argentés", "S\'il y a une chose qui est encore plus horrible que leur apparence, c\'est le fait que les écureuils infectés des Maleterres ne collectent plus des noisettes, mais des bouts de chair en putréfaction.");
INSERT INTO `dbc_battle_pet_species` VALUES (628, 61827, 132104, 0, 3, 4, 26, "|cFFFFD200Combat de mascottes : |rÎle de Brume-Sang, Maleterres de l’Est, Forêt des Pins-Argentés", "Seul un simple d\'esprit ou un dément aurait l\'idée d\'orner son mur d\'une tête de daim infecté. Mais il est vrai que ça ferait plaisir à ses nouveaux amis morts-vivants.");
INSERT INTO `dbc_battle_pet_species` VALUES (629, 63097, 132186, 123212, 8, 2, 2, "|cFFFFD200Vendeur : |rMatty|n|cFFFFD200Région : |rOrgrimmar", "Cette jolie créature marine n\'est jamais aperçue que sur les rivages de Kezan. Il peut lui arriver d\'être un peu caustique.");
INSERT INTO `dbc_battle_pet_species` VALUES (630, 63098, 132915, 123214, 2, 2, 2, "|cFFFFD200Vendeur :|r Will Larsons|n|cFFFFD200Région :|r Sombrivage", "Le corbeau dit : le mot que vous venez de prononcer. C’est fatigant, à la longue.");
INSERT INTO `dbc_battle_pet_species` VALUES (631, 62127, 132211, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rTarides du Nord, Tarides du Sud, Uldum, Cratère d’Un’Goro", "Chassée pour les jolis motifs de sa peau, cette créature majestueuse vit le long des rivages luxuriants d\'Azeroth.");
INSERT INTO `dbc_battle_pet_species` VALUES (632, 62364, 463491, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rMont Hyjal, Cratère d’Un’Goro", "Le lézard cendré tire son nom de l’apparence de sa peau, qui ressemble à des cendres brûlées.");
INSERT INTO `dbc_battle_pet_species` VALUES (633, 61677, 463569, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rLes Grisonnes, Les Serres-Rocheuses, Les pics Foudroyés, Les Paluns, Berceau-de-l’Hiver", "Le putois des montagnes est célèbre pour sa nature aventureuse. Et pour rien d\'autre, je vous le promets.");
INSERT INTO `dbc_battle_pet_species` VALUES (634, 62435, 132196, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rBerceau-de-l’Hiver", "Ces créatures étranges étincellent comme la glace au soleil, ce qui leur permet de se camoufler dans les grandes étendues neigeuses de Berceau-de-l\'Hiver.");
INSERT INTO `dbc_battle_pet_species` VALUES (635, 61325, 132211, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rTerres Foudroyées, Dun Morogh, Durotar, dépôt de Tristerail, Péninsule des Flammes Infernales, Nagrand, Tarides du Nord, Strangleronce septentrionale, Tarides du Sud, flèches d’Arak", "Contrairement à d\'autres races de serpents qui pourchassent leurs proies, ces astucieux reptiles se cachent dans les feuillages épais pour surprendre les leurs.");
INSERT INTO `dbc_battle_pet_species` VALUES (637, 62638, 132196, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rLes Tranchantes", "Ces arachnides usent de leurs pattes acérées pour creuser des tunnels dans la dure pierre des massifs des Tranchantes.");
INSERT INTO `dbc_battle_pet_species` VALUES (638, 62625, 294474, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rRaz-de-Néant", "Le cafard du Néant s\'est adapté au vide gelé de l\'espace et n\'a pas besoin d\'air pour survivre. Ses organes internes n\'ont plus grand-chose à voir avec ceux d\'un cafard classique.");
INSERT INTO `dbc_battle_pet_species` VALUES (639, 62695, 656176, 0, 4, 4, 24, "|cFFFFD200Combat de mascottes : |rToundra Boréenne", "Différentes de leurs cousins des climats plus chauds, ces robustes créatures peuplent les étendues gelées du Norfendre.");
INSERT INTO `dbc_battle_pet_species` VALUES (640, 61755, 254857, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rContreforts de Hautebrande", "Grâce à la grande largeur de leurs pattes, ces lièvres peuvent bondir sans effort dans la neige pendant que leurs prédateurs s\'enfoncent dans les congères.");
INSERT INTO `dbc_battle_pet_species` VALUES (641, 62693, 254857, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rToundra Boréenne, Désolation des dragons, Les pics Foudroyés, Zul’Drak", "Le cœur d\'un lièvre arctique bat deux fois plus vite que celui de son cousin commun.");
INSERT INTO `dbc_battle_pet_species` VALUES (644, 62641, 134324, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rFjord Hurlant", "Si le rat du fjord n\'était pas sujet à d\'aussi malheureux accidents, il pourrait conquérir le monde.");
INSERT INTO `dbc_battle_pet_species` VALUES (645, 62906, 250626, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes : |rHautes-terres du Crépuscule", "Elle est capable de voler, contrairement aux espèces domestiques.");
INSERT INTO `dbc_battle_pet_species` VALUES (646, 62664, 250626, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes : |rBois de la Pénombre, Marécage d’Âprefange, Forêt d’Elwynn, Contreforts de Hautebrande, Fjord Hurlant, Les Carmines, Forêt de Terokkar, Clairières de Tirisfal, Marche de l’Ouest, Les Paluns|n", "Renâcle à traverser les rues, même quand la motivation paraît importante.");
INSERT INTO `dbc_battle_pet_species` VALUES (647, 62818, 134322, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rLes Grisonnes, Hautes-terres du Crépuscule", "Au plus profond de la forêt, même les petites créatures doivent faire preuve d\'une grande férocité pour survivre.");
INSERT INTO `dbc_battle_pet_species` VALUES (648, 61368, 237579, 0, 8, 4, 26, "|cFFFFD200Combat de mascottes : |rContreforts de Hautebrande, Marais des Chagrins, Hautes-terres du Crépuscule, Zul’Drak", "Ce crapaud est énorme. Oui Madame.");
INSERT INTO `dbc_battle_pet_species` VALUES (649, 62815, 237579, 0, 8, 4, 26, "|cFFFFD200Combat de mascottes : |rBassin de Sholazar", "Les rumeurs sur les propriétés médicinales du crapaud-bile semblent avoir été lancées par un apothicaire particulièrement farceur.");
INSERT INTO `dbc_battle_pet_species` VALUES (650, 63365, 646148, 123778, 6, 0, 2, "|cFFFFD200Butin : |rButin mondial|n|cFFFFD200Région : |rVallée des Quatre vents", "Et vous qui pensiez que des navets au dîner, c’était terrible.");
INSERT INTO `dbc_battle_pet_species` VALUES (652, 63559, 133922, 124000, 8, 2, 2, "|cFFFFD200Vendeur : |rNat Pagle|n|cFFFFD200Région : |rÉtendues sauvages de Krasarang|n|cFFFFD200Faction : |rLes Hameçonneurs - Honoré|n|cFFFFD200Coût : |r250|TINTERFACE\MONEYFRAME\UI-GOLDICON.BLP:0|t", "Cette bulle est pire qu’un aquarium.");
INSERT INTO `dbc_battle_pet_species` VALUES (665, 54745, 134931, 102353, 4, 8, 2, "|cFFFFD200Jeu de cartes à collectionner : |rTombe des Oubliés|n", "Souvent pris à tort pour un hanneton commun, la piqûre de ce scarabée des sables dissipera rapidement ce malentendu, si jamais vous avez la malchance de vous faire piquer.");
INSERT INTO `dbc_battle_pet_species` VALUES (666, 63724, 620832, 0, 8, -1, 56, "", "Elayna n\'apparaît que lors des tempêtes de neige. Il est possible de l\'appâter avec un bloc-note et la promesse de quelques pages de Wiki.");
INSERT INTO `dbc_battle_pet_species` VALUES (671, 63832, 656128, 124660, 4, 7, 82, "|cFFFFD200Promotion :|r Édition collector de Mists of Pandaria", "Progéniture du quilen impérial.");
INSERT INTO `dbc_battle_pet_species` VALUES (675, 62954, 134324, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rForêt d’Elwynn, Hurlevent|n", "Seuls les chats et les dératiseurs l\'auront remarqué mais, peu avant l\'attaque de Hurlevent par Aile de mort, tous les rats avaient précipitamment quitté la ville. Ce n\'est que récemment qu\'ils sont revenus.");
INSERT INTO `dbc_battle_pet_species` VALUES (677, 64246, 646059, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rVallée des Quatre vents", "Un alchimiste gobelin prétendit un jour que la morsure de ce raton laveur lui avait donné le don de double vue. Mais ça n\'était que la rage.");
INSERT INTO `dbc_battle_pet_species` VALUES (678, 63304, 648918, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rÉtendues sauvages de Krasarang", "Ces petites larves vertes naissent dans les étendues sauvages de Pandarie, où le feuillage verdoyant leur permet de se fondre dans le décor.");
INSERT INTO `dbc_battle_pet_species` VALUES (679, 64248, 237397, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes :|r Sommet de Kun-Lai|n", "Vous les trouverez souvent perchés à des hauteurs incroyables, sur des affleurements rocheux. Il existe peu de créatures au pied aussi assuré.");
INSERT INTO `dbc_battle_pet_species` VALUES (680, 63953, 237409, 0, 8, 4, 26, "|cFFFFD200Combat de mascottes :|r Steppes de Tanglong|n", "La mangouste est recherchée pour sa capacité à chasser les serpents venimeux. Rikki-tikki-tchik !");
INSERT INTO `dbc_battle_pet_species` VALUES (699, 63715, 132196, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes :|r La forêt de Jade|n", "Cette créature terrifiante et terriblement venimeuse bondit sur ses proies depuis la canopée touffue de la forêt de Jade.");
INSERT INTO `dbc_battle_pet_species` VALUES (702, 63919, 237579, 0, 8, 4, 26, "|cFFFFD200Combat de mascottes : |rGorgrond, forêt de Jade", "La rainette léopard tient son nom de sa robe tachetée, pas de son régime alimentaire.");
INSERT INTO `dbc_battle_pet_species` VALUES (703, 63716, 646059, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rLa forêt de Jade", "Les jeunes chiens viverrins protestent souvent contre les horaires imposés par leurs parents, et voudraient avoir la permission de midi.");
INSERT INTO `dbc_battle_pet_species` VALUES (705, 65029, 250626, 0, 2, 4, 58, "|cFFFFD200Combat de mascottes :|r Vallée des Quatre vents", "Il y a peu de choses plus ennuyantes pour un fermier que de découvrir qu’un poulet cherche l’amour.");
INSERT INTO `dbc_battle_pet_species` VALUES (706, 63062, 646059, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes :|r Vallée des Quatre vents", "Le régime alimentaire du taciturne raton voleur se compose principalement de pommes.");
INSERT INTO `dbc_battle_pet_species` VALUES (707, 63064, 646059, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes :|r Vallée des Quatre vents", "On entend parfois certains druides dire que les pandarens et les ratons laveurs sont de lointains cousins, mais jamais quand il y a un pandaren dans la pièce.");
INSERT INTO `dbc_battle_pet_species` VALUES (708, 63094, 134322, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes :|r Vallée des Quatre vents, Étendues sauvages de Krasarang", "Beaucoup de gens croient que le rat-épic peut projeter ses épines vers ses adversaires.");
INSERT INTO `dbc_battle_pet_species` VALUES (709, 63095, 134322, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes :|r Vallée des Quatre vents", "Même si les blessures infligées par les petits de rats-épics se révèlent rarement mortelles, la plupart des prédateurs préfèrent s\'en prendre à des proies plus faciles.");
INSERT INTO `dbc_battle_pet_species` VALUES (710, 63096, 646325, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rVallée des Quatre vents", "Se nourrit de la délicieuse végétation de la vallée des Quatre vents.");
INSERT INTO `dbc_battle_pet_species` VALUES (711, 63057, 645906, 0, 8, 4, 26, "|cFFFFD200Combat de mascottes :|r Vallée des Quatre vents", "La loutre sicroc vit le long des rivières et se montre normalement joueuse et amicale.");
INSERT INTO `dbc_battle_pet_species` VALUES (712, 63358, 645906, 0, 8, 4, 26, "|cFFFFD200Combat de mascottes :|r Vallée des Quatre vents", "Les loutres nagent en se propulsant grâce à leur puissant appendice caudal et en fléchissant leur long corps. Extrêmement rapide dans l’eau.");
INSERT INTO `dbc_battle_pet_species` VALUES (713, 63060, 656341, 0, 8, 4, 26, "|cFFFFD200Combat de mascottes :|r Vallée des Quatre vents", "La chair de la tortue à carapace molle est considérée comme un mets rare. Il s\'agit d\'ailleurs de l\'ingrédient principal de la bisque de tortue apaisante.");
INSERT INTO `dbc_battle_pet_species` VALUES (714, 65054, 132196, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes :|r Étendues sauvages de Krasarang|n|n", "Le remède des gens du commun à la morsure d’un jeune mandibrûle est de danser sauvagement pour essayer, en suant, d’éliminer les toxines avant qu’elles agissent.");
INSERT INTO `dbc_battle_pet_species` VALUES (715, 65099, 648918, 0, 4, 4, 58, "|cFFFFD200Combat de mascottes :|r Vallée des Quatre vents|n", "Très recherchés pour leur capacité à tisser des cocons de soie. Dans leur habitat naturel, l’œil inexercé à du mal à les discerner.");
INSERT INTO `dbc_battle_pet_species` VALUES (716, 63288, 132196, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes :|r Étendues sauvages de Krasarang|n", "De manière peu commune pour un arachnide, cette créature non venimeuse ne se nourrit que de plantes et de fruits.");
INSERT INTO `dbc_battle_pet_species` VALUES (717, 63291, 237144, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes :|r Étendues sauvages de Krasarang|n|n", "Dans la famille des hannetons, je voudrais le plus délicieux.");
INSERT INTO `dbc_battle_pet_species` VALUES (718, 65124, 236193, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes :|r Étendues sauvages de Krasarang|n", "Un insecte intrépide et orgueilleux, capable de braver tous les dangers dans sa quête sans fin d\'une lumière qui brille.");
INSERT INTO `dbc_battle_pet_species` VALUES (722, 65185, 236196, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes :|r Étendues sauvages de Krasarang|n", "Se nourrit de petites feuilles et d\'herbe une fois arrivé à maturité.");
INSERT INTO `dbc_battle_pet_species` VALUES (723, 63293, 656345, 0, 8, 4, 26, "|cFFFFD200Combat de mascottes :|r Étendues sauvages de Krasarang", "Lorsqu\'une cistude épineuse est contrariée, elle se recroqueville dans sa carapace et se met à tournoyer violemment en direction de son agresseur.");
INSERT INTO `dbc_battle_pet_species` VALUES (724, 63550, 797544, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes :|r Sommet de Kun-Lai|n", "S\'enfouit profondément dans les congères pour échapper à ses prédateurs.");
INSERT INTO `dbc_battle_pet_species` VALUES (725, 63551, 797544, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes :|r Sommet de Kun-Lai|n", "Ce petit animal se nourrit en chapardant ici et là des morceaux des victimes de plus grands prédateurs.");
INSERT INTO `dbc_battle_pet_species` VALUES (726, 63547, 463491, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes :|r Sommet de Kun-Lai|n", "Lorsqu\'il est immobile, l\'observateur des plaines fait osciller sa queue d\'avant en arrière. Vu de loin, cela reproduit le mouvement des hautes herbes sous le vent et lui permet de tromper ses prédateurs volants.");
INSERT INTO `dbc_battle_pet_species` VALUES (727, 59702, 647701, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes :|r Sommet de Kun-Lai|n", "La prairie pullule de rongeurs et autres petites créatures, mais aucun n\'est aussi commun que la souris.");
INSERT INTO `dbc_battle_pet_species` VALUES (728, 63585, 250626, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes :|r Sommet de Kun-Lai", "Ne pond ses œufs que sous une lumière intense.");
INSERT INTO `dbc_battle_pet_species` VALUES (729, 63557, 254857, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes :|r Sommet de Kun-Lai|n", "Les légendes racontent que c\'est le premier lièvre tolai qui, en gambadant parmi les étoiles, les bouscula et les fit tomber du firmament vers notre monde.");
INSERT INTO `dbc_battle_pet_species` VALUES (730, 63558, 254857, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes :|r Sommet de Kun-Lai|n", "Les lapereaux tolai doivent quitter leur tanière dès leur plus jeune âge pour se faire une place dans le monde.");
INSERT INTO `dbc_battle_pet_species` VALUES (731, 63555, 132211, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes :|r Sommet de Kun-Lai|n", "Les écailles de ces reptiles sont munies de petits crochets qui leur permettent de gravir les pentes les plus raides du sommet de Kun-Lai.");
INSERT INTO `dbc_battle_pet_species` VALUES (732, 65187, 236193, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes :|r Terres de l’Angoisse, Steppes de Tanglong", "Cet insecte, qui pullule pendant la saison des pluies, est très attiré par les orages.");
INSERT INTO `dbc_battle_pet_species` VALUES (733, 63549, 646325, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes :|r Steppes de Tanglong", "La nuit, les sauterelles de la prairie remplissent l’obscurité de leurs chants insupportables. Ceux qui n’y sont pas habitués passent en général de nombreuses nuits sans sommeil, à cause de la sérénade de leurs cris de parade.");
INSERT INTO `dbc_battle_pet_species` VALUES (737, 65190, 237409, 0, 8, 4, 26, "|cFFFFD200Combat de mascottes :|r Steppes de Tanglong|n", "Malgré leur apparence inoffensive, les mangoustes sont de dangereux carnivores, bien connus pour leur talent dans la chasse aux serpents venimeux, des serpents qui pourraient tuer des créatures bien plus grosses.");
INSERT INTO `dbc_battle_pet_species` VALUES (739, 63954, 237409, 0, 8, 4, 26, "|cFFFFD200Combat de mascottes :|r Steppes de Tanglong|n", "Les bébés mangoustes préfèrent les herbes hautes où seules leurs mères peuvent les trouver.");
INSERT INTO `dbc_battle_pet_species` VALUES (740, 63957, 134324, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes :|r Steppes de Tanglong, Terres de l’Angoisse|n", "Le lien qui unit le yack au rat est bien étrange. Ce que le rat a à gagner dans cette relation symbiotique reste un grand mystère.");
INSERT INTO `dbc_battle_pet_species` VALUES (741, 64804, 644001, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes :|r Terres de l’Angoisse|n", "Ces hérissons sont peut-être silencieux, mais on entend souvent les cris de ceux qui ont bêtement marché sur leurs épines.");
INSERT INTO `dbc_battle_pet_species` VALUES (742, 64242, 644001, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes :|r Terres de l’Angoisse|n", "Non, les hérissons n\'aiment pas tous jouer au croquet avec des flamands roses.");
INSERT INTO `dbc_battle_pet_species` VALUES (743, 64352, 442735, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes :|r Terres de l’Angoisse|n|n", "Les bigorneaux peuvent se livrer entre eux à des batailles épiques lors du partage de la nourriture.");
INSERT INTO `dbc_battle_pet_species` VALUES (744, 64238, 294474, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes :|r Terres de l’Angoisse|n", "Les cafards des terres de l\'Angoisse vivent dans les ombres. Certains seraient prêts à jurer que ce sont les ombres qui les suivent.");
INSERT INTO `dbc_battle_pet_species` VALUES (745, 63548, 132195, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes :|r Terres de l’Angoisse|n", "Sa carapace unique est très recherchée par les bijoutiers pour sa texture et sa solidité.");
INSERT INTO `dbc_battle_pet_species` VALUES (746, 65203, 132186, 0, 8, 4, 26, "|cFFFFD200Combat de mascottes :|r Terres de l’Angoisse|n", "Le crabe empereur tient son nom de son apparence royale : les bords de sa carapace et ses pinces dressées ressemblent étrangement à une couronne. Qui vous arracherait les yeux si vous essayiez de la porter.");
INSERT INTO `dbc_battle_pet_species` VALUES (747, 63850, 236196, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes :|r Val de l’Éternel printemps|n", "Son corps luminescent sert à attirer le sexe opposé.");
INSERT INTO `dbc_battle_pet_species` VALUES (748, 63838, 236193, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes :|r Val de l’Éternel printemps|n", "Les ailes délicates de cet insecte sont souvent utilisées par les couturiers pour orner les vêtements et les armures.");
INSERT INTO `dbc_battle_pet_species` VALUES (749, 63841, 645906, 0, 8, 4, 26, "|cFFFFD200Combat de mascottes :|r Val de l’Éternel printemps", "Les civettes sont les cousines terrestres des loutres. Elles sont recherchées pour leur musc très odorant.");
INSERT INTO `dbc_battle_pet_species` VALUES (750, 63842, 645906, 0, 8, 4, 26, "|cFFFFD200Combat de mascottes :|r Val de l’Éternel printemps", "Les civettes sont connues pour la férocité avec laquelle elles défendent leurs petits.");
INSERT INTO `dbc_battle_pet_species` VALUES (751, 63847, 643423, 0, 8, 4, 26, "|cFFFFD200Combat de mascottes :|r Val de l’Éternel printemps|n", "Ainsi nommée pour la manière dont elle semble danser en parcourant les surfaces aquatiques, cette créature se déplace de manière erratique pour éviter de servir de repas aux poissons ou autres prédateurs immergés.");
INSERT INTO `dbc_battle_pet_species` VALUES (752, 63849, 237579, 0, 8, 4, 26, "|cFFFFD200Combat de mascottes :|r Val de l’Éternel printemps|n", "Malgré son apparence menaçante, cette grenouille taureau ventre-jaune porte bien son nom.");
INSERT INTO `dbc_battle_pet_species` VALUES (753, 65215, 236193, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes :|r Forêt de Jade|n", "Cet insecte, qui pullule pendant la saison des pluies, est très attiré par les orages.");
INSERT INTO `dbc_battle_pet_species` VALUES (754, 65216, 236196, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes :|r La forêt de Jade|n", "Cette espèce d’insectes est attirée par les énergies latentes des sanctuaires, leur aspect luminescent ajoutant encore au mystique de ces lieux de pouvoir.");
INSERT INTO `dbc_battle_pet_species` VALUES (755, 62887, 294474, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes :|r Mont Hyjal|n", "Les marques sur la carapace de ce cafard ressemblent beaucoup à celles de la Tête de Mort.");
INSERT INTO `dbc_battle_pet_species` VALUES (756, 62916, 236193, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes :|r Le Tréfonds|n", "Aime les grottes sombres et raffole du nectar de pétale de cœur.");
INSERT INTO `dbc_battle_pet_species` VALUES (757, 14755, 134157, 23531, 1, 7, 82, "|cFFFFD200Promotion :|r iCoke Chine", "Les légendes racontent que ces êtres d’émeraude vibrant portent chance et assurent des voyages sans danger à leurs propriétaires.");
INSERT INTO `dbc_battle_pet_species` VALUES (758, 14756, 134158, 23530, 1, 7, 82, "|cFFFFD200Promotion :|r iCoke Chine", "On dit qu’en voir un en automne est synonyme de bonne fortune. Il est connu pour se cacher des créatures inconnues.");
INSERT INTO `dbc_battle_pet_species` VALUES (792, 65314, 646586, 127816, 2, 2, 2, "|cFFFFD200Vendeur : |rAudrey Burnhep, Varzok|n|cFFFFD200Région : |rHurlevent, Orgrimmar|n|cFFFFD200Coût : |r50|TINTERFACE\MONEYFRAME\UI-SILVERICON.BLP:0|t", "Une gracieuse créature de l’île de Pandarie.");
INSERT INTO `dbc_battle_pet_species` VALUES (800, 65344, 133718, 0, 7, -1, 57, "", "");
INSERT INTO `dbc_battle_pet_species` VALUES (802, 65313, 646018, 127813, 1, 2, 18, "|cFFFFD200Vendeur :|r Marché noir", "Une version miniature du serpent-nuage fulminant, en provenance directe des terres lointaines de la Pandarie.");
INSERT INTO `dbc_battle_pet_species` VALUES (817, 65323, 646018, 0, 1, 4, 26, "|cFFFFD200Combat de mascottes :|r La forêt de Jade|n|cFFFFD200Faction :|r L’ordre du Serpent-nuage - Exalté|n", "Tellement adorable, et pourtant si épique.");
INSERT INTO `dbc_battle_pet_species` VALUES (818, 65324, 652138, 0, 1, 4, 26, "|cFFFFD200Combat de mascottes :|r La forêt de Jade|n|cFFFFD200Faction :|r L’ordre du Serpent-nuage - Exalté", "Tellement adorable, et pourtant si épique.");
INSERT INTO `dbc_battle_pet_species` VALUES (819, 65321, 630863, 0, 1, 4, 26, "|cFFFFD200Combat de mascottes :|r La forêt de Jade|n|cFFFFD200Faction :|r L’ordre du Serpent-nuage - Exalté|n", "Tellement adorable, et pourtant si épique.");
INSERT INTO `dbc_battle_pet_species` VALUES (820, 64232, 646325, 127815, 4, 4, 82, "|cFFFFD200Haut fait : |rCheptel de pros|n|cFFFFD200Catégorie : |rCombat de mascottes", "Se nourrit de la délicieuse végétation des étendues sauvages de Krasarang.");
INSERT INTO `dbc_battle_pet_species` VALUES (821, 63621, 646445, 124152, 0, 5, 82, "|cFFFFD200Haut fait : |rVa y avoir des nœuds dans les laisses !|n|cFFFFD200Catégorie : |rCollection", "Il n’arrête pas de me regarder. Je crois qu’il veut me mordre.");
INSERT INTO `dbc_battle_pet_species` VALUES (823, 62907, 463569, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes : |rHautes-terres du Crépuscule", "Ce putois féroce peut asperger un assaillant à trente mètres de distance et l’aveugler temporairement. Manipuler avec précaution !");
