INSERT INTO `dbc_battle_pet_species` VALUES (834, 64634, 399041, 126251, 6, 0, 2, "|cFFFFD200Butin :|r Karr l’Assombrisseur|n|cFFFFD200Région :|r Terres de l’Angoisse|n|n", "Les sorciers mogu, gardiens du secret pour donner vie à la pierre, préfèrent ces compagnons solides comme un roc aux mascottes plus bestiales.");
INSERT INTO `dbc_battle_pet_species` VALUES (835, 64632, 594273, 126247, 0, 4, 82, "|cFFFFD200Haut fait : |rVoyage mystique aux plantes de Ling Ting|n|cFFFFD200Catégorie : |rDonjons et raids", "Le monde extérieur peut être un endroit dangereux pour une si petite créature toute mignonne.");
INSERT INTO `dbc_battle_pet_species` VALUES (836, 64633, 133942, 126249, 8, 0, 2, "|cFFFFD200Butin :|r Nalash Verdantis|n|cFFFFD200Région :|r Terres de l’Angoisse|n|n", "Les trotteurs aquatiques jouent un rôle important dans la culture jinyu, servant de monture à ce peuple, tout autant que de transport de messages, et même d’animaux domestiques.");
INSERT INTO `dbc_battle_pet_species` VALUES (837, 62915, 134397, 0, 6, 4, 26, "|cFFFFD200Combat de mascottes : |rTréfonds", "Des aventuriers ont signalé avoir vu une gigantesque araignée de schiste couleur émeraude, que les habitants du Tréfonds ont rapidement appelée Jadecroc.");
INSERT INTO `dbc_battle_pet_species` VALUES (838, 62182, 134399, 0, 6, 4, 26, "|cFFFFD200Combat de mascottes : |rTréfonds, Désolace", "Les araignées de schiste améthyste adultes préfèrent généralement créer leur nid contre les murs extérieurs du Tréfonds.");
INSERT INTO `dbc_battle_pet_species` VALUES (844, 64899, 236473, 126885, 9, 3, 2, "|cFFFFD200Métier :|r Ingénierie|n", "Construit à partir d’un amas de rouages, de ressorts et de câbles, ainsi que d’un petit quelque chose en plus, ce petit dragon personnifie l’esprit de la Pandarie vu à travers des lunettes d’ingénieur.");
INSERT INTO `dbc_battle_pet_species` VALUES (845, 61877, 133247, 120501, 5, 3, 2, "|cFFFFD200Métier :|r Joaillerie|n", "Le jade est une pierre d’une couleur riche et pleine de vie. C’est le matériau parfait pour cet adorable compagnon. On raconte que la brise créée par le battement de ses ailes peut apaiser l’esprit troublé.");
INSERT INTO `dbc_battle_pet_species` VALUES (846, 61883, 133236, 120507, 6, 3, 2, "|cFFFFD200Métier :|r Joaillerie|n", "Chacun de ces petits félins est confectionné de la même manière, mais les imperfections de la pierre donnent à chacun un caractère unique lorsqu’ils sont animés. Cependant, tous restent joueurs et amicaux !");
INSERT INTO `dbc_battle_pet_species` VALUES (847, 62829, 237309, 122748, 8, 1, 2, "|cFFFFD200Quête : |rQu’ils crament !|n|cFFFFD200Région : |rLa forêt de Jade", "Nombreux sont ceux qui croient (à tort) que les poissons rouges n’ont pas de mémoire. Les dresseurs qui travaillent avec eux savent bien que ce n’est pas le cas et ont quelques bleus pour le prouver.");
INSERT INTO `dbc_battle_pet_species` VALUES (848, 59358, 237182, 114090, 4, 0, 2, "|cFFFFD200Butin : |rLapin de Sombrelune|n|cFFFFD200Région : |rÎle de Sombrelune", "Ce lapin est plus solide qu’il n’en a l’air.");
INSERT INTO `dbc_battle_pet_species` VALUES (849, 66104, 604252, 127008, 2, 3, 146, "|cFFFFD200Métier :|r Calligraphie|n", "Un magnifique cerf-volant animé par les calligraphes de Pandarie.");
INSERT INTO `dbc_battle_pet_species` VALUES (850, 66105, 133840, 127006, 2, 3, 146, "|cFFFFD200Métier :|r Calligraphie|n", "Qui eût cru que les calligraphes pouvaient concevoir des cerfs-volants ?");
INSERT INTO `dbc_battle_pet_species` VALUES (851, 62894, 463491, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rUldum", "Rapide mais aussi très bruyant !");
INSERT INTO `dbc_battle_pet_species` VALUES (855, 66491, 463540, 130759, 6, 5, 82, "|cFFFFD200Haut fait : |rÇa en fait des sacs de croquettes !|n|cFFFFD200Catégorie : |rCollection", "Bizarre qu’elle n’aime pas les mouches, vous ne trouvez pas ?");
INSERT INTO `dbc_battle_pet_species` VALUES (856, 66450, 254105, 130726, 6, 5, 82, "|cFFFFD200Haut fait : |rEt si j’ouvrais une animalerie ?|n|cFFFFD200Catégorie : |rCombats de mascottes", "Attention, il peut parfois devenir intenable !");
INSERT INTO `dbc_battle_pet_species` VALUES (868, 66950, 656560, 131590, 6, 1, 2, "|cFFFFD200Quête :|r Dompteur d’esprits pandarens|n|cFFFFD200Région : |rVal de l’Éternel printemps", "Ces esprits personnifient le cœur des pouvoirs élémentaires de la Pandarie.");
