INSERT INTO `dbc_battle_pet_species` VALUES (1013, 67022, 656342, 0, 8, 4, 26, "|cFFFFD200Combat de mascottes :|r Étendues sauvages de Krasarang|n|cFFFFD200Évènement :|r Festival de l’explorateur", "La légende raconte que l’île Vagabonde, Shen Zin Su, était au départ une tortue-dragon de mer aussi petite que celle-ci. Peut-être que votre petite tortue prendra des proportions gigantesques aussi ?");
INSERT INTO `dbc_battle_pet_species` VALUES (1039, 67230, 236193, 132574, 2, 3, 2, "|cFFFFD200Métier : |rTailleur (soie impériale)", "Sous sa forme larvaire, ce phalène est connu dans le monde entier pour produire la soie la plus délicate d’Azeroth.");
INSERT INTO `dbc_battle_pet_species` VALUES (1040, 67233, 648918, 132580, 4, 3, 2, "|cFFFFD200Métier : |rTailleur (soie impériale)|n", "Cette chenille rouge et dodue est connue dans le monde entier pour produire la soie la plus délicate d’Azeroth.");
INSERT INTO `dbc_battle_pet_species` VALUES (1042, 63370, 646325, 123784, 4, 1, 26, "|cFFFFD200Quête :|r Sho, nécessite d’être exalté auprès de la faction|n|cFFFFD200Région :|r Vallée des Quatre vents|n", "Attention, il pourrait manger vos récoltes !");
INSERT INTO `dbc_battle_pet_species` VALUES (1061, 67319, 132198, 132762, 4, 2, 2, "|cFFFFD200Vendeur : |rLhara|n|cFFFFD200Région : |rÎle de Sombrelune|n|cFFFFD200Coût : |r90|TINTERFACE\ICONS\inv_misc_ticket_darkmoon_01:0|t", "La plupart des bébés trotteurs des forêts de Violette sont transformés en plats de foire par Stamp Corne-Tonnerre, mais quelques-uns sont élevés pour devenir des montures ou achetés par des aventuriers amateurs de petits compagnons violets.");
INSERT INTO `dbc_battle_pet_species` VALUES (1062, 67329, 135799, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes :|r Île de Sombrelune|n", "On aperçoit parfois, entre les arbres tordus de l’île de Sombrelune, leurs inquiétantes lueurs vertes rappelant les gangreflammes.");
INSERT INTO `dbc_battle_pet_species` VALUES (1063, 67332, 136155, 132789, 5, 0, 2, "|cFFFFD200Butin : |rFournitures pour familiers de Sombrelune|n", "N’essayez même pas de tricher.");
