INSERT INTO `dbc_battle_pet_species` VALUES (1068, 67443, 645217, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes :|r Se joint parfois à d’autres créatures lors de combats sur l’île de Sombrelune.", "Même s’ils sont plus petits que les grands freux de Gilnéas, les corbeaux ne sont pas moins rusés et sournois.");
INSERT INTO `dbc_battle_pet_species` VALUES (1073, 16445, 656558, 28487, 0, 7, 82, "|cFFFFD200Promotion :|r iCoke Taiwan", "Terkie, aussi timide qu’adorable, préfère la compagnie d’amis proches plutôt que d’en découvrir de nouveaux.");
INSERT INTO `dbc_battle_pet_species` VALUES (1117, 68267, 666530, 134538, 6, 9, 82, "|cFFFFD200Boutique en jeu|r", "Un petit pyromane joueur et ronronnant. Ne le carressez pas sans vos gants ignifugés !");
