INSERT INTO `dbc_battle_pet_species` VALUES (1142, 68601, 134152, 135156, 9, 2, 18, "|cFFFFD200Vendeur : |rPaul Nord|n|cFFFFD200Région : |rArène de Castagn’ar|n|cFFFFD200Coût : |r30|TINTERFACE\MONEYFRAME\UI-SILVERICON.BLP:0|t", "Créé dans un unique but : écraser les petites bestioles aussi fort que possible.");
INSERT INTO `dbc_battle_pet_species` VALUES (1143, 68656, 134899, 135254, 3, 0, 2, "|cFFFFD200Butin : |rMaexxna|n|cFFFFD200Région : |rNaxxramas", "Trouvée dans un œuf poussiéreux au fond du repaire de Maexxna, cette petite créature éclot à l’état de mort-vivant.");
INSERT INTO `dbc_battle_pet_species` VALUES (1144, 68657, 571319, 135255, 3, 0, 2, "|cFFFFD200Butin : |rHorreb|n|cFFFFD200Région : |rNaxxramas", "Le corps de Horreb est couvert de petites capsules capables d’envoyer des spores fongiques chancreuses jusqu’à mille mètres de distance quand les vents sont favorables.");
INSERT INTO `dbc_battle_pet_species` VALUES (1145, 68655, 656579, 135256, 3, 5, 82, "|cFFFFD200Haut fait : |rRaid de mascottes|n|cFFFFD200Catégorie : |rCollections", "Après avoir quitté la glaciale nécropole de Naxxramas, M. Bigglesworth s’est vite fait une nouvelle place au soleil pour faire une sieste bien méritée.");
INSERT INTO `dbc_battle_pet_species` VALUES (1146, 68654, 133718, 135257, 3, 0, 2, "|cFFFFD200Butin : |rGluth|n|cFFFFD200Région : |rNaxxramas", "Quand Gluth fut tué par un groupe de courageux aventuriers, ces petites bestioles ont été retrouvées sur le cadavre, en train de ronger les os du géant mort-vivant.");
INSERT INTO `dbc_battle_pet_species` VALUES (1147, 68665, 132312, 135258, 0, 0, 2, "|cFFFFD200Butin : |rMessager de Sulfuron|n|cFFFFD200Région : |rCœur du Magma", "Il reste très peu de ces messagers enflammés depuis la défaite de Ragnaros dans les terres de Feu.");
INSERT INTO `dbc_battle_pet_species` VALUES (1149, 68664, 136218, 135259, 0, 0, 2, "|cFFFFD200Butin : |rMagmadar|n|cFFFFD200Région : |rCœur du Magma", "Ces petits diablotins de flammes hantent le Cœur du Magma en grandes meutes et peuvent renouveler leurs nombres très rapidement.");
INSERT INTO `dbc_battle_pet_species` VALUES (1150, 68666, 237011, 135259, 6, 0, 2, "|cFFFFD200Butin : |rGolemagg l’Incinérateur|n|cFFFFD200Région : |rCœur du Magma|n", "Le cœur refroidi de Golemagg semble garder des propriétés plus ou moins sensibles.");
INSERT INTO `dbc_battle_pet_species` VALUES (1151, 68661, 236469, 135263, 1, 0, 2, "|cFFFFD200Butin : |rTranchetripe l’Indompté|n|cFFFFD200Région : |rRepaire de l’Aile noire", "Un des œufs de Tranchetripe ayant miraculeusement survécu dans le repaire de l’Aile noire.");
INSERT INTO `dbc_battle_pet_species` VALUES (1152, 68662, 377270, 135264, 1, 0, 2, "|cFFFFD200Butin : |rChromaggus|n|cFFFFD200Région : |rRepaire de l’Aile noire", "Les adversaires qui affrontent cette féroce monstruosité chromatique espèrent seulement ne pas avoir à subir d’Arrêt du temps.");
INSERT INTO `dbc_battle_pet_species` VALUES (1153, 68663, 236472, 135265, 1, 0, 2, "|cFFFFD200Butin : |rSeigneur des couvées Lanistaire|n|cFFFFD200Région : |rRepaire de l’Aile noire", "Avorton de la Serre noire, resté pour garder les dragonnets.");
INSERT INTO `dbc_battle_pet_species` VALUES (1154, 68660, 132108, 135266, 5, 0, 2, "|cFFFFD200Butin : |rViscidus|n|cFFFFD200Région : |rAhn’Qiraj", "Ce petit globule n’est pas revenu à temps vers Viscidus pour se reformer.");
INSERT INTO `dbc_battle_pet_species` VALUES (1155, 68659, 134908, 135267, 0, 0, 2, "|cFFFFD200Butin : |rEmpereur Vek’lor|n|cFFFFD200Région : |rAhn’Qiraj", "Idole anubisath du temple d’Ahn’Qiraj, imprégnée d’une antique magie qiraji.");
INSERT INTO `dbc_battle_pet_species` VALUES (1156, 68658, 134896, 135268, 5, 0, 2, "|cFFFFD200Butin : |rLe prophète Skeram|n|cFFFFD200Région : |rAhn’Qiraj", "Fléau de nombreux aventuriers, le regarder dans les yeux, c’est comme plonger le regard dans la folie pure.");
INSERT INTO `dbc_battle_pet_species` VALUES (1157, 68804, 132921, 0, 0, 4, 26, "|cFFFFD200Combat de mascottes :|r Tarides du Nord", "Elles deviennent de plus en plus belles avec l’âge.");
INSERT INTO `dbc_battle_pet_species` VALUES (1158, 68805, 458252, 0, 0, 4, 26, "|cFFFFD200Combat de mascottes :|r Féralas", "Chassé par les indigènes pendant des années, c’est un spécimen extrêmement rare.");
INSERT INTO `dbc_battle_pet_species` VALUES (1159, 68806, 133740, 0, 5, 4, 26, "|cFFFFD200Combat de mascottes :|r Contreforts de Hautebrande|n", "La plupart des gens n’ont jamais lu ce livre.");
INSERT INTO `dbc_battle_pet_species` VALUES (1160, 68819, 134416, 0, 5, 4, 26, "|cFFFFD200Combat de mascottes :|r Défilé de Deuillevent|n", "Ne le quittez jamais des yeux.");
INSERT INTO `dbc_battle_pet_species` VALUES (1161, 68820, 298656, 0, 1, 4, 26, "|cFFFFD200Combat de mascottes :|r Grottes du Temps|n", "Aucun autre choix que de croire qu’il sait l’heure qu’il est.");
INSERT INTO `dbc_battle_pet_species` VALUES (1162, 68838, 668552, 0, 9, 4, 26, "|cFFFFD200Combat de mascottes : |rGnomeregan|n", "Créé à l’origine par les ingénieurs dans les entrailles de Gnomeregan, le félin flammaré est une merveille de l’ingénierie moderne.");
INSERT INTO `dbc_battle_pet_species` VALUES (1163, 68839, 773096, 0, 9, 4, 26, "|cFFFFD200Combat de mascottes :|r Berceau-de-l’Hiver|n", "Les sorciers ingénieurs de Long-Guet créèrent des modèles de test de cette jeune créature. Malheureusement, certains se sont éloignés et n’ont plus jamais été retrouvés.");
INSERT INTO `dbc_battle_pet_species` VALUES (1164, 68841, 774414, 0, 9, 4, 26, "|cFFFFD200Combat de mascottes :|r Les Tranchantes|n", "Ce modèle de raptor est conçu dans les Tranchantes. Ses rugissements robotiques peuvent souvent être entendus près du poste de Toshley.");
INSERT INTO `dbc_battle_pet_species` VALUES (1165, 68845, 797547, 0, 1, 4, 26, "|cFFFFD200Combat de mascottes :|r Frimarra|n", "Avortons du Vol de Malygos, ces dragonnets sont très prisés pour leur magie des Arcanes illimitée.");
INSERT INTO `dbc_battle_pet_species` VALUES (1166, 68846, 797882, 0, 0, 4, 26, "|cFFFFD200Combat de mascottes :|r Sommet de Kun-Lai|n", "Présent sur les pics du sommet de Kun-Lai, ce yéti très rare est bien plus dangereux qu’il n’y parait.");
INSERT INTO `dbc_battle_pet_species` VALUES (1167, 68850, 587740, 0, 1, 4, 26, "|cFFFFD200Combat de mascottes :|r Bassin de Scholazar|n", "Ces magnifiques drakes sont présents sur toutes les rives du Norfendre.");
INSERT INTO `dbc_battle_pet_species` VALUES (1168, 15361, 132834, 25018, 0, 7, 82, "|cFFFFD200Promotion :|r Évènement promotionnel coréen", "Murki est un membre de la race de murlocs la plus rare du monde.");
INSERT INTO `dbc_battle_pet_species` VALUES (1174, 69208, 355498, 136484, 5, 8, 2, "|cFFFFD200Jeu de cartes à collectionner :|r La Trahison du gardien", "Un livre gangrené dont les secrets sont si affreux et bizarres que ses pages semblent insufflées d’une vie sinistre. On le trouve sur le chevet de nombreux démonistes.");
INSERT INTO `dbc_battle_pet_species` VALUES (1175, 69648, 796787, 0, 6, 4, 26, "|cFFFFD200Combat de mascottes : |rÎle du Tonnerre", "Mieux vaut rester à distance de ces dangereux rongeurs touchés par la foudre.");
INSERT INTO `dbc_battle_pet_species` VALUES (1176, 69649, 774009, 137568, 7, 1, 82, "|cFFFFD200Quêtes : |rCréatures des mythes et légendes", "Vous avez vu comme je suis trop trop mignon ? Incroyable, non ?");
INSERT INTO `dbc_battle_pet_species` VALUES (1177, 69748, 796636, 137977, 6, 0, 2, "|cFFFFD200Butin : |rTrône du tonnerre", "Nées du sable, ces dangereuses créatures peuvent se solidifier et se disperser en un battement de cils.");
INSERT INTO `dbc_battle_pet_species` VALUES (1178, 69778, 838516, 138082, 9, 0, 2, "|cFFFFD200Butin :|r Assemblage saccage-soleil détraqué|n|cFFFFD200Région :|r Île du Tonnerre", "Loyaux envers les Saccage-soleil depuis leur création, ces mini assemblages ont tout de même une force de frappe étonnante.");
INSERT INTO `dbc_battle_pet_species` VALUES (1179, 69794, 801473, 0, 6, 4, 26, "|cFFFFD200Combat de mascottes : |rÎle du Tonnerre", "On peut voir la foudre traverser son corps, du bout du museau à la pointe de la queue.");
INSERT INTO `dbc_battle_pet_species` VALUES (1180, 69796, 797325, 138087, 7, 0, 2, "|cFFFFD200Butin : |rDinomancien zandalari|n|cFFFFD200Région : |rÎle des Géants|n", "Ce raptor unique est né sur l’île de Zandalar et a été transporté en même temps que l’armée zandalari.");
INSERT INTO `dbc_battle_pet_species` VALUES (1181, 69818, 801474, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rÎle du Tonnerre", "On raconte que ces serpents ne font qu’une bouchée des gnomes.");
INSERT INTO `dbc_battle_pet_species` VALUES (1182, 69819, 804969, 0, 8, 4, 26, "|cFFFFD200Combat de mascottes : |rÎle du Tonnerre", "Mieux vaut ne pas y toucher.");
INSERT INTO `dbc_battle_pet_species` VALUES (1183, 69820, 838812, 138161, 9, 0, 2, "|cFFFFD200Butin : |rAnimus|n|cFFFFD200Région : |rTrône du tonnerre", "Forgées au Trône du tonnerre, ces minuscules créations servent le Sombre animus.");
INSERT INTO `dbc_battle_pet_species` VALUES (1184, 69849, 840541, 138287, 7, 5, 82, "|cFFFFD200Haut fait : |rDresseur de mascottes brutal|n|cFFFFD200Catégorie : |rCombat", "Bien que minuscules, les navrecornes s’en prennent à des victimes sans méfiance et sèment le chaos dans leur sillage.");
INSERT INTO `dbc_battle_pet_species` VALUES (1185, 69848, 838549, 138285, 5, 1, 2, "|cFFFFD200Quête : |rUn gros tas d’os de dinosaure géant|n|cFFFFD200Région : |rÎle des Géants", "Cette créature vous semble familière…");
