INSERT INTO `dbc_battle_pet_species` VALUES (1196, 69891, 774009, 138380, 7, 1, 2, "|cFFFFD200Quêtes : |rCréatures des mythes et légendes", "Ces petits fripons câlins aiment se prélasser au soleil, dormir au soleil et surtout se reposer au soleil !");
INSERT INTO `dbc_battle_pet_species` VALUES (1197, 69893, 838693, 138381, 7, 1, 2, "|cFFFFD200Quêtes : |rCréatures des mythes et légendes", "La fourrure blanche du panda neigeux lui permet de se cacher dans les étendues neigeuses du sommet de Kun-Lai.");
INSERT INTO `dbc_battle_pet_species` VALUES (1198, 69892, 838691, 138382, 7, 1, 2, "|cFFFFD200Quêtes : |rCréatures des mythes et légendes", "Cousin du panda neigeux, le panda des montagnes réside dans les régions moins élevées du sommet de Kun-Lai.");
INSERT INTO `dbc_battle_pet_species` VALUES (1200, 70083, 840540, 138825, 7, 0, 2, "|cFFFFD200Butin : |rHorridon|n|cFFFFD200Région : |rTrône du tonnerre", "Ne pas caresser l’extrémité pointue. Note : toutes les extrémités sont pointues.");
INSERT INTO `dbc_battle_pet_species` VALUES (1201, 70098, 133898, 138913, 8, 0, 2, "|cFFFFD200Butin : |rG’nathus|n|cFFFFD200Région : |rSteppes de Tanglong", "Certains pandarens pensent que G’nathus est un ancien loa zandalari resté sur place pour protéger les eaux au sud de la citadelle de l’île de Lei Shen.");
INSERT INTO `dbc_battle_pet_species` VALUES (1202, 70144, 838694, 139148, 2, 0, 2, "|cFFFFD200Butin : |r Ji Kun|n|cFFFFD200Région : |rTrône du tonnerre", "Dangereux rejetons de la Mère ancestrale, les fils de Ji Kun sont considérés comme des dons sacrés dans de nombreuses cultures.");
INSERT INTO `dbc_battle_pet_species` VALUES (1204, 70082, 798062, 138824, 9, 3, 2, "|cFFFFD200Métier : |rIngénierie", "Le rêve de tous les cuisiniers emballé dans un paquet de métal et d’écrous absolument parfait. Dommage que ce qu’il cuisine soit impropre à la consommation. D’après les notes de Jard, ce petit bonhomme est destiné à son amie Emily Cole.");
INSERT INTO `dbc_battle_pet_species` VALUES (1205, 70154, 840542, 139153, 7, 0, 2, "|cFFFFD200Butin : |rNavrecornes|n|cFFFFD200Région : |rÎle des Géants|n", "Les Zandalari élèvent certaines espèces de navrecornes pour leur taille réduite, afin de les utiliser comme montures.");
INSERT INTO `dbc_battle_pet_species` VALUES (1206, 70257, 850139, 139361, 8, 3, 2, "|cFFFFD200Métier :|r Pêche", "On suppose que les écailles rouges de ce poisson servent d’avertissement aux prédateurs des steppes de Tanglong et des terres de l’Angoisse contre leur chair toxique. Les pêcheurs dévoués peuvent parfois se lier d’amitié avec l’un de ces poissons.");
INSERT INTO `dbc_battle_pet_species` VALUES (1207, 70258, 850137, 139362, 8, 3, 2, "|cFFFFD200Métier :|r Pêche", "Ces petites bêtes ne vivent que dans les eaux de l’île du Temps figé ou à proximité des danios joyaux et des mandarins ventre-rouge. Les pêcheurs dévoués peuvent parfois se lier d’amitié avec l’un de ces poissons.");
INSERT INTO `dbc_battle_pet_species` VALUES (1208, 70259, 850138, 139363, 8, 3, 2, "|cFFFFD200Métier :|r Pêche", "La couleur verte et les rayures de cette petite carpe semblent lui servir de camouflage dans les eaux de la forêt de Jade, de la vallée des Quatre vents et des étendues sauvages de Krasarang. Les pêcheurs dévoués peuvent parfois se lier d’amitié avec l’un de ces poissons.");
INSERT INTO `dbc_battle_pet_species` VALUES (1209, 70260, 850140, 139365, 8, 3, 2, "|cFFFFD200Métier :|r Pêche|n", "Ce poisson robuste peut survivre sous une grande variété de climats, des sommets enneigés de Kun-Lai aux eaux salées et tempérées qui bordent la Pandarie. Les pêcheurs dévoués peuvent parfois se lier d’amitié avec l’un de ces poissons.");
INSERT INTO `dbc_battle_pet_species` VALUES (1211, 70451, 797326, 139932, 7, 0, 2, "|cFFFFD200Butin : |rDinomancien zandalari|n|cFFFFD200Région : |rÎle des Géants|n", "Ces minuscules serviteurs de Zandalar adorent se faufiler clandestinement dans les cales des énormes navires de guerre zandalari.");
INSERT INTO `dbc_battle_pet_species` VALUES (1212, 70452, 797327, 139933, 7, 0, 2, "|cFFFFD200Butin : |rDinomancien zandalari|n|cFFFFD200Région : |rÎle des Géants|n", "Malgré leur petite taille, ces raptors peuvent éviscérer une vache en moins de dix secondes.");
INSERT INTO `dbc_battle_pet_species` VALUES (1213, 70453, 797328, 139934, 7, 0, 2, "|cFFFFD200Butin : |rDinomancien zandalari|n|cFFFFD200Région : |rÎle des Géants|n", "Ces adorables créatures sont parfaites pour les câlins, tant qu’on n’est pas attaché à son visage.");
INSERT INTO `dbc_battle_pet_species` VALUES (1226, 71014, 463876, 141433, 0, 0, 2, "|cFFFFD200Butin : |rLe Grand Méchant Loup|n|cFFFFD200Région : |rKarazhan", "La progéniture maintes fois oubliée du Grand Méchant Loup, retrouvée repue et endormie dans un panier de provisions.");
INSERT INTO `dbc_battle_pet_species` VALUES (1227, 71015, 236513, 141434, 9, 0, 2, "|cFFFFD200Butin : |rLe conservateur|n|cFFFFD200Région : |rKarazhan", "L’une de ses tâches principales est le dépoussiérage des nombreux artéfacts magiques de Medivh. Une activité qui a coûté la santé mentale ou physique, voire la vie à de nombreux conservateurs avant l’arrivée de Moroes.");
INSERT INTO `dbc_battle_pet_species` VALUES (1228, 71016, 615096, 141435, 5, 0, 2, "|cFFFFD200Butin : |rPrince Malchezaar|n|cFFFFD200Région : |rKarazhan", "En quittant le Néantespace, les Abyssaux de Malchezaar sont étrangement désorientés par les protections magiques de Karazhan. Leur taille est considérablement réduite et ils sont bien moins enclins à provoquer spontanément un jaillissement de flammes.");
INSERT INTO `dbc_battle_pet_species` VALUES (1229, 71033, 460856, 141451, 0, 0, 2, "|cFFFFD200Butin : |rTerestian Malsabot|n|cFFFFD200Région : |rKarazhan", "Sbire de Terestian Malsabot, ce diablotin rusé est resté enfermé dans Karazhan lorsque les portes vers le Néant distordu se sont fermées derrière lui.");
INSERT INTO `dbc_battle_pet_species` VALUES (1230, 71017, 443382, 141436, 8, 0, 2, "|cFFFFD200Butin : |rMorogrim Marcheur-des-flots|n|cFFFFD200Région : |rCaverne du Sanctuaire du serpent", "Une découverte majeure pour la communauté scientifique d’Azeroth, qui s’est longtemps demandé, principalement dans les tavernes, à quoi pouvait ressembler un bébé géant des mers.");
INSERT INTO `dbc_battle_pet_species` VALUES (1231, 71018, 136030, 141437, 6, 0, 2, "|cFFFFD200Butin : |rHydross l’Instable|n|cFFFFD200Région : |rCaverne du Sanctuaire du serpent", "Le rejeton d’Hydross est considéré par la communauté élémentaire comme le « Comte des flaques ».");
INSERT INTO `dbc_battle_pet_species` VALUES (1232, 71019, 254502, 141446, 5, 0, 2, "|cFFFFD200Butin : |rDame Vashj|n|cFFFFD200Région : |rCaverne du Sanctuaire du serpent", "On ne sait pas comment dame Vashj parvient à contenir ces trotteurs des tourbières dotés de puissantes capacités de contrôle mental. Dès leur plus jeune âge, ces créatures font preuve de remarquables aptitudes psychiques.");
INSERT INTO `dbc_battle_pet_species` VALUES (1233, 71020, 466089, 141447, 9, 0, 2, "|cFFFFD200Butin : |rSaccageur du Vide|n|cFFFFD200Région : |rDonjon de la Tempête|n|n", "Ils ont été envoyés à travers le Néant distordu par le grand Archimonde le Profanateur pour terroriser des civilisations minuscules, au rythme du martèlement de leurs pieds de taille moyenne.");
INSERT INTO `dbc_battle_pet_species` VALUES (1234, 71021, 538447, 141448, 5, 0, 2, "|cFFFFD200Butin :|r Grande astromancienne Solarian|n|cFFFFD200Région : |rDonjon de la Tempête|n", "Les lanternes portées par les implorateurs du Vide sont un passage entre le monde des vivants et celui des morts, elles attirent souvent les fantômes et les esprits errants.");
INSERT INTO `dbc_battle_pet_species` VALUES (1235, 71022, 132188, 141449, 1, 0, 2, "|cFFFFD200Butin : |rAl’ar|n|cFFFFD200Région : |rDonjon de la Tempête|n", "Ces faucons-dragons aux couleurs chatoyantes, aussi rapides qu\'infaillibles, transportent l’énergie de Kael’thas à travers la tempête de néant qui entoure l’Œil.");
INSERT INTO `dbc_battle_pet_species` VALUES (1236, 71023, 133695, 141450, 7, 5, 82, "|cFFFFD200Haut fait : |rRaid de mascottes II : Harmonisation|n|cFFFFD200Catégorie : |rCollections", "Bien que le spectacle soit réglé comme du papier à musique, il est interrompu chaque fois que Tito croise le regard de Graou et se lance à sa poursuite dans tout l’Opéra de Karazhan.");
INSERT INTO `dbc_battle_pet_species` VALUES (1237, 71159, 463493, 141789, 8, 2, 2, "|cFFFFD200Vendeur : |rRavika|n|cFFFFD200Région : |rDurotar|n|cFFFFD200Coût : |r1|TINTERFACE\ICONS\inv_drink_31_embalmingfluid:0|t|n|n|cFFFFD200Vendeur : |rTenuki|n|cFFFFD200Région : |rDurotar|n|cFFFFD200Coût :|r1|TINTERFACE\ICONS\inv_drink_31_embalmingfluid:0|t", "Cela fait longtemps que les trolls ont appris à maîtriser l’invocation des hydres. Gahz’rooki n’est peut-être pas la plus grande et la plus forte d\'entre elles, mais elle est capable de vous arracher un doigt si vous vous approchez trop.");
INSERT INTO `dbc_battle_pet_species` VALUES (1238, 71163, 851119, 0, 3, 4, 26, "|cFFFFD200Combat de mascottes : |rNorfendre", "Les Val’kyrs sont considérées comme « en herbe » jusqu’à ce qu’elles aient vaincu une créature noble.");
INSERT INTO `dbc_battle_pet_species` VALUES (1243, 71199, 237513, 142028, 5, 0, 2, "|cFFFFD200Butin : |rPrimordius|n|cFFFFD200Région : |rTrône du tonnerre|n|cFFFFD200Mode : |rOutil Raids|n", "Les sombres bassins de la chambre de Primordius stagnaient depuis la chute des mogu, et de sombres pensées ont commencé à s’y former.");
INSERT INTO `dbc_battle_pet_species` VALUES (1244, 71200, 576309, 142029, 5, 0, 2, "|cFFFFD200Butin : |r Primordius|n|cFFFFD200Région : |rTrône du tonnerre|n|cFFFFD200Mode : |rNormal ou héroïque|n", "Ces limons malveillants sont très sensibles aux phéromones de Primordius.");
INSERT INTO `dbc_battle_pet_species` VALUES (1245, 71201, 576313, 142030, 5, 0, 2, "|cFFFFD200Butin : |rCrasse tremblante", "Il est fortement conseillé aux dompteurs de mascottes de combat venant de jouer avec des crasseux de se laver les mains pendant au moins 30 minutes.");
INSERT INTO `dbc_battle_pet_species` VALUES (1247, 71438, 327502, 0, 8, -1, 1072, "", "");
INSERT INTO `dbc_battle_pet_species` VALUES (1248, 71488, 874857, 142880, 6, 9, 82, "|cFFFFD200Boutique en jeu|r", "Cet ancien en fleurs est une espèce unique en Azeroth, créé à partir des branches éternelles du Grand arbre. Il peut fleurir en n’importe quelle saison.");
INSERT INTO `dbc_battle_pet_species` VALUES (1255, 71655, 135129, 143637, 0, 7, 176, "|cFFFFD200Promotion :|r Tournoi d’arène 2013|n", "Bourbimus Tyrannicus cache bien son mrrglrrglrgl.");
INSERT INTO `dbc_battle_pet_species` VALUES (1256, 71693, 876476, 143703, 9, 3, 2, "|cFFFFD200Métier : |rIngénierie", "Ce petit bonhomme semble aimer rôder autour d’évènements cataclysmiques. Rien ne prouve qu’il soit à l’origine de ces évènements, évidemment. Il n’est peut-être qu’un observateur. Ou alors il se perd en route pendant ses explorations.");
INSERT INTO `dbc_battle_pet_species` VALUES (1257, 71700, 618977, 143732, 0, 0, 34, "|cFFFFD200Butin : |rDéveloppeurs", "Grand risque de crise de folie qui pourrait faire des victimes.");
