INSERT INTO `dbc_battle_pet_species` VALUES (1303, 72462, 877514, 145697, 2, 2, 82, "|cFFFFD200Vendeur : |rMaître Li|n|cFFFFD200Région : |rÎle du Temps figé|n|cFFFFD200Coût : |r3|TINTERFACE\ICONS\INV_MISC_TRINKETPANDA_07:0|t", "Chi Chi tente d’apporter l’espoir par son cri puissant et en chassant (puis en mangeant) des papillons.");
INSERT INTO `dbc_battle_pet_species` VALUES (1304, 72463, 877408, 145698, 1, 2, 82, "|cFFFFD200Vendeur : |rMaître Li|n|cFFFFD200Région : |rÎle du Temps figé|n|cFFFFD200Coût : |r3|TINTERFACE\ICONS\INV_MISC_TRINKETPANDA_07:0|t", "Un maître sculpteur a dédié son existence à la création d’une statue à la beauté inégalée, inspiré par son amour et son admiration pour Yu’lon. Lorsque cet artiste est mort, Yu’lon a insufflé une fraction de son essence à la statue, donnant naissance à Yu’la.");
INSERT INTO `dbc_battle_pet_species` VALUES (1305, 72464, 900317, 145699, 7, 2, 82, "|cFFFFD200Vendeur :|r Tournoi des Astres vénérables", "Toujours prêt à se battre ou à trouver de l’herbe à brouter, Zao garde la tête basse et le regard vers l’avant.");
INSERT INTO `dbc_battle_pet_species` VALUES (1311, 72285, 877514, 0, 2, -1, 1072, "", "");
