INSERT INTO `dbc_battle_pet_species` VALUES (1320, 73011, 773178, 147124, 9, 0, 2, "|cFFFFD200Butin :|r Paquet-cadeau du Bling-o-tron|n", "Ce magnifique petit robot est l’image crachée de son « papa », le Bling-o-tron 4000. Comme les circuits imprimés de générosité d’un Bling-o-tron ne viennent à maturité que lorsqu’il a atteint ses spécifications maximales, ne vous attendez pas à ce qu’il vous offre quoi que ce soit.");
INSERT INTO `dbc_battle_pet_species` VALUES (1321, 73534, 646586, 0, 2, 0, 2, "|cFFFFD200Butin :|r Nid de grues|n|cFFFFD200Région :|r Île du Temps figé", "Les gruons de l’île du Temps figé chantent parfois une curieuse mélodie qui a le don de calmer les nerfs et apaiser l’âme.");
INSERT INTO `dbc_battle_pet_species` VALUES (1322, 73352, 897633, 15048, 9, 0, 2, "|cFFFFD200Butin :|r Ingé-siège Boîte-Noire|n|cFFFFD200Raid :|r Siège d’Orgrimmar", "La première bombinette créée dans le Sous-bastion. L’ingé-siège Boîte-Noire ne supportait pas l’idée qu’elle soit détruite et l’a gardée comme gentil (et explosif) animal de compagnie.");
INSERT INTO `dbc_battle_pet_species` VALUES (1323, 73533, 136021, 148046, 0, 0, 2, "|cFFFFD200Butin :|r Soigne-Feuille|n|cFFFFD200Région :|r Île du Temps figé|n", "Les lutins feuille-d’orne de l’île du Temps figé protègent inlassablement la végétation de l’île de la fureur ardente d’Ordos et de ses serviteurs.");
INSERT INTO `dbc_battle_pet_species` VALUES (1324, 73542, 236193, 148068, 2, 4, 26, "|cFFFFD200Combat de mascottes :|r Île du Temps figé|n", "Les assassins jinyu utilisent ce phalène à la fois comme leurre mobile et source de lumière pour révéler les failles des armures de leurs ennemis.");
INSERT INTO `dbc_battle_pet_species` VALUES (1325, 73543, 236193, 148069, 2, 4, 26, "|cFFFFD200Combat de mascottes :|r Île du Temps figé|n", "Les chats et autres créatures semblables tentent parfois de s’amuser avec ce phalène... Mais ils finissent généralement par s’y brûler les moustaches.");
INSERT INTO `dbc_battle_pet_species` VALUES (1326, 73368, 236193, 148065, 2, 4, 26, "|cFFFFD200Combat de mascottes :|r Île du Temps figé|n", "Cet étrange phalène interrompt parfois arbitrairement le sort d’un mage ou d’un démoniste de passage.");
INSERT INTO `dbc_battle_pet_species` VALUES (1328, 73356, 669468, 138161, 6, 0, 2, "|cFFFFD200Butin :|r Garnia|n|cFFFFD200Région :|r Île du Temps figé", "Les eaux du lac Rubis sont teintées d’une étrange nuance cramoisie, probablement due aux rubis que l’on trouve au cœur des montagnes de l’île du Temps figé.");
INSERT INTO `dbc_battle_pet_species` VALUES (1329, 73532, 348547, 148051, 0, 0, 2, "|cFFFFD200Butin :|r Affreux lutin|n|cFFFFD200Région :|r Île du Temps figé|n", "Bien que fasciné par les fleurs, le boute-en-train est également connu pour danser avec les brins d’herbe et même les mauvaises herbes.");
INSERT INTO `dbc_battle_pet_species` VALUES (1330, 73364, 801475, 148052, 7, 0, 2, "|cFFFFD200Butin :|r Python impérial|n|cFFFFD200Région :|r Île du Temps figé", "À la fin de sa vie, si les conditions environnementales sont acceptables, un Aspic mortel peut muer et se transformer en Python impérial.");
INSERT INTO `dbc_battle_pet_species` VALUES (1331, 73350, 132851, 148058, 6, 0, 2, "|cFFFFD200Butin :|r Sha de l’orgueil|n|cFFFFD200Raid :|r Siège d’Orgrimmar|n|cFFFFD200Mode :|r Dynamique, normal ou héroïque", "Infime résidu du pouvoir d’Y’Shaarj qui a fait apparaître le Sha, une simple goutte d’Y’Shaarj peut amplifier les émotions sur des kilomètres, noyant des villes entières dans la peur et le doute.");
INSERT INTO `dbc_battle_pet_species` VALUES (1332, 73351, 651085, 148059, 6, 0, 2, "|cFFFFD200Butin :|r Sha de l’orgueil|n|cFFFFD200Raid :|r Siège d’Orgrimmar|n|cFFFFD200Mode :|r Outil Raids ou dynamique", "Les Sha-tons reflètent l’état émotionnel de leur dresseur. Ils peuvent être agités, dépressifs, craintifs ou en proie au doute selon l’intensité des pensées négatives de leur environnement.");
INSERT INTO `dbc_battle_pet_species` VALUES (1333, 73355, 656560, 148060, 6, 0, 2, "|cFFFFD200Butin :|r Danseur brumejade|n|cFFFFD200Région :|r Île du Temps figé", "Les danseurs brumejade de l’île du Temps figé ont une étrange attirance pour les évents à vapeur de la côte du nord-ouest.");
INSERT INTO `dbc_battle_pet_species` VALUES (1334, 73354, 624010, 148061, 7, 0, 2, "|cFFFFD200Butin :|r Parangons des Klaxxi|n|cFFFFD200Raid :|r Siège d’Orgrimmar|n", "La mini terreur des Terres de l’Angoisse, Kovok, préfère un régime stable à base de « nourriture cérébrale » : cerveaux mushans, cerveaux humains… Tous types de cerveaux ! Cet étrange besoin nutritionnel pourrait être indirectement lié à la taille de son énorme tête.");
INSERT INTO `dbc_battle_pet_species` VALUES (1335, 73357, 135777, 148551, 6, 0, 2, "|cFFFFD200Butin :|r Flamme annonciatrice|n|cFFFFD200Région :|r Île du Temps figé|n|n", "Les étranges flammes bleues de la caverne des Esprits perdus ont mené bon nombre d’aventuriers à leur perte.");
INSERT INTO `dbc_battle_pet_species` VALUES (1336, 73367, 669467, 148063, 6, 0, 2, "|cFFFFD200Butin :|r Zhu Gon l’Amer|n|cFFFFD200Région :|r Île du Temps figé|n|n", "Lorsque l’Ordon Yaungol a attaqué le village de Pi’jiu, il y a plusieurs générations, les villageois ont courageusement placé un tonneau au centre du village. Le matin suivant, à la fin des festivités, les Yaungol encore ivres ont accepté sans condition de signer un traité de paix.");
INSERT INTO `dbc_battle_pet_species` VALUES (1337, 73366, 132186, 148066, 8, 0, 2, "|cFFFFD200Butin :|r Pincépine monstrueux|n|cFFFFD200Région :|r Île du Temps figé|n", "Les crabes pincépine de Pandarie existaient déjà il y a des milliers d’années et font partie des créatures les plus anciennes du continent.");
INSERT INTO `dbc_battle_pet_species` VALUES (1338, 73359, 804966, 148067, 8, 0, 2, "|cFFFFD200Butin :|r Bufo|n|cFFFFD200Combat de mascottes :|r Île du Temps figé", "Les grenouilles gobeuses de l’île du Temps figé peuvent se repaître de proies beaucoup plus grosses qu’elles en recouvrant leur victime d’une gelée toxique fonctionnant comme des sucs gastriques, mais à l’air libre.");
INSERT INTO `dbc_battle_pet_species` VALUES (1339, 73627, 896625, 0, 7, -1, 1072, "", "");
INSERT INTO `dbc_battle_pet_species` VALUES (1343, 73668, 877482, 148373, 0, 0, 18, "|cFFFFD200Butin :|r Coffre de Kukuru|n|cFFFFD200Région :|r Île du Temps figé|n", "Les hozens turbulents sont formés dès leur plus jeune âge à utiliser des gants de boxe afin de ne pas tuer accidentellement les membres de leur tribu et, plus important, afin de les empêcher de se curer le nez.");
INSERT INTO `dbc_battle_pet_species` VALUES (1344, 73688, 838551, 148427, 7, 2, 2, "|cFFFFD200Vendeur :|r Porte-parole Gulan|n|cFFFFD200Région :|r Île du Temps figé", "Une des bêtes restées sur l’île du Temps figé quand elle s’est déplacée dans le temps, sans doute sacrifiée par les Ordons à leur demi-dieu, Ordos.");
INSERT INTO `dbc_battle_pet_species` VALUES (1345, 73730, 648918, 148527, 4, 0, 2, "|cFFFFD200Butin :|r Gu’chi l’Essaimeur|n|cFFFFD200Région :|r Île du Temps figé", "Les couvelins de Gu’chi tissent une forme de soie très ancienne et très puissante qui n’existe plus en Pandarie depuis très longtemps.");
INSERT INTO `dbc_battle_pet_species` VALUES (1346, 73732, 838550, 148530, 5, 2, 2, "|cFFFFD200Vendeur : |rTisse-brume Ku|n|cFFFFD200Région : |rÎle du Temps figé|n|cFFFFD200Faction : |rShaohao - Éveillé", "Une exposition prolongée aux brumes apaisantes de Shaohao a calmé ces esprits, qui servent désormais de gentils compagnons aux Tisse-brume.");
INSERT INTO `dbc_battle_pet_species` VALUES (1348, 73738, 135794, 148552, 6, 0, 2, "|cFFFFD200Butin :|r Esprit de Jadefeu|n|cFFFFD200Région :|r Île du Temps figé|n|n", "On dit que l’esprit jadefeu serait l’incarnation vivante du souffle de jade de Yu’lon.");
INSERT INTO `dbc_battle_pet_species` VALUES (1349, 73741, 134144, 148567, 0, 1, 2, "|cFFFFD200Évènement mondial :|r Fête du Voile d’hiver|n", "Agacé par les longues heures de travail, un salaire misérable et la compagnie d’aventuriers se plaignant que les cadeaux du Voile d’hiver sont ridicules, ces petites créatures vicieuses ont fui les ateliers magiques pour rejoindre l’abominable Grinche.");
INSERT INTO `dbc_battle_pet_species` VALUES (1350, 73809, 645352, 148684, 2, 2, 146, "|cFFFFD200Vendeur :|r Ku Mo|n|cFFFFD200Région : |rÎle du Temps figé", "Une bougie à l’intérieur du cerf-volant chauffe l’air, permettant de le faire flotter sans l’aide du vent.");
INSERT INTO `dbc_battle_pet_species` VALUES (1351, 34770, 237272, 66175, 3, 6, 2, "|cFFFFD200Évènement mondial :|r Jour des morts", "Ces marionnettes possédées sont animées d’un semblant de vie par les esprits fantaisistes de ceux qui nous ont quittés.");
INSERT INTO `dbc_battle_pet_species` VALUES (1352, 444, 135997, 0, 7, -1, 49, "", "");
