INSERT INTO `dbc_battle_pet_species` VALUES (1363, 74402, 132620, 149787, 7, 9, 82, "|cFFFFD200Boutique en jeu|r", "Cette race de grands chiens est élevée pour le secours en montagne, et est célèbre pour avoir retrouvé et ranimé de nombreux voyageurs piégés sous la neige… avant de les recouvrir de bave !");
INSERT INTO `dbc_battle_pet_species` VALUES (1364, 74405, 615479, 149792, 0, 7, 82, "|cFFFFD200Promotion :|r BlizzCon 2013", "Les croisés comme tout le monde de temps en temps se dévergondent. Notre imagination est féconde et notre chorégraphie vagabonde !");
INSERT INTO `dbc_battle_pet_species` VALUES (1365, 74413, 370211, 149810, 0, 7, 82, "|cFFFFD200Promotion :|r Édition collector de Diablo III: Reaper of Souls", "Attention à votre porte-monnaie.");
INSERT INTO `dbc_battle_pet_species` VALUES (1384, 76873, 134163, 170267, 0, -1, 114, "", "Coriaces et terribles, les puissants lardons massacrent les ennemis plus faibles sans hésitation.");
INSERT INTO `dbc_battle_pet_species` VALUES (1385, 77021, 236190, 0, 1, 1, 2, "|cFFFFD200Quête :|r Maîtriser la Ménagerie|n", "Les deux têtes de cette terrifiante chimère sont tout aussi redoutables, chacune possédant sa propre attaque Souffle.");
INSERT INTO `dbc_battle_pet_species` VALUES (1386, 77137, 952507, 155748, 2, 7, 82, "|cFFFFD200Promotion :|r Édition collector de Warlords of Draenor", "Ces ailes sombres obscurciront et consumeront l’esprit des faibles… parce qu’elles sont vraiment trop mignonnes !");
INSERT INTO `dbc_battle_pet_species` VALUES (1387, 77221, 892829, 155838, 9, 1, 2, "|cFFFFD200Quête :|r Un rapport au roi (Alliance), Prévenir le chef de guerre (Horde)|n|cFFFFD200Région :|r Terres Foudroyées (90+)|n", "Cette arme de destruction massive a été miniaturisée pour un usage privé, idéal pour le jardin. Attention les taupes !");
INSERT INTO `dbc_battle_pet_species` VALUES (1394, 78421, 136118, 0, 3, 1, 2, "|cFFFFD200Quête :|r Maîtriser la Ménagerie|n", "Conçue à partir de morceaux de mascottes vaincues, de magie noire et d’une pincée d’amour.");
INSERT INTO `dbc_battle_pet_species` VALUES (1395, 78895, 134062, 25162, 5, 3, 2, "|cFFFFD200Métier :|r Cuisine", "On en mangerait, mais ce n’est pas recommandé.");
INSERT INTO `dbc_battle_pet_species` VALUES (1396, 79039, 134010, 159581, 6, 2, 2, "|cFFFFD200Vendeur :|r Naron Florechardon|n|cFFFFD200Région :|r Chutelune|n|cFFFFD200Coût :|r 1000|TINTERFACE\ICONS\INV_FARM_PUMPKINSEED_YELLOW.BLP:0|t|n|n|cFFFFD200200Vendeur :|r Tarnon|n|cFFFFD200Région :|r Mur-de-Givre|n|cFFFFD200Coût :|r 1000|TINTERFACE\ICONS\INV_FARM_PUMPKINSEED_YELLOW.BLP:0|t", "Livrées à elles-mêmes, les carottes finissent par devenir folles.");
