INSERT INTO `dbc_battle_pet_species` VALUES (1412, 80329, 463878, 162135, 9, 3, 2, "|cFFFFD200Métier : |rIngénierie", "À la suite de nombreuses plaintes à propos de la difficulté de dompter de jeunes sangliers, des ingénieurs ont mis au point cet étrange modèle plus vrai que nature.");
INSERT INTO `dbc_battle_pet_species` VALUES (1416, 81431, 132921, 164212, 2, 0, 2, "|cFFFFD200Butin :|r Nid de terogriffe|n|cFFFFD200Région :|r Talador", "La terogriffe est l’un des pires cauchemars de tout ce qui est petit, mignon et poilu.");
