INSERT INTO `dbc_battle_pet_species` VALUES (1427, 82715, 134324, 0, 4, 4, 26, "|cFFFFD200Combat de mascottes :|r crête de Givrefeu", "Horripilant et nuisible, il peut être amadoué à grand renfort de fromage et devenir ainsi un compagnon très acceptable.");
INSERT INTO `dbc_battle_pet_species` VALUES (1428, 83562, 134048, 0, 3, 0, 2, "|cFFFFD200Butin :|r Carapace d’os de Sombreflux réanimé, Carcasse de Sombreflux|n|cFFFFD200Région :|r Vallée d’Ombrelune|n", "Zomstrok est le mal incarné, l’erreur impie d’une personne rendue folle par l’isolement, une créature impossible à détruire.");
