INSERT INTO `dbc_battle_pet_species` VALUES (1435, 89198, 134060, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes : |rNagrand", "Il y a longtemps, un ogre essaya d’utiliser un avorton peau-de-cuir comme ballon. Depuis, son nom est devenu Un-Pied.");
INSERT INTO `dbc_battle_pet_species` VALUES (1441, 83642, 804968, 0, 8, 4, 26, "|cFFFFD200Combat de mascottes :|r Nagrand, flèches d’Arak, Talador|n", "Les enfants orcs ont pour tradition de se glisser près d’un saute-boue et de murmurer un secret qu’ils veulent voir se réaliser. Si le saute-boue reste immobile, le vœu se réalisera.");
INSERT INTO `dbc_battle_pet_species` VALUES (1442, 83817, 877477, 0, 3, 1, 2, "|cFFFFD200Quête :|r Maîtriser la Ménagerie", "Dans une ultime démonstration d’obstination, ce vieux bouc autrefois vivant a simplement refusé de mourir.");
INSERT INTO `dbc_battle_pet_species` VALUES (1443, 83839, 516666, 0, 3, -1, 48, "", "");
