INSERT INTO `dbc_battle_pet_species` VALUES (1446, 84330, 656599, 168668, 7, 2, 2, "|cFFFFD200Quête :|r De nouveaux petits|n|cFFFFD200Région :|r Nagrand (Draenor)", "L’intelligence et la personnalité attachante des jeunes elekk en font des compagnons fidèles pour toute une vie.");
INSERT INTO `dbc_battle_pet_species` VALUES (1447, 82045, 133275, 0, 8, 4, 26, "|cFFFFD200Combat de mascottes :|r vallée d’Ombrelune", "Le crabe carapalune réfléchit si bien la lumière qu’on peut s’en servir comme d’un miroir. Attention à ne pas trop approcher votre visage de ses pinces !");
INSERT INTO `dbc_battle_pet_species` VALUES (1448, 84441, 134480, 168977, 8, 3, 2, "|cFFFFD200Métier :|r Pêche|n|cFFFFD200Vendeur :|r Nat Pagle|n|cFFFFD200Région :|r fief, cabane de pêche|n|cFFFFD200Coût :|r 50|Tinterface\icons\inv_misc_coin_19.blp:0|t", "Les bébés phoques préfèrent être nus que porter de la fourrure !");
INSERT INTO `dbc_battle_pet_species` VALUES (1449, 84521, 1044497, 169220, 7, 5, 18, "|cFFFFD200Vendeur :|r Vendeur de guilde|n|cFFFFD200Région :|r Hurlevent, Orgrimmar|n|cFFFFD200Coût : |r300|TINTERFACE\MONEYFRAME\UI-GOLDICON.BLP:0|t", "Ce petit possède une capacité de perception rare qui lui permet de détecter et suivre les prédateurs alpha pour survivre.");
INSERT INTO `dbc_battle_pet_species` VALUES (1450, 84885, 986488, 169666, 9, 2, 2, "|cFFFFD200Vendeur :|r Redresseur de torts Nuurem|n|cFFFFD200Réputation :|r Conseil des exarques|n", "Bien que ces minuscules gardiens mécaniques aient été créés pour protéger les elekk domestiques des nuisibles, on a également découvert qu’ils sont aussi très efficaces pour masser le dos.");
INSERT INTO `dbc_battle_pet_species` VALUES (1451, 84915, 1044792, 169695, 6, 5, 82, "|cFFFFD200Haut fait :|r 10e anniversaire de WoW|n|cFFFFD200Catégorie :|r Tours de force", "Il adore surveiller les troupeaux de diablotins, batifoler dans la lave et recevoir des gratouilles des seigneurs élémentaires.");
INSERT INTO `dbc_battle_pet_species` VALUES (1453, 79730, 1044480, 0, 7, 4, 58, "|cFFFFD200Combat de mascottes :|r Draenor", "Étonnamment vif quand on le provoque, il se calme aussitôt quand on lui présente un picotin de sagittaire de Nagrand.");
INSERT INTO `dbc_battle_pet_species` VALUES (1454, 85009, 1019377, 0, 0, -1, 32, "", "Gronnlin taper !");
INSERT INTO `dbc_battle_pet_species` VALUES (1455, 85005, 1044500, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes :|r vallée d’Ombrelune", "Camouflés en lichens, les glisseurs se dissimulent dans les arbres pour plonger sur leurs victimes.");
INSERT INTO `dbc_battle_pet_species` VALUES (1456, 85007, 1044500, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes :|r flèches d’Arak", "Ils abusent leurs proies grâce à leur air si mignon, puis la cueillent dans un déluge de coups dévastateurs. Câliner avec prudence uniquement.");
INSERT INTO `dbc_battle_pet_species` VALUES (1457, 85003, 1044498, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes :|r crête de Givrefeu", "Dotés de griffes extrêmement tranchantes, ces jeunes épine-glaciale creusent les couches de glace et de roche pour s’abriter.");
INSERT INTO `dbc_battle_pet_species` VALUES (1458, 85014, 1044486, 170286, 2, 2, 2, "|cFFFFD200Vendeur :|r Marchand de faction|n|cFFFFD200Réputation :|r Crâne-Ricanant", "Un rituel ogre implique de prendre une guêpe d’os entre ses mains et de les secouer vigoureusement. Leur venin augmente la force et provoque des hallucinations vivaces.");
INSERT INTO `dbc_battle_pet_species` VALUES (1462, 85255, 1044488, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes :|r flèches d’Arak", "Contrairement à la croyance populaire, les guêpes piquesang ne vivent pas de sang mais d’amour.");
INSERT INTO `dbc_battle_pet_species` VALUES (1463, 85254, 1044487, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes :|r Gorgrond", "Les guêpes forestières font leur nid dans des arbres morts ou creux et sont promptes à attaquer tout ce qui menace leur essaim.");
INSERT INTO `dbc_battle_pet_species` VALUES (1464, 85253, 1044485, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes :|r crête de Givrefeu, Gorgrond", "Au clair de lune, leurs ailes créent une aura brillante donnant l’illusion d’une étoile scintillante.");
INSERT INTO `dbc_battle_pet_species` VALUES (1465, 85257, 1044489, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes :|r Gorgrond", "Les barbelambres poussent souvent des créatures plus grandes qu’elles à les agresser. Une fois que leur victime les attaque, c’est toute la ruche qui réplique.");
INSERT INTO `dbc_battle_pet_species` VALUES (1466, 85283, 1019377, 0, 0, -1, 34, "", "Gronnlin taper !");
INSERT INTO `dbc_battle_pet_species` VALUES (1467, 85284, 894222, 170292, 9, 0, 2, "|cFFFFD200Butin :|r Paquet-cadeau du Bling-o-tron 5000|n", "À l’origine, le Ciel Bo a été conçu par la tristement célèbre Millie Watt pour que son chat joue avec, mais ce dernier a préféré faire la sieste dans le carton d’emballage.");
INSERT INTO `dbc_battle_pet_species` VALUES (1468, 85388, 1045160, 0, 2, -1, 58, "", "Les anciennes tribus de Draenor récoltaient leurs becs tranchants pour en faire des armes et outils grossiers.");
INSERT INTO `dbc_battle_pet_species` VALUES (1469, 85192, 1045161, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes :|r Gorgrond", "Les collectionneurs recherchent avec ardeur le plumage coloré de ces oiseaux. On dit que porter leurs plumes vous rend chanceux.");
INSERT INTO `dbc_battle_pet_species` VALUES (1470, 85389, 1045158, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes :|r Gorgrond", "Les bords de leur bec sont particulièrement tranchants. Attention à vos doigts en les nourrissant !");
