INSERT INTO `dbc_battle_pet_species` VALUES (1515, 85231, 986489, 170282, 9, 0, 2, "|cFFFFD200Butin :|r Talador", "Le soin incroyable apporté aux mécanismes et la précision des joints prouvent que ce robot a été conçu avec amour.");
INSERT INTO `dbc_battle_pet_species` VALUES (1516, 85846, 132926, 171500, 4, 2, 2, "|cFFFFD200Évènement mondial :|r Bienfaits du pèlerin", "Introduits sur le continent par les gobelins, les poulets des fourrés sont devenus un mets incontournable de la fête des Bienfaits du pèlerin en raison de leur extrême succulence et de leur intellect atrophié.");
INSERT INTO `dbc_battle_pet_species` VALUES (1517, 85872, 519830, 171552, 6, 6, 2, "|cFFFFD200Évènement mondial :|r Fête du Feu du solstice d’été|n|cFFFFD200Vendeur : |rMarchande du solstice, Fournisseuse du solstice|n|cFFFFD200Coût : |r350|TINTERFACE\ICONS\INV_SummerFest_FireFlower:0|t", "Les exosquelettes carbonisés de minuscules insectes jonchent le sol de sa cage.");
INSERT INTO `dbc_battle_pet_species` VALUES (1518, 85994, 669468, 171758, 6, 6, 2, "|cFFFFD200Évènement mondial :|r Fête des Brasseurs|n|cFFFFD200Vendeur :|r Belbi Coupecircuit|n|cFFFFD200Région :|r Dun Morogh|n|cFFFFD200Coût :|r 200|TINTERFACE\ICONS\INV_Misc_Coin_01:0|t|n|n|cFFFFD200Vendeur :|r Bliz Réparouage|n|cFFFFD200Région :|r Durotar|n|cFFFFD200Coût :|r 200|TINTERFACE\ICONS\INV_Misc_Coin_01:0|t", "Les maîtres brasseurs pandarens ont accidentellement apporté un tonneau du dernier brassin animé de l’oncle Gao à la fête des Brasseurs.");
INSERT INTO `dbc_battle_pet_species` VALUES (1519, 86038, 970584, 0, 0, -1, 1331, "", "Un mouton se désaltérait dans le courant d\'une onde pure. Un loup survient à jeun qui cherchait aventure. Là-dessus, au fond des forêts, le mouton l\'emporte et puis lui PÈTE LA FIGURE. Parce qu\'il ne faut pas l\'embêter.");
INSERT INTO `dbc_battle_pet_species` VALUES (1521, 86061, 454045, 171912, 3, 6, 2, "|cFFFFD200Évènement mondial :|r Sanssaint|n|cFFFFD200Vendeur : |rPoupin|n|cFFFFD200Région : |rClairières de Tirisfal|n|cFFFFD200Coût : |r150|TINTERFACE\ICONS\achievement_halloween_candy_01:0|t|n|n|cFFFFD200Vendeur : |rDorothy|n|cFFFFD200Région : |rHurlevent|n|cFFFFD200Coût : |r150|TINTERFACE\ICONS\achievement_halloween_candy_01:0|t", "L’une des neuf vies de ce chat est retournée hanter les vivants en miaulant bruyamment quand ils essaient de dormir.");
INSERT INTO `dbc_battle_pet_species` VALUES (1523, 86067, 656575, 171915, 3, 6, 2, "|cFFFFD200Évènement mondial :|r Sanssaint|n|cFFFFD200Vendeur : |rPoupin|n|cFFFFD200Région : |rClairières de Tirisfal|n|cFFFFD200Coût : |r150|TINTERFACE\ICONS\achievement_halloween_candy_01:0|t|n|n|cFFFFD200Vendeur : |rDorothy|n|cFFFFD200Région : |rHurlevent|n|cFFFFD200Coût : |r150|TINTERFACE\ICONS\achievement_halloween_candy_01:0|t", "Même la mort ne saurait arrêter la curiosité insatiable de ce chaton.");
INSERT INTO `dbc_battle_pet_species` VALUES (1524, 86081, 136007, 170269, 5, 0, 2, "|cFFFFD200Butin :|r Rejeton du Néant|n|cFFFFD200Région :|r Nagrand|n", "Cette gelée est issue d’une longue et intrigante lignée.");
INSERT INTO `dbc_battle_pet_species` VALUES (1530, 86422, 133284, 170274, 3, 3, 2, "|cFFFFD200Métier :|r Archéologie", "Le petit loup devait rester coi.
INSERT INTO `dbc_battle_pet_species` VALUES (1531, 86420, 986483, 172632, 9, 3, 2, "|cFFFFD200Métier :|r Archéologie", "L’ancien gardien de nid a été conçu avec une idée insupportable en tête : la haine des chats.");
INSERT INTO `dbc_battle_pet_species` VALUES (1532, 86447, 132914, 170270, 2, 1, 2, "|cFFFFD200Quête :|r Œuf d’Ikky|n|cFFFFD200Région :|r Flèches d’Arak", "Les petits kaliris se nourrissent de larves quand ils sont jeunes, puis passent progressivement à tout ce qui est plus petit qu’eux.");
INSERT INTO `dbc_battle_pet_species` VALUES (1533, 86532, 462337, 172998, 1, 0, 2, "|cFFFFD200Butin :|r La lanticore|n|cFFFFD200Région :|r Sommet du pic Rochenoire", "Personne ne sait au juste dans quelle partie de la descente de l’Aile noire on gardait enfermée la lanticore, mais ça ne devait pas y sentir la rose.");
INSERT INTO `dbc_battle_pet_species` VALUES (1536, 86715, 460954, 173532, 2, -1, 34, "", "Sur Draenor, la mer de Zangar cherche à submerger la terre en lâchant des milliers de ces spores et en recouvrant lentement les terres intérieures.");
INSERT INTO `dbc_battle_pet_species` VALUES (1537, 86716, 463282, 173542, 2, 0, 2, "|cFFFFD200Butin :|r Spore étrange|n|cFFFFD200Région :|r Gorgrond|n", "Une spore prise dans le marais Rouge, une partie de Gorgrond engloutie quand Draenor a été déchirée par la magie du Néant.");
INSERT INTO `dbc_battle_pet_species` VALUES (1538, 86717, 463280, 173543, 2, 0, 2, "|cFFFFD200Butin :|r Spore étrange|n|cFFFFD200Région :|r Vallée d’Ombrelune|n", "L’umbretourbe au bleu profond est avec le temps devenue le foyer d’une tribu de Brisés.");
INSERT INTO `dbc_battle_pet_species` VALUES (1539, 86718, 463281, 173544, 2, -1, 34, "", "Une spore de Zangarra, point central de l’invasion de Zangar, qui fleurira plus tard pour donner naissance au marécage du même nom.");
INSERT INTO `dbc_battle_pet_species` VALUES (1540, 86719, 132851, 173547, 2, 0, 2, "|cFFFFD200Butin :|r Chapeau-Pourri|n|cFFFFD200Région :|r Flèches d’Arak", "Des spores brillantes, rarement vues et profondément enfouies sous les vagues de Draenor, que l’on trouve dans les recoins les plus sombres de l’invasion fongique de Zangar.");
INSERT INTO `dbc_battle_pet_species` VALUES (1541, 86879, 463493, 170273, 8, 0, 2, "|cFFFFD200Butin :|r Œuf de Varasha|n|cFFFFD200Région :|r Flèches d’Arak", "Mascotte parfois exubérante, Hydralon est capable de manger dans trois bols à la fois.");
INSERT INTO `dbc_battle_pet_species` VALUES (1542, 87111, 1044793, 170281, 7, 2, 2, "|cFFFFD200Vendeur :|r Beska Rouge-Défense|n|cFFFFD200Faction :|r Loups-de-Givre|n|n", "Ces petits sont des boules de fourrure et de dents jusqu’à ce qu’ils grandissent. Ensuite il n’y a plus que les dents.");
INSERT INTO `dbc_battle_pet_species` VALUES (1543, 87257, 136072, 177216, 4, 0, 18, "|cFFFFD200Butin :|r fiefs, grange de niveau 3", "Élevés à l’origine pour rendre le lait plus digeste pour les gnomes.");
INSERT INTO `dbc_battle_pet_species` VALUES (1544, 87669, 525026, 170268, 6, 0, 2, "|cFFFFD200Butin :|r Seigneur du Feu Ragnaros|n|cFFFFD200Région :|r Cœur du Magma (Édition anniversaire)", "Sa haine n’est en rien entamée par sa taille.");
INSERT INTO `dbc_battle_pet_species` VALUES (1545, 87704, 526578, 170291, 2, 1, 2, "|cFFFFD200Quête :|r Maîtriser la Ménagerie", "Ne tapissez pas sa cage de papier, sauf si vous avez besoin de feu.");
