INSERT INTO `dbc_battle_pet_species` VALUES (1563, 7546, 134156, 10699, 1, 0, 2, "|cFFFFD200Butin : |rHorde de Fer|n|cFFFFD200Région : |r Terres Foudroyées", "Le jeune dragonnet de bronze était perdu dans le temps jusqu’à ce qu’un événement inhabituel lui permette de s’échapper.");
INSERT INTO `dbc_battle_pet_species` VALUES (1564, 88103, 134205, 170272, 6, 0, 2, "|cFFFFD200Butin :|r Mandrakor|n|cFFFFD200Région :|r Gorgrond|n", "Une illustration limpide de la raison pour laquelle il faut utiliser l’engrais gobelin avec parcimonie.");
INSERT INTO `dbc_battle_pet_species` VALUES (1565, 88134, 986492, 170288, 9, 3, 2, "|cFFFFD200Métier : |rIngénierie", "Fer, fer, fournaise et acier. Engrenages, disques, câbles et soufflets.");
INSERT INTO `dbc_battle_pet_species` VALUES (1566, 88222, 667692, 176137, 2, 1, 2, "|cFFFFD200Quête :|r Pour les oiseaux", "Ces minuscules créatures semblent apprécier les grands nids douillets, surtout sur la tête d’une dryade amie des animaux.");
INSERT INTO `dbc_battle_pet_species` VALUES (1567, 88225, 133247, 176140, 2, 1, 2, "|cFFFFD200Quête :|r Petite sœur perdue|n", "Compagnon d’une Sentinelle elfe de la nuit, rarement offert aux étrangers.");
INSERT INTO `dbc_battle_pet_species` VALUES (1568, 88300, 893777, 170279, 8, 1, 2, "|cFFFFD200Quête :|r Maîtriser la Ménagerie", "Vivant aussi près de la surface de l’eau, la terreur des flaques n’est pas seulement un ami de toutes les créatures aquatiques : elle en fait partie.");
INSERT INTO `dbc_battle_pet_species` VALUES (1569, 84853, 1044088, 170276, 6, 3, 2, "|cFFFFD200Métier :|r Forge", "Lorsque l’on forge l’acier enchanté, des particules de magie se diffusent dans les braises. Après des années, voire des décennies, l’énergie libérée prend forme lentement, créant un minuscule élémentaire extrêmement brave.");
INSERT INTO `dbc_battle_pet_species` VALUES (1570, 88367, 132919, 177227, 2, 1, 2, "|cFFFFD200Quête :|r Maîtriser la Ménagerie", "La légende raconte que le kaliri feu-solaire plonge dans le soleil lorsqu’il est prêt à mourir.");
INSERT INTO `dbc_battle_pet_species` VALUES (1571, 85281, 1044482, 170287, 7, 2, 2, "|cFFFFD200Vendeur :|r Kwilax Krissmèche|n|cFFFFD200Faction :|r Société de « Conservation » de Gentepression - Révéré|n|cFFFFD200Coût : |r1000|TINTERFACE\MONEYFRAME\UI-GOLDICON.BLP:0|t", "Leur peau blanche les rendrait sensibles aux coups de soleil si elle n’était pas aussi épaisse qu’une armure de plaques.");
INSERT INTO `dbc_battle_pet_species` VALUES (1572, 88385, 667690, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes :|r Talador", "Le plumesang ne connaît pas la peur, il déploie ses ailes et charge sur toute menace apparente.");
INSERT INTO `dbc_battle_pet_species` VALUES (1573, 85798, 667690, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes :|r flèches d’Arak", "On dit que ses plumes brillent comme l’or quand elles sont illuminées par le soleil matinal.");
INSERT INTO `dbc_battle_pet_species` VALUES (1574, 88401, 1029738, 170277, 3, 2, 66, "|cFFFFD200Vendeur :|r Corborateur Skeega|n|cFFFFD200Région : |rFer-de-Lance|n|cFFFFD200Faction :|r Parias Arakkoa - Révéré|n|cFFFFD200Coût : |r1000|TINTERFACE\MONEYFRAME\UI-GOLDICON.BLP:0|t|n|n|cFFFFD200Vendeur : |rSage-ombre Brakoss|n|cFFFFD200Région :|r Bouclier-des-Tempêtes|n|cFFFFD200Faction : |rParias arakkoa - Révéré|n|cFFFFD200Coût : |r1000|TINTERFACE\MONEYFRAME\UI-GOLDICON.BLP:0|t", "Être divin, maintenant disponible en taille de poche.");
INSERT INTO `dbc_battle_pet_species` VALUES (1575, 88452, 132191, 170271, 2, 2, 2, "|cFFFFD200Vendeur : |rMarchand de réputation|n|cFFFFD200Réputation :|r Garde-ciel sha’tari", "Jouer à la baballe avec une raie est toujours amusant. Il n’y a pas besoin de viser.");
INSERT INTO `dbc_battle_pet_species` VALUES (1576, 88490, 236407, 170275, 5, 0, 2, "|cFFFFD200Butin :|r Orumo l’Observateur|n|cFFFFD200Région :|r Talador", "Vous avez toujours l’impression que… quelque chose vous observe.");
INSERT INTO `dbc_battle_pet_species` VALUES (1577, 88514, 1044499, 0, 7, -1, 58, "", "Quand ils sont petits et moins meurtriers, les ravageurs sont l’en-cas préféré des ogres.");
INSERT INTO `dbc_battle_pet_species` VALUES (1578, 88480, 132186, 0, 8, 4, 26, "|cFFFFD200Combat de mascottes :|r crête de Givrefeu", "Les pinceurs givre-coques accumulent des couches de glace pour se protéger des grands prédateurs.");
INSERT INTO `dbc_battle_pet_species` VALUES (1579, 88474, 132186, 0, 8, 4, 26, "|cFFFFD200Combat de mascottes :|r crête de Givrefeu", "Ses griffes étant capables de transpercer un gantelet en plaques, il vaut mieux le manipuler avec précaution.");
INSERT INTO `dbc_battle_pet_species` VALUES (1581, 88473, 132186, 0, 8, -1, 58, "", "Ces crustacés rusés s’enterrent sous la vase pour ne pas être enlevés par de plus grands prédateurs.");
INSERT INTO `dbc_battle_pet_species` VALUES (1582, 88466, 132186, 0, 8, 4, 26, "|cFFFFD200Combat de mascottes :|r vallée d’Ombrelune", "Les spores fongiques riches en nutriments dont ils se nourrissent donnent à la carapace de ces crabes sa couleur distinctive.");
INSERT INTO `dbc_battle_pet_species` VALUES (1583, 88465, 132186, 0, 8, 4, 26, "|cFFFFD200Combat de mascottes :|r Talador", "Ils se couvrent d’algues et autres végétaux marins pour avoir une pince d’avance sur leur proie.");
INSERT INTO `dbc_battle_pet_species` VALUES (1586, 88422, 236193, 0, 2, -1, 58, "", "Délicate et dangereuse, brillante et audacieuse, cette beauté plus que l’or est précieuse.");
INSERT INTO `dbc_battle_pet_species` VALUES (1587, 88417, 236193, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes :|r vallée d’Ombrelune", "Leurs ailes d’un violet profond sont très recherchées par les ogres-mages qui croient que les consommer sous forme de potions prolonge la vie.");
INSERT INTO `dbc_battle_pet_species` VALUES (1588, 88415, 236193, 0, 2, -1, 58, "", "Leurs ailes diffusent une fine poudre, mécanisme de défense face à un danger.");
INSERT INTO `dbc_battle_pet_species` VALUES (1589, 88413, 236193, 0, 2, -1, 58, "", "Délicate et dangereuse, brillante et audacieuse, cette beauté plus que son poids en or est précieuse. Dommage qu’elle soit si légère.");
INSERT INTO `dbc_battle_pet_species` VALUES (1590, 88359, 236196, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes :|r flèches d’Arak", "Les érudits débattent tant et plus sur le mécanisme qui fait briller les lucioles, mais l’hypothèse majeure reste : « c’est de la magie ».");
INSERT INTO `dbc_battle_pet_species` VALUES (1591, 88357, 236196, 0, 2, -1, 58, "", "La lueur violette de ces lucioles vient de leur régime de nectar et fleurs violettes uniquement.");
INSERT INTO `dbc_battle_pet_species` VALUES (1592, 88356, 236196, 0, 2, -1, 58, "", "Baptisées parfois « gemmes volantes » du fait de leur éclat rappelant celui des saphirs les plus purs.");
INSERT INTO `dbc_battle_pet_species` VALUES (1593, 88355, 236196, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes :|r vallée d’Ombrelune, Talador", "L’eauciole se sert de la surface de l’onde réfléchissant la lueur de son abdomen pour attirer ses partenaires.");
INSERT INTO `dbc_battle_pet_species` VALUES (1594, 88571, 979220, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes :|r Gorgrond", "Étonnamment vif quand on le provoque, il se calme aussitôt quand on lui présente un picotin de sagittaire de Nagrand.");
INSERT INTO `dbc_battle_pet_species` VALUES (1595, 88572, 1044481, 0, 7, 4, 26, "|cFFFFD200Combat de mascottes :|r Talador", "Étonnamment vif quand on le provoque, il se calme aussitôt quand on lui présente un picotin de sagittaire de Nagrand.");
INSERT INTO `dbc_battle_pet_species` VALUES (1596, 88573, 132914, 170283, 2, 2, 2, "|cFFFFD200Vendeur :|r Vesharr|n|cFFFFD200Région :|r Flèches d’Arak|n|cFFFFD200Coût :|r 1 000|TINTERFACE\MONEYFRAME\UI-GOLDICON.BLP:0|t", "Chassés pour leurs plumes jusqu’à leur quasi-extinction, les kaliris ont développé des sens aiguisés.");
INSERT INTO `dbc_battle_pet_species` VALUES (1597, 88574, 132914, 177215, 2, 2, 2, "|cFFFFD200Vendeur :|r Vesharr|n|cFFFFD200Région :|r Flèches d’Arak|n|cFFFFD200Coût :|r 10|TINTERFACE\MONEYFRAME\UI-GOLDICON.BLP:0|t", "Chassés pour leurs plumes jusqu’à leur quasi-extinction, les kaliris ont développé des sens aiguisés.");
INSERT INTO `dbc_battle_pet_species` VALUES (1598, 88575, 132197, 0, 2, -1, 58, "", "Les sporoptères sauvages sont des créatures féroces. Mais, domestiqués jeunes, ils peuvent devenir des compagnons fidèles.");
INSERT INTO `dbc_battle_pet_species` VALUES (1599, 88576, 132197, 0, 2, 4, 26, "|cFFFFD200Combat de mascottes :|r Talador", "Les sporoptères sauvages sont des créatures féroces. Mais, domestiqués jeunes, ils peuvent devenir des compagnons fidèles.");
INSERT INTO `dbc_battle_pet_species` VALUES (1600, 88577, 970830, 177238, 3, 4, 2, "|cFFFFD200Combat de mascottes :|r Erris la Collectionneuse, Kura Sabot-Tonnerre", "Les jeunes serpents d’os essaieront de s’enfouir dans le crâne de créatures vivantes pour leur dévorer le cerveau. Mieux vaut se couvrir la tête.");
INSERT INTO `dbc_battle_pet_species` VALUES (1601, 88692, 136221, 170278, 5, 0, 2, "|cFFFFD200Butin :|r Demidos|n|cFFFFD200Région :|r Vallée d’Ombrelune", "Demidos a créé de nombreux serviteurs quand il organisait des fêtes pour les autres seigneurs du Néant.");
INSERT INTO `dbc_battle_pet_species` VALUES (1602, 88805, 1044791, 177234, 0, 7, 82, "|cFFFFD200Promotion :|r BlizzCon 2014", "Même s’il en réclame en hurlant, ne jamais lui donner de sang de démon.");
INSERT INTO `dbc_battle_pet_species` VALUES (1603, 88807, 1044790, 177232, 7, 9, 82, "|cFFFFD200Boutique en jeu|r", "Souvent surprises en train de mâchonner tout et n’importe quoi, les chèvres ont tout d’abord été tenues responsables du crash de l’Exodar.");
