INSERT INTO `dbc_battle_pet_species` VALUES (1622, 90200, 458967, 179811, 3, 0, 2, "|cFFFFD200Butin : |rAzgalor|n|cFFFFD200Région : |rSommet d’Hyjal", "Gargouille de taille réduite, utilisée pour rediriger les écoulements d’eau de pluie sur les bâtiments du Fléau, afin d’empêcher l’inondation des mausolées.");
INSERT INTO `dbc_battle_pet_species` VALUES (1623, 90201, 1100171, 179830, 8, 0, 2, "|cFFFFD200Butin : |rGrand seigneur de guerre Naj’entus|n|cFFFFD200Région : |rTemple Noir", "Les eaux sombres et verdâtres des égouts du temple Noir donnent à ces hydres leur couleur caractéristique.");
INSERT INTO `dbc_battle_pet_species` VALUES (1624, 90202, 1100169, 179831, 5, 0, 2, "|cFFFFD200Butin : |rSupremus|n|cFFFFD200Région : |rTemple Noir", "Fragment du grand abyssal Supremus, Abyssius renferme une essence enflammée assez puissante pour faire un trou dans votre poche.");
