INSERT INTO `dbc_battle_pet_species` VALUES (1628, 90206, 136220, 179835, 0, 0, 2, "|cFFFFD200Butin : |rMère Shahraz|n|cFFFFD200Région : |rTemple Noir", "C’est la voix du démon dans votre tête.");
INSERT INTO `dbc_battle_pet_species` VALUES (1629, 90207, 1100170, 179836, 3, 0, 2, "|cFFFFD200Butin : |rAnetheron|n|cFFFFD200Région : |rSommet d’Hyjal", "Beuh… C’est quoi cette odeur ?");
