INSERT INTO `dbc_battle_pet_species` VALUES (1632, 90212, 1100176, 179838, 9, 0, 2, "|cFFFFD200Butin : |rBrutallus|n|cFFFFD200Région : |rPlateau du Puits de soleil", "Vous ne voulez pas savoir ce qui se passe quand un rat s’aventure dans le Puits de soleil.");
INSERT INTO `dbc_battle_pet_species` VALUES (1633, 90213, 1100177, 179839, 5, 0, 2, "|cFFFFD200Butin : |rM’uru|n|cFFFFD200Région : |rPlateau du Puits de soleil", "Le Néant distordu n’est que terreurs abyssales, à l’exception d’un petit coin de mignonnitude absolue.");
INSERT INTO `dbc_battle_pet_species` VALUES (1634, 90214, 1100179, 179840, 0, 0, 2, "|cFFFFD200Butin : |rJumelles érédars|n|cFFFFD200Région : |rPlateau du Puits de soleil", "Les elfes drogués à la magie, trop frêles pour servir dans l’armée de Kil’jaeden, étaient relégués au rôle de serviteurs pour les démons supérieurs.");
INSERT INTO `dbc_battle_pet_species` VALUES (1635, 90215, 1100172, 179841, 5, 5, 210, "|cFFFFD200Haut fait : |rRaid de mascottes II : Puisons dans le Puits de soleil|n|cFFFFD200Catégorie : |rCollections", "Quand cette mascotte est dans les environs, des carillons apaisants résonnent dans votre esprit et vous rappellent une époque lointaine.");
