INSERT INTO `creature_text` (`entry`,`groupid`,`id`,`text`,`type`,`language`,`probability`,`emote`,`duration`,`sound`,`comment`) VALUES ("77704","0","0","Champions! Nyami's agents - they went this way!","14","0","100","0","0","0","");
INSERT INTO `creature_text` (`entry`,`groupid`,`id`,`text`,`type`,`language`,`probability`,`emote`,`duration`,`sound`,`comment`) VALUES ("77704","0","1","No... Oh no...","14","0","100","0","0","0","");
INSERT INTO `creature_text` (`entry`,`groupid`,`id`,`text`,`type`,`language`,`probability`,`emote`,`duration`,`sound`,`comment`) VALUES ("77810","22","0","Your efforts were valiant, I did not think the Encorai could powered the defences with only one of the Crystals.","14","0","100","0","0","43639","");
INSERT INTO `creature_text` (`entry`,`groupid`,`id`,`text`,`type`,`language`,`probability`,`emote`,`duration`,`sound`,`comment`) VALUES ("77810","23","0","But nothing will stop my plan, nor that of my master's.. and the only thing that's on our way..","14","0","100","0","0","43640","");
INSERT INTO `creature_text` (`entry`,`groupid`,`id`,`text`,`type`,`language`,`probability`,`emote`,`duration`,`sound`,`comment`) VALUES ("77810","24","0","Are YOU Tuulani, and your outsider friends!","14","0","100","0","0","43641","");
INSERT INTO `creature_text` (`entry`,`groupid`,`id`,`text`,`type`,`language`,`probability`,`emote`,`duration`,`sound`,`comment`) VALUES ("77810","25","0","Try as you might, this place is doomed.. it is only a matter of time.","14","0","100","0","0","43642","");
INSERT INTO `creature_text` (`entry`,`groupid`,`id`,`text`,`type`,`language`,`probability`,`emote`,`duration`,`sound`,`comment`) VALUES ("79248","5","0","The Vindicators and Exrachs of the past. Heroes all - their souls still remain with us here.","12","0","100","0","0","44658","");
INSERT INTO `creature_text` (`entry`,`groupid`,`id`,`text`,`type`,`language`,`probability`,`emote`,`duration`,`sound`,`comment`) VALUES ("79248","6","0","Nyami! We are here -","12","0","100","0","0","44659","");
INSERT INTO `creature_text` (`entry`,`groupid`,`id`,`text`,`type`,`language`,`probability`,`emote`,`duration`,`sound`,`comment`) VALUES ("79248","7","0","What is this? Oh.. no.. no!","12","0","100","0","0","44660","");
INSERT INTO `creature_text` (`entry`,`groupid`,`id`,`text`,`type`,`language`,`probability`,`emote`,`duration`,`sound`,`comment`) VALUES ("79248","8","0","You have to stop them!","12","0","100","0","0","44661","");
INSERT INTO `creature_text` (`entry`,`groupid`,`id`,`text`,`type`,`language`,`probability`,`emote`,`duration`,`sound`,`comment`) VALUES ("79248","9","0","The protector still answers her call, you'll have to defeat it!","12","0","100","0","0","44645","");
INSERT INTO `creature_text` (`entry`,`groupid`,`id`,`text`,`type`,`language`,`probability`,`emote`,`duration`,`sound`,`comment`) VALUES ("77810","21","0","Yes Tuulani, now you see.","14","0","100","0","0","43638","");
INSERT INTO `creature_text` (`entry`,`groupid`,`id`,`text`,`type`,`language`,`probability`,`emote`,`duration`,`sound`,`comment`) VALUES ("79248","17","0","You have done more then you know, our people can never thank you enough.","12","0","100","0","0","44653","");
INSERT INTO `creature_text` (`entry`,`groupid`,`id`,`text`,`type`,`language`,`probability`,`emote`,`duration`,`sound`,`comment`) VALUES ("79248","16","0","Now we can rebuild, we must rebuild. We'll never allow our souls to fall to the legion.","12","0","100","0","0","44652","");
INSERT INTO `creature_text` (`entry`,`groupid`,`id`,`text`,`type`,`language`,`probability`,`emote`,`duration`,`sound`,`comment`) VALUES ("79248","15","0","Here.. this is not enough to thank you for your actions, but it is what we have to give.","12","0","100","0","0","44651","");
INSERT INTO `creature_text` (`entry`,`groupid`,`id`,`text`,`type`,`language`,`probability`,`emote`,`duration`,`sound`,`comment`) VALUES ("79248","14","0","You.. you did it!","12","0","100","0","0","44650","");
INSERT INTO `creature_text` (`entry`,`groupid`,`id`,`text`,`type`,`language`,`probability`,`emote`,`duration`,`sound`,`comment`) VALUES ("79248","13","0","I will gather any who remain loyal here, but you.. please.. go stop this madness","12","0","100","0","0","44649","");
INSERT INTO `creature_text` (`entry`,`groupid`,`id`,`text`,`type`,`language`,`probability`,`emote`,`duration`,`sound`,`comment`) VALUES ("79248","11","0","She got away.. the defences!","12","0","100","0","0","44647","");
INSERT INTO `creature_text` (`entry`,`groupid`,`id`,`text`,`type`,`language`,`probability`,`emote`,`duration`,`sound`,`comment`) VALUES ("79248","12","0","She had help.. so many of our order were corrupt!","12","0","100","0","0","44648","");
INSERT INTO `creature_text` (`entry`,`groupid`,`id`,`text`,`type`,`language`,`probability`,`emote`,`duration`,`sound`,`comment`) VALUES ("79248","18","0","Let us proceed","12","0","100","0","0","44657","");
INSERT INTO `creature_text` (`entry`,`groupid`,`id`,`text`,`type`,`language`,`probability`,`emote`,`duration`,`sound`,`comment`) VALUES ("77693","3","0","Nyami awaits you in the western ritual chamber, champions.","12","0","100","0","0","0","");
INSERT INTO `creature_text` (`entry`,`groupid`,`id`,`text`,`type`,`language`,`probability`,`emote`,`duration`,`sound`,`comment`) VALUES ("79248","4","0","Strange... why the door is sealed? No matter...","12","0","100","0","0","44656","");
INSERT INTO `creature_text` (`entry`,`groupid`,`id`,`text`,`type`,`language`,`probability`,`emote`,`duration`,`sound`,`comment`) VALUES ("79248","0","0","Good! We must have arrived before Teron'gor and Gul'den!","12","0","100","0","0","44644","");
INSERT INTO `creature_text` (`entry`,`groupid`,`id`,`text`,`type`,`language`,`probability`,`emote`,`duration`,`sound`,`comment`) VALUES ("79248","1","0","Nyami is working to bolster the defenses against their assault - follow me,we will report to her.","12","0","100","0","0","44654","");
INSERT INTO `creature_text` (`entry`,`groupid`,`id`,`text`,`type`,`language`,`probability`,`emote`,`duration`,`sound`,`comment`) VALUES ("79248","2","0","Beautiful, isn't it? Just think - you're one of the first outsiders to see inside the Auchindoum.","12","0","100","0","0","44655","");
INSERT INTO `creature_text` (`entry`,`groupid`,`id`,`text`,`type`,`language`,`probability`,`emote`,`duration`,`sound`,`comment`) VALUES ("77810","26","0","Yes... yes! the Crystal harms, just a bit of focus and..","14","0","100","0","0","43643","");
