INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76806, 0, 0, "Je suis liiiiiibre !", "Endlich frei!", "¡Libertaaaaad!", "Свобо-о-о-о-ода!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76806, 1, 0, "Je vous réduirai tous en cendres !", "Ich werde Euch alle einäschern!", "¡Los haré a todos cenizas!", "Я обращу вас в пепел!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76806, 1, 1, "Mes flammes dévoreront vos chairs !", "Mein Feuer wird das Fleisch von Euren Knochen brennen!", "¡Mis llamas lamerán la carne de sus huesos!", "Мой огонь обглодает плоть с ваших костей!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76806, 2, 0, "SOUFFREZ !", "LEIDET!", "¡SUFRAN!", "СТРАДАЙТЕ!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76806, 3, 0, "Que le feu vous engloutisse !", "Ertrinkt in den Flammen!", "¡Que el fuego los asfixie!", "Горите!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76806, 4, 0, "Je suis le feu !", "Ich verschlinge alles!", "¡Consumiré todo!", "Я поглощу все!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76806, 4, 1, "À mort !", "Sterbt!", "¡Mueran!", "Умри!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76806, 4, 2, "Brûlez !", "Brennt!", "¡Ardan!", "Горите!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76806, 5, 0, "Je ferai de cette montagne un tas de cendres !", "Ich lege diesen Berg in Schutt und Asche!", "¡Quemaré esta montaña hasta las cenizas!", "Я спалю эту гору дотла!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76806, 6, 0, "Mes flammes... Elles meurent...", "Mein Feuer wurde... ausgelöscht.", "Mi fuego está... extin... guido.", "Мой огонь... угас.");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76806, 7, 0, "|TInterface\Icons\spell_shaman_improvedfirenova.blp:20|tLe Cœur de la montagne est sur le point de libérer une |cFFFF0000|Hspell:155209|h[Explosion]|h|r d'intense chaleur !", "|TInterface\Icons\spell_shaman_improvedfirenova.blp:20|tDas Herz des Berges wird gleich eine |cFFFF0000|Hspell:155209|h[Flammenzunge]|h|r gewaltiger Hitze entfesseln!", "|TInterface\Icons\spell_shaman_improvedfirenova.blp:20|t¡El Corazón de la Montaña está a punto de liberar un |cFFFF0000|Hspell:155209|h[Estallido]|h|r de calor intenso!", "|TInterface\Icons\spell_shaman_improvedfirenova.blp:20|tСердце Горы вот-вот устроит |cFFFF0000|Hspell:155209|h[Взрыв]|h|r нестерпимого жара!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76806, 8, 0, "|TInterface\Icons\spell_sandexplosion.blp:20|tVous portez une |cFFFF0000|Hspell:155187|h[Bombe]|h|r !", "|TInterface\Icons\spell_sandexplosion.blp:20|tIhr tragt eine |cFFFF0000|Hspell:155187|h[Bombe]|h|r!", "|TInterface\Icons\spell_sandexplosion.blp:20|t¡Tienes una |cFFFF0000|Hspell:155187|h[Bomba]|h|r sobre ti!", "|TInterface\Icons\spell_sandexplosion.blp:20|tНа вас наложили заклинание |cFFFF0000|Hspell:155187|h[Бомба]|h|r!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76806, 9, 0, "|TInterface\Icons\Spell_Fire_LavaSpawn.blp:20|tUn mandefeu vous a lancé |cFFFF0000|Hspell:176121|h[Feu volatil]|h|r !", "|TInterface\Icons\Spell_Fire_LavaSpawn.blp:20|tEin Feuerrufer hat Euch mit |cFFFF0000|Hspell:176121|h[flüchtigem Feuer]|h|r belegt!", "|TInterface\Icons\Spell_Fire_LavaSpawn.blp:20|t¡Un clamafuegos te afectó con |cFFFF0000|Hspell:176121|h[Fuego volátil]|h|r!", "|TInterface\Icons\Spell_Fire_LavaSpawn.blp:20|tПризыватель Огня наложил на вас заклинание |cFFFF0000|Hspell:176121|h[Неустойчивый огонь]|h|r!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76806, 10, 0, "|TInterface\Icons\ability_foundryraid_melt.blp:20|tLe Cœur de la montagne va |cFFFF0000|Hspell:155225|h[Fondre]|h|r le sol sous vos pieds !", "|TInterface\Icons\ability_foundryraid_melt.blp:20|tDas Herz des Berges wird den Boden unter Euren Füßen |cFFFF0000|Hspell:155225|h[schmelzen]|h|r!", "|TInterface\Icons\ability_foundryraid_melt.blp:20|t¡El Corazón de la Montaña va a |cFFFF0000|Hspell:155225|h[Fundir]|h|r el suelo bajo tus pies!", "|TInterface\Icons\ability_foundryraid_melt.blp:20|tСердце Горы собирается применить |cFFFF0000|Hspell:155225|h[Растапливание]|h|r к земле под вашими ногами!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76809, 0, 0, "Protégez le haut fourneau à tout prix !", "Schützt den Ofen um jeden Preis!", "¡Protejan el horno a toda costa!", "Защищайте горнило любой ценой!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76809, 1, 0, "Actionnez-moi ces soufflets !", "An die Blasebälge!", "¡Pongan a funcionar esos fuelles!", "Раздуть меха!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76809, 1, 1, "Faites grimper la température, les gars, ça les réchauffera !", "Macht ihnen Feuer unterm Hintern!", "¡Aumenten el calor! ¡Sudarán hasta el fin!", "Поддайте жару, парни, пусть попотеют!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76809, 2, 0, "Appelez des renforts !", "Ruft Verstärkung herbei!", "¡Llamen a más guardias!", "Где вся охрана?!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76809, 3, 0, "Je vais vous écrabouiller !", "Ich werd Euch zermalmen!", "¡Los aplastaré!", "Раздавлю!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76809, 3, 1, "Meurs, vermine !", "Sterbt, dreckiges Pack!", "¡Muere, basura!", "Умрите!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76809, 4, 0, "Ridicule !", "Schwach!", "¡Débil!", "Кишка тонка!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76809, 4, 1, "Pitoyable !", "Erbärmlich!", "¡Patético!", "Ничтожество!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76809, 5, 0, "Vous avez chaud ? Alors sortez !", "Zu heiß für Euch? Dann spielt nicht mit dem Feuer!", "¿No soportan el calor? ¡Salgan del horno!", "Слишком жарко? Убирайтесь из горнила!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76809, 6, 0, "arrr... aaaaggg...", "Aaaahhrrrg...", "ooohhh... aaagghhh...", "А-а-а-а... а-а-а-х-х-х...");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76809, 7, 0, "|TInterface\Icons\spell_shaman_improvedfirenova.blp:20|tLa fréquence des |cFFFF0000|Hspell:155209|h[Explosions]|h|r du Cœur de la montagne augmente !", "|TInterface\Icons\spell_shaman_improvedfirenova.blp:20|tDie Frequenz der |cFFFF0000|Hspell:155209|h[Flammenzungen]|h|r des Herzens des Berges wurde erhöht!", "|TInterface\Icons\spell_shaman_improvedfirenova.blp:20|t¡Aumentó la frecuencia de |cFFFF0000|Hspell:155209|h[Reventar]|h|r del Corazón de la Montaña!", "|TInterface\Icons\spell_shaman_improvedfirenova.blp:20|tЧастота |cFFFF0000|Hspell:155209|h[взрывов]|h|r Сердца Горы увеличилась!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76809, 8, 0, "|TInterface\Icons\ability_rhyolith_magmaflow_whole.blp:20|tLe contremaître Feldspath s'apprête à créer une |cFFFF0000|Hspell:156934|h[Rupture]|h|r sous vos pieds !", "|TInterface\Icons\ability_rhyolith_magmaflow_whole.blp:20|tVorarbeiter Feldspat wird einen |cFFFF0000|Hspell:156934|h[Bodenriss]|h|r unter Euch erzeugen!", "|TInterface\Icons\ability_rhyolith_magmaflow_whole.blp:20|t¡El supervisor Feldespato va a crear una |cFFFF0000|Hspell:156934|h[Ruptura]|h|r bajo tus pies!", "|TInterface\Icons\ability_rhyolith_magmaflow_whole.blp:20|tШтейгер Полевой Шпат создает |cFFFF0000|Hspell:156934|h[Разлом]|h|r у вас под ногами!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76831, 0, 0, "Gardes ! Des intrus envahissent le haut fourneau !", "Wachen! Eindringlinge machen sich am Schmelzofen zu schaffen!", "¡Guardias! ¡Los intrusos están interfiriendo con el Alto horno!", "Стража! Чужаки вторглись в Горнило!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76831, 1, 0, "Un régulateur de pression a été détruit. Il n'en reste plus qu'un.", "Ein Hitzeregler wurde zerstört, ein weiterer ist noch da.", "Se destruyó un regulador de presión. Falta otro.", "Один регулятор давления уничтожен, остался еще один.");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76831, 2, 0, "La machinerie est endommagée. Empêchez cette vermine de saboter le fourneau !", "Der Mechanismus ist beschädigt. Lasst diesen Abschaum nicht den Ofen sabotieren!", "La maquinaria está dañada. ¡Eviten que esos malditos saboteen el horno!", "Оборудование повреждено. Не дайте им вывести из строя Горнило!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76831, 3, 0, "Les deux régulateurs de chaleur ont été détruits. Le Cœur de la montagne a été libéré !", "Beide Hitzeregler wurden zerstört. Das Herz des Berges ist freigesetzt!", "Se destruyeron ambos reguladores de calor. ¡Se desató el Corazón de la Montaña!", "Оба регулятора температуры уничтожены. Сердце Горы на свободе!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76831, 4, 0, "La fournaise est en surchauffe ! Imbéciles ! Si la fureur s'échappe, tout notre travail sera anéanti !", "Der Schmelzofen überhitzt! Ihr Narren! Wenn der Zorn ausbricht, macht er alles zunichte!", "¡El horno se está sobrecalentando! Tontos, si la furia es liberada, ¡nuestro trabajo se habrá arruinado!", "Горнило перегревается! Если Гнев вырвется на свободу, все пропало!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76831, 5, 0, "Triomphez des élémentalistes primordiaux pour libérer le Cœur de la montagne de ses entraves.", "Besiegt die Urelementaristen, um das Herz des Berges von seinen Fesseln zu befreien.", "Derrota a los elementalistas primigenios para liberar al Corazón de la Montaña de sus ataduras.", "Расправьтесь с повелительницами изначальных стихий, чтобы высвободить Сердце Горы.");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76831, 6, 0, "Il reste trois élémentalistes primordiaux.", "Noch drei Urelementaristen.", "Faltan tres elementalistas primigenios.", "Осталось три повелительницы изначальных стихий.");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76831, 7, 0, "Il reste deux élémentalistes primordiaux.", "Noch zwei Urelementaristen.", "Faltan dos elementalistas primigenios.", "Осталось две повелительницы изначальных стихий.");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76831, 8, 0, "Il reste un élémentaliste primordial.", "Noch ein Urelementarist.", "Falta una elementalista primigenia.", "Осталась одна повелительница изначальных стихий.");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76831, 9, 0, "Tous les élémentalistes primordiaux ont été vaincus. Le Cœur de la montagne n'est plus entravé !", "Alle Urelementaristen wurden besiegt. Das Herz des Berges ist frei!", "Derrotaste a todos los elementalistas primigenios. ¡El Corazón de la Montaña es libre!", "Все повелительницы изначальных стихий повержены, Сердце Горы на свободе.");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76831, 10, 0, "Savourez cette petite victoire, maintenant vous allez périr dans les flammes qui rugissent dans le cœur de Rochenoire !", "Ein unbedeutender Sieg. Denn jetzt werdet Ihr in den Flammen im Herzen des Schwarzfels aufgehen!", "Una pequeña victoria, ¡pero ahora perecerás en la llama que arde en el corazón de Roca Negra!", "Скромная победа... а теперь сгорите в пламени сердца Черной горы!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76831, 11, 0, "Qu'est-il arrivé à mon fourneau ? Où sont mes gardes ? Débarrassez-moi de ces intrus !", "Was ist mit meiner Gießerei passiert? Wo sind die Wachen? Schiebt diesen Eindringlingen einen Riegel vor!", "¿Qué le ha pasado a mi horno? ¿Dónde están mis guardias? ¡Encierren a estos intrusos!", "Что случилось с моим горнилом? Где стража? Убейте этих чужаков!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76877, 0, 0, "Gruul sentir chair fraîche...", "Gruul riechen Fleisch...", "Gruul huele carne...", "Груул чует плоть...");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76877, 1, 0, "Ha ha ha ha ! Tuer vous, facile pour Gruul !", "Hahahaha, Gruul euch ganz leicht töten!", "Ja, ja, ja, ¡Gruul matarlos muy fácil!", "Ха-ха-ха, Груул вас убивать!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76877, 2, 0, "Pas bouger !", "Ihr bleiben...", "Ustedes quedarse...", "Постойте тут...");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76877, 2, 1, "Fuir, pas possible !", "Keiner entkommen!", "¡No escaparse!", "Выхода нет!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76877, 2, 2, "Courir...", "Husch...", "Corran...", "Прочь...");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76877, 3, 0, "GRUUL TAPER !", "GRUUL MACHT PLATT!", "¡GRUUL MACHACA!", "ГРУУЛ БИТЬ!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76877, 4, 0, "GRUUL ÉCRASER VOUS MAINTENANT !", "GRUUL ZERMATSCHEN JETZT!", "¡¡¡GRUUL MACHACARLOS AHORA!!!", "ГРУУЛ КРУШИТЬ!!!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76877, 5, 0, "Indigne.", "Unwürdig.", "Indigno.", "Ничтожество.");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76877, 5, 1, "Disparu !", "Weg!", "No más.", "Хватит.");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76877, 6, 0, "Gruul... tombé ?", "Gruul... fällt?", "Gruul... ¿caer?", "Груул... упал?");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76877, 7, 0, "%s déclenche un |cFFFF0404|Hspell:155539|h[Saccage destructeur]|h|r !", "%s steigert sich in einen |cFFFF0404|Hspell:155539|h[zerstörerischen Amoklauf]|h|r!", "¡%s entra en modo |cFFFF0404|Hspell:155539|h[Desenfreno destructivo]|h|r!", "%s впадает в |cFFFF0404|Hspell:155539|h[Разрушительное буйство]|h|r!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76877, 8, 0, "Le |cFFFF0404|Hspell:155539|h[Saccage destructeur]|h|r |2 %s prend fin.", "Der |cFFFF0404|Hspell:155539|h[zerstörerische Amoklauf]|h|r von %s endet.", "El |cFFFF0404|Hspell:155539|h[Desenfreno destructivo]|h|r de %s acaba.", "|cFFFF0404|Hspell:155539|h[Разрушительное буйство]|h|r |3-1(%s) заканчивается.");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (77182, 0, 0, "|Hunit:77182:Oregorger|hMangeroc se prépare à lancer |cFFFF0000|Hspell:156879|h[Barrage rochenoire]|h|r.", "|Hunit:77182:Oregorger|hErzfresser bereitet sich darauf vor, |cFFFF0000|Hspell:156879|h[Schwarzfelssperrfeuer]|h|r zu wirken!", "|Hunit:77182:Oregorger|hZampamenas se prepara para lanzar |cFFFF0000|Hspell:156879|h[Tromba de Roca Negra]|h|r.", "|Hunit:77182:Oregorger|hРудожуй готовится применить |cFFFF0000|Hspell:156879|h[Шквал Черной горы]|h|r.");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (77182, 1, 0, "Mangeroc a faim et veut manger plus de minerai rochenoire !", "Erzfresser ist jetzt hungrig und will mehr Schwarzfelserz fressen!", "¡Zampamenas tiene hambre y quiere comer más mena de Roca Negra!", "Рудожуй проголодался и хочет еще руды Черной горы!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (77182, 2, 0, "Mangeroc est rempli de minerai rochenoire !", "Erzfresser ist voller Schwarzfelserz!", "¡Zampamenas está lleno de mena de Roca Negra!", "Рудожуй наелся руды Черной горы!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (77504, 0, 0, "%s incante une |cFFFF0000|Hspell:156446|h[Vague Explosive]|h|r ! Cachez-vous !", "%s entfesstelt eine |cFFFF0000|Hspell:156446|h[Druckwelle]|h|r! In Deckung!", "¡%s está lanzando una |cFFFF0000|Hspell:156446|h[Ola Explosiva]|h|r! ¡Escondéos!", "%s использует |cFFFF0000|Hspell:156446|h[Взрывную волну]|h|r! Прячься!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (87771, 0, 0, "Bouffe-le ! Bouffe-le !", "Beißt ihn! Beißt ihn!", "¡Muérdelo! ¡Muérdelo!", "Кусай! Кусай!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (87771, 0, 1, "Allez, la brute !", "Na los, Schläger!", "¡Vamos bruto!", "Давай же, громила!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (87771, 0, 2, "Hé ! C'est pas réglo, comme manière de se battre, ça !", "He! Das ist kein fairer Kampf!", "¡Oye! ¡Ese no pelear limpio!", "Эй, так нечестно!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (87771, 0, 3, "J'ai parié de l'or sur le gros balaise !", "Ich habe Gold auf den Großen gesetzt!", "¡Yo le aposte al grandulón!", "Я поставил на того здоровяка!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (87771, 0, 4, "OUILLE ! Ça devait faire mal !", "AUTSCH! Das hat bestimmt wehgetan!", "¡AUCH! ¡Eso debió doler!", "АЙ! Это, наверное, больно!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76815, 0, 0, "La Fureur est sortie !", "Der Zorn ist ausgebrochen!", "¡La Furia se ha liberado!", "Гнев на свободе!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76815, 1, 0, "Vite, elle ne doit pas nous échapper !", "Schnell, bringt ihn unter Kontrolle!", "¡Rápido, tómenla bajo su control!", "Скорее подчините его!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76815, 2, 0, "Seigneur de guerre, tout est sous contrôle.", "Kriegsherr, wir haben ihn unter Kontrolle.", "Señor de la Guerra, lo tenemos bajo control.", "Вождь, у нас все под контролем.");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (76815, 3, 0, "Elle... s'est libérée. Nous avons échoué...", "Er... er ist frei. Kriegsherr, wir haben versagt...", "Está... libre. Señor de la Guerra, hemos fracasado...", "Он... вырвался. Мы подвели тебя...");
