INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (80599, 0, 0, "Vous ne comprenez pas ! Ce rituel ne peut pas...", "Ihr versteht nicht! Er darf nicht...!", "¡No lo entiendes! ¡Este no debe escapar!", "Вы не понимаете! Так не должно...");
