INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (80599, 0, 2, "Oui, Tectus. Soumets-toi... à la volonté... de notre maître...", "Ja, Tectus. Beug dich... dem Willen... des Meisters...", "Sí, Tectus. Responde... a la voluntad... de nuestro maestro...", "Тектоник... Склонись перед волей... хозяина");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (80822, 0, 0, "Kral'ach... Les ténèbres parlent... D'UNE VOIX !", "Kral'ach... die Finsternis spricht... EINE STIMME!", "Kral'ach... la oscuridad habla... ¡UNA VOZ!", "Крал'арх... Тьма взывает... Голос!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (80822, 0, 1, "MAÎTRE ! POUR VOUS SERVIR !", "MEISTER! EMPFANGT MICH!", "¡MAESTRO! ¡VENGO POR TI!", "ХОЗЯИН! Я ИДУ К ТЕБЕ!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (79015, 0, 0, "Vous osez pénétrer en ces lieux ?", "Ihr wagt es, diese Hallen zu betreten?", "¿Osan entrar a estos salones? Los Rompevileza nunca han caído en batalla.", "Кто посмел войти в эти залы? Скверноломов никому не победить!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (79015, 1, 0, "Votre magie est impuissante ici.", "Eure Magie ist hier machtlos.", "Su magia es inútil aquí.", "Ваша магия здесь бессильна.");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (79015, 2, 0, "VOUS PAIEREZ POUR ÇA !!", "Dafür werdet Ihr ZAHLEN!", "¡¡¡Van a PAGAR POR ESTO!!!", "Вы заплатите за это!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (79015, 2, 1, "AHHHHHH !", "AHHHHH!", "¡AHHHHHH!", "А-а-а-а-а!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (79015, 3, 0, "Vos attaques sont faibles !", "Eure Angriffe sind jämmerlich.", "¡Sus ataques son débiles!", "Какие же вы слабые!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (79015, 3, 1, "Rien ne peut m'arrêter.", "Nichts kann mich aufhalten.", "Soy imparable.", "Меня не остановить.");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (79015, 3, 2, "Je vais vous briser.", "Zerbrochen.", "Roto.", "Сломаю.");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (79015, 3, 3, "Sortez !", "Verschwindet!", "¡Largo!", "Прочь!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (79015, 4, 0, "Je vous réduirai en pièces !", "Ich reiße Euch in Stücke!", "¡Los partiré por la mitad!", "Я разорву вас на части!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (79015, 4, 1, "Je vous écraserai !", "Ich werde Euch zermalmen!", "¡Los aplastaré!", "Я сокрушу вас!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (79015, 4, 2, "Silence !", "Schweigt!", "¡Silencio!", "Молчать!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (79015, 4, 3, "Taisez-vous !", "Ruhe!", "¡Calla!", "Тихо!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (79015, 5, 0, "C'est moi qui contrôle tout, ici.", "Hier habe ich das Sagen.", "Yo soy quien manda aquí.", "Здесь повелеваю я.");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (79015, 5, 1, "Nous contrôlons le flux de pouvoir.", "Wir kontrollieren hier die Energien!", "Aquí controlamos el flujo del poder.", "Здесь мы управляем силой.");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (79015, 6, 0, "J'en ai fini avec vous.", "Ich habe genug von Euch!", "Ya me harté de ustedes.", "Я покончу с вами.");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (79015, 7, 0, "Futile.", "Wertlos.", "Despreciable.", "Ничтожество.");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (79015, 7, 1, "Faible.", "Schwach.", "Débil.", "Ничтожество.");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (79015, 7, 2, "Vos efforts sont vains !", "Ihr bemüht Euch vergebens!", "¡Tus esfuerzos fueron en vano!", "Не стоило и пытаться!");
INSERT INTO locales_creature_text (entry, textGroup, id, text_loc2, text_loc3, text_loc6, text_loc8) VALUE (79015, 8, 0, "Impossible...", "Unmöglich...", "Imposible...", "Не может быть...");
