update quest_template set offerrewardtext = 'It will take the Iron Horde quite a while to get those demolishers operational again. Excellent work, $n.' where id = 35462;
update quest_template set offerrewardtext = 'Good form, champion. I\'m sure your presence on the field was inspiring for our troops.' where id = 35460;
