update quest_template set offerrewardtext = 'Your will be done.$B$BFor the Horde!' where id = 35277;
update quest_template set offerrewardtext = 'I hope everything is to your liking. It\'s been one hell of a job.' where id = 35276;
update quest_template set offerrewardtext = 'Our lives for your will, commander.' where id = 35275;
