update quest_template set offerrewardtext = 'Very good. Our mission continues, undetected.' where id = 35191;
update quest_template set offerrewardtext = 'Welcome, my friend. I have managed to earn a bit of favor with the Sargerei, but they still guard their secrets closely.$B$BOnly the strongest will be accepted in the ranks of the Sargerei.$B$BSoulpriest Zamaya is the leader of our operations here. To secure our objectives, you must first win her trust.' where id = 35187;
update quest_template set offerrewardtext = 'Greetings, $c. Khadgar told me to expect you.' where id = 35186;
update quest_template set offerrewardtext = 'Greetings, $c. Khadgar told me to expect you.' where id = 35185;
update quest_template set offerrewardtext = 'You did what? You are a life saver, commander.$b$bWith those tasks out of the way my job will be much easier.' where id = 35176;
update quest_template set offerrewardtext = 'This is unnerving to say the least.$b$bThose things must have come from underground.$b$bHopefully we have seen the last of them.' where id = 35174;
