update quest_template set offerrewardtext = 'I\'m honored you\'re here to help, wolf-$g brother:sister;. Things here are worse than we thought.' where id = 35150;
update quest_template set offerrewardtext = '<Sallee Silverclamp examines the fragments you\'ve retrieved>$b$bThese will do nicely! Great work, kiddo!' where id = 35147;
