update quest_template set offerrewardtext = 'I have encountered evil in many places, $n. Even deep within my own soul, I have seen dark shadows of what could be.$b$bHowever, the evil within the hearts of the Pale defies explanation. It is dark, loathing, and miserable in a way I have never encountered.' where id = 35145;
update quest_template set offerrewardtext = 'My apprentice... Inside... (She dies in your arms.)' where id = 35103;
