update quest_template set offerrewardtext = 'The clan is prepared. It is time for us to advance on Bladespire Citadel!' where id = 34380;
update quest_template set offerrewardtext = 'I hope you are finding your land agreeable.' where id = 34379;
update quest_template set offerrewardtext = 'Right! I think we\'ve got this well in hand now. And hardly an explosion to be found!' where id = 34378;
update quest_template set offerrewardtext = 'That should be plenty of wood for now.' where id = 34375;
update quest_template set offerrewardtext = 'I\'m under orders to obey your orders, commander.' where id = 34364;
