update quest_template set offerrewardtext = 'Thank you flesh creature. In the lava the ashes will mix and the cycle shall continue as it has ever since the beginning.' where id = 34321;
update quest_template set offerrewardtext = 'Gordawg was a mighty ally. The elements will mourn the loss of the fury, but death was the only answer. Gordawg would not have wished to stay a slave to evil.$b$bThough Gordawg is no more, the heart of a fury is eternal and unbreakable. In time, this brightly glowing heart will take shape into a new earthen fury.$b$bSuch is the cycle of life upon the Elemental Plateau.' where id = 34271;
update quest_template set offerrewardtext = 'It\'s not safe here. The Shadow Council has already taken the city!' where id = 34242;
update quest_template set offerrewardtext = 'It\'s not safe here. The Shadow Council has already taken the city!' where id = 34240;
update quest_template set offerrewardtext = 'Excellent. The shards are at last together!' where id = 34234;
update quest_template set offerrewardtext = 'Thank you for coming, $n. The archmage and I are in need of your assistance.' where id = 34209;
update quest_template set offerrewardtext = 'Time to see what you can do.' where id = 34194;
update quest_template set offerrewardtext = 'Great work, $n! We will get started planting the garden right away.' where id = 34193;
update quest_template set offerrewardtext = 'Nice job, $n! Let\'s get this mining operation started!' where id = 34192;
update quest_template set offerrewardtext = 'Without the shadow orbs, the main Auchenai force will be able to march on Gul\'rok safely.' where id = 34163;
