update quest_template set offerrewardtext = '<Liadrin smiles.>$B$BI knew they\'d see it our way.' where id = 34122;
update quest_template set offerrewardtext = 'Is that what I think it is? You are too kind, commander!' where id = 34107;
update quest_template set offerrewardtext = 'I knew you\'d kill her. No Iron Horde is safe from your wrath.' where id = 34106;
update quest_template set offerrewardtext = 'All hail your glory, commander.' where id = 34105;
update quest_template set offerrewardtext = 'I have no idea how you managed to track down the creature so close to the Horde base, but well done $Gsir:madame;.' where id = 34103;
update quest_template set offerrewardtext = 'Maraad is dead... but we must carry on. I will not let his sacrifice be for nothing.' where id = 34099;
update quest_template set offerrewardtext = 'The screams of our enemies washed over me. The people of Tuurem are avenged.' where id = 34098;
update quest_template set offerrewardtext = '-- ENEMIES ELIMINATED --' where id = 34097;
update quest_template set offerrewardtext = 'I wondered why the manual was so big... they write with really big letters!' where id = 34094;
update quest_template set offerrewardtext = 'You radiate the power of the Ango\'rosh!$B$B<Restalaan smiles.>$B$BIt is time to take this battle to Teron\'gor\'s doorstep.' where id = 34092;
