update quest_template set offerrewardtext = 'Orgrim leads the Iron Horde in Talador... Now we know the face of our enemy.' where id = 34087;
update quest_template set offerrewardtext = 'We saved everyone we could, $n. We were too late for the others... including my sister.$b$bProphet Velen is right. When it is asked of us, we ALL must fight. I promise this: I will never again let my loved ones suffer.$b$bNer\'zhul is close to calling down the Dark Star. We are the only ones who can stop him.' where id = 34054;
update quest_template set offerrewardtext = 'Good, you\'ve arrived. Rulkan\'s message said that you would not be long.' where id = 34043;
update quest_template set offerrewardtext = 'Ya know... this thing is actually pretty impressive.$B$BThe drive system seems... familiar...' where id = 34035;
update quest_template set offerrewardtext = 'Mmf! Mm- mff!!$B$B<You remove his gag.>$B$BTHEY GOT THAELIN!!' where id = 34028;
