update quest_template set offerrewardtext = 'Thank you, adventurer! We can rest more easily knowing that this beast has been put to its end.' where id = 33836;
update quest_template set offerrewardtext = 'By the elders of Karabor... that thing is huge!$B$BOur trade routes will be safe once again. Well... as safe as you can be running through the northern forests.$B$BWell done, $c!' where id = 33834;
