update quest_template set offerrewardtext = 'Nerok and I are the only surviving prisoners, but we cannot leave just yet.' where id = 33826;
update quest_template set offerrewardtext = 'If you choose to continue to help our clan, the other Shamanstones will sense your closeness to the Frostwolves and might bless you with more gifts.' where id = 33816;
update quest_template set offerrewardtext = 'Wow, that was fast! Ok, let\'s get to it!' where id = 33808;
update quest_template set offerrewardtext = 'Thank you. My acolytes will study these in great detail. With any luck, we will be able to use this knowledge to defeat the Shadowmoon.' where id = 33795;
update quest_template set offerrewardtext = 'We are taking care of the survivors now. We owe their lives to you. Thank you, $n.' where id = 33794;
