update quest_template set offerrewardtext = 'It is a relief to know that their weapons of war cannot be used against us.' where id = 33736;
update quest_template set offerrewardtext = 'I believe we can use these at Archenon Siegeyard.' where id = 33735;
update quest_template set offerrewardtext = '-- ENEMIES ELIMINATED --' where id = 33729;
update quest_template set offerrewardtext = 'Orgrim seems to be up to no good.' where id = 33728;
update quest_template set offerrewardtext = 'We will have fewer enemies to deal with at our backs. Lok\'tar.' where id = 33722;
update quest_template set offerrewardtext = 'I always love a good explosion.' where id = 33721;
update quest_template set offerrewardtext = 'A prototype Iron Shredder, huh?! I bet we can get this thing working... they didn\'t have ME to fix it!' where id = 33720;
